183\"&gt;&lt;a href=\"http://www.summitpost.com/show/route_link.pl/route_id/125/object_id/117\"&gt;Liberty Ridge&lt;/a&gt;&lt;/td&gt;&lt;td width=\"61\" align=\"center\"&gt;2-4 days&lt;/td&gt;&lt;td width=\"87\" align=\"center\"&gt;III+&lt;/td&gt;&lt;td width=\"118\" align=\"center\"&gt;11,400 feet&lt;/td&gt;&lt;td width=\"119\" align=\"center\"&gt;55 degrees&lt;/td&gt;&lt;td width=\"108\" align=\"center\"&gt;January to July&lt;/td&gt;&lt;td width=\"83\" align=\"center\"&gt;White River&lt;/td&gt;    &lt;/tr&gt;    &lt;tr&gt;      &lt;td width=\"183\"&gt;Willis Wall, Thermogenesis&lt;/td&gt;&lt;td width=\"61\" align=\"center\"&gt;2-4 days&lt;/td&gt;&lt;td width=\"87\" align=\"center\"&gt;IV&lt;/td&gt;&lt;td width=\"118\" align=\"center\"&gt;11,400 feet&lt;/td&gt;&lt;td width=\"119\" align=\"center\"&gt;60 degrees&lt;/td&gt;&lt;td width=\"108\" align=\"center\"&gt;January to June&lt;/td&gt;&lt;td width=\"83\" align=\"center\"&gt;White River&lt;/td&gt;    &lt;/tr&gt;    &lt;tr&gt;      &lt;td width=\"183\"&gt;Willis Wall, West Rib&lt;/td&gt;&lt;td width=\"61\" align=\"center\"&gt;2-4 days&lt;/td&gt;&lt;td width=\"87\" align=\"center\"&gt;IV+&lt;/td&gt;&lt;td width=\"118\" align=\"center\"&gt;11,400 feet&lt;/td&gt;&lt;td width=\"119\" align=\"center\"&gt;60 degrees&lt;/td&gt;&lt;td width=\"108\" align=\"center\"&gt;January to June&lt;/td&gt;&lt;td width=\"83\" align=\"center\"&gt;White River&lt;/td&gt;    &lt;/tr&gt;    &lt;tr&gt;      &lt;td width=\"183\"&gt;Willis Wall, Ice Cliff&lt;/td&gt;&lt;td width=\"61\" align=\"center\"&gt;2-4 days&lt;/td&gt;&lt;td width=\"87\" align=\"center\"&gt;V&lt;/td&gt;&lt;td width=\"118\" align=\"center\"&gt;11,400 feet&lt;/td&gt;&lt;td width=\"119\" align=\"center\"&gt;85 degrees&lt;/td&gt;&lt;td width=\"108\" align=\"center\"&gt;January to June&lt;/td&gt;&lt;td width=\"83\" align=\"center\"&gt;White River&lt;/td&gt;    &lt;/tr&gt;    &lt;tr&gt;      &lt;td width=\"183\"&gt;Willis Wall, Central Rib&lt;/td&gt;&lt;td width=\"61\" align=\"center\"&gt;2-4 days&lt;/td&gt;&lt;td width=\"87\" align=\"center\"&gt;IV+&lt;/td&gt;&lt;td width=\"118\" align=\"center\"&gt;11,400 feet&lt;/td&gt;&lt;td width=\"119\" align=\"center\"&gt;60 degrees&lt;/td&gt;&lt;td width=\"108\" align=\"center\"&gt;January to June&lt;/td&gt;&lt;td width=\"83\" align=\"center\"&gt;White River&lt;/td&gt;    &lt;/tr&gt;    &lt;tr&gt;      &lt;td width=\"183\"&gt;Willis Wall, East Rib&lt;/td&gt;&lt;td width=\"61\" align=\"center\"&gt;2-4 days&lt;/td&gt;&lt;td width=\"87\" align=\"center\"&gt;IV+&lt;/td&gt;&lt;td width=\"118\" align=\"center\"&gt;11,400 feet&lt;/td&gt;&lt;td width=\"119\" align=\"center\"&gt;60 degrees&lt;/td&gt;&lt;td width=\"108\" align=\"center\"&gt;January to June&lt;/td&gt;&lt;td width=\"83\" align=\"center\"&gt;White River&lt;/td&gt;    &lt;/tr&gt;    &lt;tr&gt;      &lt;td width=\"183\"&gt;Willis Wall East&lt;/td&gt;&lt;td width=\"61\" align=\"center\"&gt;2-4 days&lt;/td&gt;&lt;td width=\"87\" align=\"center\"&gt;IV&lt;/td&gt;&lt;td width=\"118\" align=\"center\"&gt;11,400 feet&lt;/td&gt;&lt;td width=\"119\" align=\"center\"&gt;55 degrees&lt;/td&gt;&lt;td width=\"108\" align=\"center\"&gt;January to June&lt;/td&gt;&lt;td width=\"83\" align=\"center\"&gt;White River&lt;/td&gt;    &lt;/tr&gt;    &lt;tr&gt;      &lt;td width=\"183\"&gt;Curtis Ridge&lt;/td&gt;&lt;td width=\"61\" align=\"center\"&gt;2-4 days&lt;/td&gt;&lt;td width=\"87\" align=\"center\"&gt;IV&lt;/td&gt;&lt;td width=\"118\" align=\"center\"&gt;11,400 feet&lt;/td&gt;&lt;td width=\"119\" align=\"center\"&gt;55 degrees&lt;/td&gt;&lt;td width=\"108\" align=\"center\"&gt;January to June&lt;/td&gt;&lt;td width=\"83\" align=\"center\"&gt;White River&lt;/td&gt;    &lt;/tr&gt;    &lt;tr&gt;      &lt;td width=\"183\"&gt;Russell Cliff Gully&lt;/td&gt;&lt;td width=\"61\" align=\"center\"&gt;2 days&lt;/td&gt;&lt;td width=\"87\" align=\"center\"&gt;II+&lt;/td&gt;&lt;td width=\"118\" align=\"center\"&gt;10,000 feet&lt;/td&gt;&lt;td width=\"119\" align=\"center\"&gt;50 degrees&lt;/td&gt;&lt;td width=\"108\" align=\"center\"&gt;January to July&lt;/td&gt;&lt;td width=\"83\" align=\"center\"&gt;White River&lt;/td&gt;    &lt;/tr&gt;    &lt;tr&gt;      &lt;td width=\"183\"&gt;Russell Cliff Central Bowl&lt;/td&gt;&lt;td width=\"61\" align=\"center\"&gt;2 days&lt;/td&gt;&lt;td width=\"87\" align=\"center\"&gt;II+&lt;/td&gt;&lt;td width=\"118\" align=\"center\"&gt;10,000 feet&lt;/td&gt;&lt;td width=\"119\" align=\"center\"&gt;50 degrees&lt;/td&gt;&lt;td width=\"108\" align=\"center\"&gt;January to July&lt;/td&gt;&lt;td width=\"83\" align=\"center\"&gt;White River&lt;/td&gt;    &lt;/tr&gt;    &lt;tr&gt;      &lt;td width=\"183\"&gt;Russell Cliff Upper Headwall&lt;/td&gt;&lt;td width=\"61\" align=\"center\"&gt;2 days&lt;/td&gt;&lt;td width=\"87\" align=\"center\"&gt;II+&lt;/td&gt;&lt;td width=\"118\" align=\"center\"&gt;10,000 feet&lt;/td&gt;&lt;td width=\"119\" align=\"center\"&gt;50 degrees&lt;/td&gt;&lt;td width=\"108\" align=\"center\"&gt;January to July&lt;/td&gt;&lt;td width=\"83\" align=\"center\"&gt;White River&lt;/td&gt;    &lt;/tr&gt;    &lt;tr&gt;      &lt;td width=\"183\"&gt;&lt;a href=\"http://www.summitpost.com/show/route_link.pl/route_id/550/object_id/117\"&gt;Winthrop Glacier&lt;/a&gt;&lt;/td&gt;&lt;td width=\"61\" align=\"center\"&gt;2 days&lt;/td&gt;&lt;td width=\"87\" align=\"center\"&gt;II&lt;/td&gt;&lt;td width=\"118\" align=\"center\"&gt;10,000 feet&lt;/td&gt;&lt;td width=\"119\" align=\"center\"&gt;40 degrees&lt;/td&gt;&lt;td width=\"108\" align=\"center\"&gt;January to              Sept.&lt;/td&gt;&lt;td width=\"83\" align=\"center\"&gt;White River&lt;/td&gt;    &lt;/tr&gt;    &lt;tr&gt;      &lt;td width=\"183\"&gt;&lt;a href=\"http://www.summitpost.com/show/route_link.pl/route_id/550/object_id/117\"&gt;Emmons Glacier&lt;/a&gt;&lt;/td&gt;&lt;td width=\"61\" align=\"center\"&gt;2 days&lt;/td&gt;&lt;td width=\"87\" align=\"center\"&gt;II&lt;/td&gt;&lt;td width=\"118\" align=\"center\"&gt;10,000 feet&lt;/td&gt;&lt;td width=\"119\" align=\"center\"&gt;40 degrees&lt;/td&gt;&lt;td width=\"108\" align=\"center\"&gt;January to              Sept.&lt;/td&gt;&lt;td width=\"83\" align=\"center\"&gt;White River&lt;/td&gt;    &lt;/tr&gt;  &lt;/tbody&gt;&lt;/table&gt;  &lt;p&gt;Special thanks to &lt;a href=\"http://www.summitpost.com/mountains/user_link.pl/user_id/3405\"&gt;Martin  Cash &lt;/a&gt;for a lot of the initial work on building the above table.&lt;/p&gt;&lt;br&gt;&lt;div align=\"center\" class=\"image-right\" style=\"width: 506px;\"&gt;&lt;a href=\"/above-13k-ft-on-the-dc-route/65796\"&gt;&lt;img width=\"500\" height=\"375\" alt=\"Above 13k ft on the DC route...\" src=\"/images/medium/65796.jpg\"&gt;&lt;/a&gt;&lt;br&gt;&lt;span class=\"caption\"&gt;Sunrise on the DC Route&lt;/span&gt;&lt;/div&gt;&lt;div align=\"center\" class=\"image\" style=\"width: 506px;\"&gt;&lt;a href=\"/rainier-in-alpenglow/15141\"&gt;&lt;img width=\"500\" height=\"308\" alt=\"Rainier in alpenglow\" src=\"/images/medium/15141.jpg\"&gt;&lt;/a&gt;&lt;br&gt;&lt;span class=\"caption\"&gt; &lt;/span&gt;&lt;/div&gt;&lt;a name=\"chapter_7\"&gt;&lt;/a&gt;&lt;h2&gt;Mountain Conditions/Information&lt;/h2&gt;&lt;p&gt;This is the average high and low temperatures for the summit of Mount   Rainier. &lt;/p&gt;  &lt;table width=\"20%\" id=\"table1\" border=\"1\"&gt;   &lt;tbody&gt;&lt;tr&gt;    &lt;td align=\"center\"&gt;&lt;b&gt;Month&lt;/b&gt;&lt;/td&gt;    &lt;td align=\"center\"&gt;&lt;b&gt;High&lt;/b&gt;&lt;/td&gt;    &lt;td align=\"center\"&gt;&lt;b&gt;Low&lt;/b&gt;&lt;/td&gt;   &lt;/tr&gt;   &lt;tr&gt;    &lt;td align=\"center\"&gt;January&lt;/td&gt;    &lt;td align=\"center\"&gt;7&lt;/td&gt;    &lt;td align=\"center\"&gt;-3&lt;/td&gt;   &lt;/tr&gt;   &lt;tr&gt;    &lt;td align=\"center\"&gt;February&lt;/td&gt;    &lt;td align=\"center\"&gt;9&lt;/td&gt;    &lt;td align=\"center\"&gt;-2&lt;/td&gt;   &lt;/tr&gt;   &lt;tr&gt;    &lt;td align=\"center\"&gt;March&lt;/td&gt;    &lt;td align=\"center\"&gt;11&lt;/td&gt;    &lt;td align=\"center\"&gt;-2&lt;/td&gt;   &lt;/tr&gt;   &lt;tr&gt;    &lt;td align=\"center\"&gt;April&lt;/td&gt;    &lt;td align=\"center\"&gt;17&lt;/td&gt;    &lt;td align=\"center\"&gt;2&lt;/td&gt;   &lt;/tr&gt;   &lt;tr&gt;    &lt;td align=\"center\"&gt;May&lt;/td&gt;    &lt;td align=\"center\"&gt;22&lt;/td&gt;    &lt;td align=\"center\"&gt;6&lt;/td&gt;   &lt;/tr&gt;   &lt;tr&gt;    &lt;td align=\"center\"&gt;Jun&lt;/td&gt;    &lt;td align=\"center\"&gt;26&lt;/td&gt;    &lt;td align=\"center\"&gt;10&lt;/td&gt;   &lt;/tr&gt;   &lt;tr&gt;    &lt;td align=\"center\"&gt;July&lt;/td&gt;    &lt;td align=\"center\"&gt;33&lt;/td&gt;    &lt;td align=\"center\"&gt;15&lt;/td&gt;   &lt;/tr&gt;   &lt;tr&gt;    &lt;td align=\"center\"&gt;August&lt;/td&gt;    &lt;td align=\"center\"&gt;32&lt;/td&gt;    &lt;td align=\"center\"&gt;14&lt;/td&gt;   &lt;/tr&gt;   &lt;tr&gt;    &lt;td align=\"center\"&gt;September&lt;/td&gt;    &lt;td align=\"center\"&gt;28&lt;/td&gt;    &lt;td align=\"center\"&gt;12&lt;/td&gt;   &lt;/tr&gt;   &lt;tr&gt;    &lt;td align=\"center\"&gt;October&lt;/td&gt;    &lt;td align=\"center\"&gt;20&lt;/td&gt;    &lt;td align=\"center\"&gt;7&lt;/td&gt;   &lt;/tr&gt;   &lt;tr&gt;    &lt;td align=\"center\"&gt;November&lt;/td&gt;    &lt;td align=\"center\"&gt;14&lt;/td&gt;    &lt;td align=\"center\"&gt;2&lt;/td&gt;   &lt;/tr&gt;   &lt;tr&gt;    &lt;td align=\"center\"&gt;December&lt;/td&gt;    &lt;td align=\"center\"&gt;8&lt;/td&gt;    &lt;td align=\"center\"&gt;-2&lt;/td&gt;   &lt;/tr&gt;  &lt;/tbody&gt;&lt;/table&gt;  &lt;p&gt;&lt;b&gt;&lt;i&gt;Thank You &lt;/i&gt;&lt;/b&gt;  &lt;a style=\"font-family: arial, verdana, sans-serif; font-size: 12px; font-style: normal; font-variant: normal; font-weight: normal;\" href=\"http://www.summitpost.org/mountains/user_link.pl?user_id=16991\" target=\"_top\"&gt;  Scott Patterson&lt;/a&gt; for the above information.&lt;/p&gt; &lt;p&gt;&lt;b&gt;&lt;i&gt;Below are some useful links for Mount Rainier:&lt;/i&gt;&lt;/b&gt;&lt;/p&gt;  &lt;p&gt;&lt;font size=\"+0\"&gt;&lt;b&gt;&lt;a href=\"http://www.nps.gov/mora/\"&gt;Mount Rainier National  Park&lt;/a&gt;&lt;/b&gt;&lt;/font&gt;&lt;/p&gt;  &lt;p&gt;&lt;font size=\"+0\"&gt;&lt;b&gt;&lt;a href=\"http://www.topozone.com\"&gt;Mountain Topo Maps&lt;/a&gt;&lt;/b&gt;        &lt;b&gt;&lt;a href=\"http://www.atmos.washington.edu/data/rainier_report.html\"&gt;National  Weather Service Mount Rainier&lt;/a&gt;&lt;/b&gt;&lt;/font&gt;&lt;/p&gt;  &lt;p&gt;&lt;font face=\"Times New Roman\" size=\"+0\"&gt;&lt;b&gt;&lt;a href=\"http://www.avalanche.org/\"&gt;Current Avalanche Information&lt;/a&gt;&lt;/b&gt;&lt;/font&gt;&lt;/p&gt;  &lt;p&gt;&lt;font face=\"Times New Roman\" size=\"+0\"&gt;&lt;b&gt;&lt;a href=\"http://www.atmos.washington.edu/data/rainier_report.html\"&gt;Mount  Rainier Weather (University of Washington)&lt;/a&gt;&lt;/b&gt;        &lt;b&gt;&lt;a href=\"http://www2.nature.nps.gov/air/WebCams/parks/moracam/moracam.htm\"&gt;  Paradise Web Cam&lt;/a&gt;&lt;/b&gt;&lt;/font&gt;&lt;/p&gt;  &lt;p&gt;&lt;font face=\"Times New Roman\"&gt;&lt;i&gt;&lt;b&gt;Links to time-lapse movies of two Webcams   at Paradise: &lt;/b&gt;&lt;/i&gt;&lt;/font&gt;&lt;/p&gt;  &lt;p&gt;&lt;b&gt;&lt;font face=\"Times New Roman\" size=\"0\"&gt;  &lt;a href=\"http://zeitcam.com/showcam.php?cam=paradise\"&gt;Looking down the Valley   from Paradise&lt;/a&gt;&lt;/font&gt;&lt;/b&gt;&lt;/p&gt;  &lt;p&gt;&lt;font face=\"Times New Roman\"&gt;&lt;font size=\"0\"&gt;&lt;b&gt;  &lt;a href=\"http://zeitcam.com/showcam.php?cam=rainier\"&gt;Looking towards Rainier and the Muir Snowfield&lt;/a&gt;&lt;/b&gt;&lt;/font&gt;&lt;/font&gt;&lt;/p&gt;  &lt;p&gt; &lt;/p&gt;&lt;div align=\"center\" class=\"image\" style=\"width: 506px;\"&gt;&lt;a href=\"/rainier-in-alpenglow/15141\"&gt;&lt;img alt=\"Rainier in alpenglow\" height=\"308\" width=\"500\" src=\"/images/medium/15141.jpg\"&gt;&lt;/a&gt;&lt;br&gt;&lt;span class=\"caption\"&gt; &lt;/span&gt;&lt;/div&gt; &lt;br&gt;&lt;a name=\"chapter_8\"&gt;&lt;/a&gt;&lt;h2&gt;Guide Services for Mount Rainier&lt;/h2&gt;&lt;p&gt;One-day climbing instruction, two-day summit climbs, five-day climbing  seminars, and private climbs, are available through:&lt;/p&gt;  &lt;ul&gt;    &lt;li&gt;&lt;a href=\"http://www.nps.gov/cgi-bin/intercept?http://www.rmiguides.com\"&gt;&lt;font color=\"#0000ff\"&gt;Rainier      Mountaineering, Inc.&lt;/font&gt;&lt;/a&gt; 360-569-2227 &lt;/li&gt;  &lt;/ul&gt;  &lt;p&gt;Guided summit climbs on the Emmons-Winthrop Glaciers are available through:&lt;/p&gt;  &lt;ul&gt;    &lt;li&gt;&lt;a href=\"http://www.nps.gov/cgi-bin/intercept?http://www.mtnguide.com/ProgramDetail.asp?program=30\"&gt;&lt;font color=\"#0000ff\"&gt;Alpine      Ascents International&lt;/font&gt;&lt;/a&gt;, &lt;br&gt;206-378-1927  &lt;br&gt;&lt;p&gt;&lt;i&gt;Please remember that providing a guided climb or mountaineering  instruction in exchange for money is illegal except by one of the approved and  authorized concessionaires.&lt;/i&gt;   &lt;br&gt;&lt;/p&gt;&lt;/li&gt;&lt;/ul&gt;&lt;ul&gt;    &lt;li&gt;&lt;a href=\"http://mountainguides.com/\"&gt;International Mountain Guides&lt;/a&gt;, &lt;br&gt;360-569-2609 &lt;br&gt;&lt;p&gt;&lt;i&gt;Please remember that providing a guided climb or mountaineering  instruction in exchange for money is illegal except by one of the approved and  authorized concessionaires.&lt;/i&gt;&lt;/p&gt;      &lt;br&gt;&lt;a name=\"chapter_9\"&gt;&lt;/a&gt;&lt;h2&gt;Recommended Climbing Equipment (From NPS)&lt;/h2&gt;&lt;blockquote&gt;    &lt;p&gt;&lt;b&gt;Individual Equipment:&lt;/b&gt;&lt;/p&gt;    &lt;blockquote&gt;      &lt;ul&gt;    &lt;li&gt;    &lt;multicol gutter=\"5\" cols=\"2\"&gt;Map &amp;amp; Compass        &lt;/multicol&gt;&lt;/li&gt;    &lt;li&gt;    &lt;multicol gutter=\"5\" cols=\"2\"&gt;Knife        &lt;/multicol&gt;&lt;/li&gt;    &lt;li&gt;    &lt;multicol gutter=\"5\" cols=\"2\"&gt;Matches/Lighter        &lt;/multicol&gt;&lt;/li&gt;    &lt;li&gt;    &lt;multicol gutter=\"5\" cols=\"2\"&gt;Sunglasses and/or ski goggles (dark lenses)         &lt;/multicol&gt;&lt;/li&gt;    &lt;li&gt;    &lt;multicol gutter=\"5\" cols=\"2\"&gt;Sunscreen and Lip Balm         &lt;/multicol&gt;&lt;/li&gt;    &lt;li&gt;    &lt;multicol gutter=\"5\" cols=\"2\"&gt;First Aid Kit        &lt;/multicol&gt;&lt;/li&gt;    &lt;li&gt;    &lt;multicol gutter=\"5\" cols=\"2\"&gt;Food (extra food for emergencies)         &lt;/multicol&gt;&lt;/li&gt;    &lt;li&gt;    &lt;multicol gutter=\"5\" cols=\"2\"&gt;Headlamp and extra batteries        &lt;/multicol&gt;&lt;/li&gt;    &lt;li&gt;    &lt;multicol gutter=\"5\" cols=\"2\"&gt;Altimeter        &lt;/multicol&gt;&lt;/li&gt;    &lt;li&gt;    &lt;multicol gutter=\"5\" cols=\"2\"&gt;Signaling Device (whistle and/or mirror)         &lt;/multicol&gt;&lt;/li&gt;    &lt;li&gt;    &lt;multicol gutter=\"5\" cols=\"2\"&gt;Gloves and Mittens &lt;/multicol&gt;&lt;/li&gt;    &lt;li&gt;    &lt;multicol gutter=\"5\" cols=\"2\"&gt;Winter Hat, Sun Hat        &lt;/multicol&gt;&lt;/li&gt;    &lt;li&gt;    &lt;multicol gutter=\"5\" cols=\"2\"&gt;Down or Synthetic Parka &lt;/multicol&gt;    &lt;multicol gutter=\"5\" cols=\"2\"&gt;&lt;/multicol&gt;&lt;/li&gt;    &lt;li&gt;    &lt;multicol gutter=\"5\" cols=\"2\"&gt;Wool or Synthetic Clothing, layered         &lt;/multicol&gt;    &lt;multicol gutter=\"5\" cols=\"2\"&gt;Waterproof/Breathable Parka &lt;/multicol&gt;    &lt;multicol gutter=\"5\" cols=\"2\"&gt;Waterproof/Breathable Pants or Bibs        &lt;/multicol&gt;&lt;/li&gt;    &lt;li&gt;    &lt;multicol gutter=\"5\" cols=\"2\"&gt;Sleeping Bag - comfortable to 10F &lt;/multicol&gt;&lt;/li&gt;    &lt;li&gt;    &lt;multicol gutter=\"5\" cols=\"2\"&gt;Ensolite Pad        &lt;/multicol&gt;&lt;/li&gt;    &lt;li&gt;    &lt;multicol gutter=\"5\" cols=\"2\"&gt;Ice Axe &lt;/multicol&gt;&lt;/li&gt;    &lt;li&gt;    &lt;multicol gutter=\"5\" cols=\"2\"&gt;Carabiners (minimum 4)        &lt;/multicol&gt;&lt;/li&gt;    &lt;li&gt;    &lt;multicol gutter=\"5\" cols=\"2\"&gt;Prussik Slings (3) or Ascenders        &lt;/multicol&gt;&lt;/li&gt;    &lt;li&gt;    &lt;multicol gutter=\"5\" cols=\"2\"&gt;Chest and Seat Harness        &lt;/multicol&gt;&lt;/li&gt;    &lt;li&gt;    &lt;multicol gutter=\"5\" cols=\"2\"&gt;Helmet &lt;/multicol&gt;    &lt;multicol gutter=\"5\" cols=\"2\"&gt;&lt;/multicol&gt;&lt;/li&gt;    &lt;li&gt;    &lt;multicol gutter=\"5\" cols=\"2\"&gt;Boots &lt;/multicol&gt;    &lt;multicol gutter=\"5\" cols=\"2\"&gt;and &lt;/multicol&gt;    &lt;multicol gutter=\"5\" cols=\"2\"&gt;Gaitors&lt;/multicol&gt;    &lt;multicol gutter=\"5\" cols=\"2\"&gt;&lt;/multicol&gt;    &lt;multicol gutter=\"5\" cols=\"2\"&gt;&lt;/multicol&gt;&lt;/li&gt;    &lt;li&gt;    &lt;multicol gutter=\"5\" cols=\"2\"&gt;Crampons&lt;/multicol&gt;    &lt;multicol gutter=\"5\" cols=\"2\"&gt;&lt;/multicol&gt;&lt;/li&gt;    &lt;li&gt;    &lt;multicol gutter=\"5\" cols=\"2\"&gt;Picket (1) or other appropriate protection device&lt;/multicol&gt;    &lt;/li&gt;   &lt;/ul&gt;    &lt;/blockquote&gt;    &lt;p&gt;&lt;b&gt;Team Equipment:&lt;/b&gt;&lt;/p&gt;    &lt;blockquote&gt;      &lt;ul&gt;    &lt;li&gt;    &lt;multicol gutter=\"5\" cols=\"2\"&gt;Rope, 40 m, 9 or 11mm diameter        &lt;/multicol&gt;    &lt;multicol gutter=\"5\" cols=\"2\"&gt;&lt;/multicol&gt;&lt;/li&gt;    &lt;li&gt;    &lt;multicol gutter=\"5\" cols=\"2\"&gt;Pickets or Ice Screws (as appropriate for route and conditions)         &lt;/multicol&gt;&lt;/li&gt;    &lt;li&gt;    &lt;multicol gutter=\"5\" cols=\"2\"&gt;Pulleys        &lt;/multicol&gt;&lt;/li&gt;    &lt;li&gt;    &lt;multicol gutter=\"5\" cols=\"2\"&gt;Stove and Fuel        Extra Fuel for emergencies&lt;/multicol&gt;&lt;/li&gt;    &lt;li&gt;    &lt;multicol gutter=\"5\" cols=\"2\"&gt;Tent or Tarp Shelter        &lt;/multicol&gt;&lt;/li&gt;    &lt;li&gt;    &lt;multicol gutter=\"5\" cols=\"2\"&gt;Wands &lt;/multicol&gt;&lt;/li&gt;   &lt;/ul&gt;    &lt;/blockquote&gt;    &lt;p&gt;&lt;i&gt;&lt;u&gt;Additional Winter Equipment:&lt;/u&gt;&lt;/i&gt;&lt;/p&gt;    &lt;blockquote&gt;      Team:      &lt;blockquote&gt;        &lt;ul&gt;    &lt;li&gt;Food and Fuel for two extra days          &lt;/li&gt;    &lt;li&gt;Wands, minimum 200          &lt;/li&gt;    &lt;li&gt;Tent, expedition type          &lt;/li&gt;    &lt;li&gt;Additional Rope &lt;/li&gt;    &lt;/ul&gt;      &lt;/blockquote&gt;      Individual:      &lt;blockquote&gt;        &lt;ul&gt;    &lt;li&gt;Down Parka, Pants, and Mittens          &lt;/li&gt;    &lt;li&gt;Double Boots or Over Boots          &lt;/li&gt;    &lt;li&gt;Avalanche Beacon &amp;amp; Probe          &lt;/li&gt;    &lt;li&gt;Snow Shovel          &lt;/li&gt;    &lt;li&gt;Snowshoes or Skis &lt;/li&gt;    &lt;/ul&gt;      &lt;/blockquote&gt;    &lt;/blockquote&gt;  &lt;/blockquote&gt;&lt;br&gt;&lt;p&gt;&lt;/p&gt;&lt;table class=\"inline_image_thumbs\"&gt;&lt;tbody&gt;&lt;tr&gt;&lt;td&gt;&lt;a href=\"/view_object.php?object_id=6532\" rel=\"no_follow\"&gt;&lt;img src=\"/images/thumb/6532.jpg\"&gt;&lt;/a&gt;&lt;/td&gt;&lt;td&gt;&lt;a href=\"/view_object.php?object_id=14826\" rel=\"no_follow\"&gt;&lt;img src=\"/images/thumb/14826.jpg\"&gt;&lt;/a&gt;&lt;/td&gt;&lt;td&gt;&lt;a href=\"/view_object.php?object_id=15141\" rel=\"no_follow\"&gt;&lt;img src=\"/images/thumb/15141.jpg\"&gt;&lt;/a&gt;&lt;/td&gt;&lt;td&gt;&lt;a href=\"/view_object.php?object_id=36437\" rel=\"no_follow\"&gt;&lt;img src=\"/images/thumb/36437.JPG\"&gt;&lt;/a&gt;&lt;/td&gt;&lt;td&gt;&lt;a href=\"/view_object.php?object_id=38416\" rel=\"no_follow\"&gt;&lt;img src=\"/images/thumb/38416.JPG\"&gt;&lt;/a&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/tbody&gt;&lt;/table&gt;&lt;p&gt;&lt;/p&gt;&lt;a name=\"chapter_10\"&gt;&lt;/a&gt;&lt;h2&gt;External Links&lt;/h2&gt;&lt;ul&gt;&lt;li&gt;&lt;a href=\"http://cascadeclimber.com/september,_2000.htm\"&gt;Emmons/Upper Winthrop Trip Report, September 2000&lt;/a&gt;&lt;br&gt;&lt;br&gt;&lt;/li&gt;&lt;li&gt;&lt;a href=\"http://www.nwog.org\"&gt;Northwest Outdoors Guide&lt;/a&gt;&lt;br&gt;Guide to mountaineering and hiking in the Pacific Northwest&lt;br&gt;&lt;/li&gt;&lt;li&gt;&lt;a href=\"http://members.rogers.com/spudsusa2/rainier.htm\"&gt;A Potato Head takes on Mount Rainier&lt;/a&gt;&lt;br&gt;&lt;br&gt;&lt;/li&gt;&lt;li&gt;&lt;a href=\"http://www.marcpagani.com/galleries/mtnsport/rainier/default.html\"&gt;Great Images of Rainier - Marc Pagani &lt;/a&gt;&lt;br&gt;Click on the mountaineering link to see many great images of Mount Rainier from Marc Pagani Photography&lt;br&gt;&lt;/li&gt;&lt;li&gt;&lt;a href=\"http://www.snwburd.com/bob/trip_reports/rainier_1.html\"&gt;DC Route via Muir - June 16, 2001&lt;/a&gt;&lt;br&gt;&lt;br&gt;&lt;/li&gt;&lt;li&gt;&lt;a href=\"http://www.justhiking.com/hikes/2004/07-31rain/rain.htm\"&gt;Mount Rainier (07/31/2004)&lt;/a&gt;&lt;br&gt;&lt;br&gt;&lt;/li&gt;&lt;li&gt;&lt;a href=\"http://www.chinookpass.com/\"&gt;Excellent Rainier Photos and Info&lt;/a&gt;&lt;br&gt;&lt;br&gt;&lt;/li&gt;&lt;li&gt;&lt;a href=\"http://www.zeitcam.com/webcam/rainier\"&gt;Zeitcam.com&lt;/a&gt;&lt;br&gt;Time-lapse movie of Mt. Rainier Web Cam, as well as other peaks.&lt;br&gt;&lt;/li&gt;&lt;li&gt;&lt;a href=\"http://www.chubbyhiker.com/galleries/Mt.%20Rainier%20-%20Emmons%20Glacier%20-%20July%2014th%20-%2017th/index.html\"&gt;Emmons Glacier - July 14th - 17th 2005&lt;/a&gt;&lt;br&gt;&lt;br&gt;&lt;/li&gt;&lt;li&gt;&lt;a href=\"http://www.tylersnook.com/rainier/index.html\"&gt;D.C.  8-3-05&lt;/a&gt;&lt;/li&gt;&lt;li&gt;&lt;br&gt;&lt;/li&gt;&lt;/ul&gt;&lt;ul&gt;&lt;li&gt;&lt;a href=\"http://danielarndt.com/article/46/Mount_Rainier_climb\"&gt;Mount Rainier via Emmons Glacier&lt;/a&gt;&lt;br&gt;&lt;br&gt;&lt;/li&gt;&lt;li&gt;&lt;a href=\"http://danielarndt.com/article/69/Mount_Rainier_climb_via_Kautz_Glacier\"&gt;Mount Rainier via Kautz Glacier&lt;/a&gt;&lt;br&gt;&lt;br&gt;&lt;/li&gt;&lt;/ul&gt;&lt;/li&gt;&lt;/ul&gt;                                          &lt;div align=\"center\"&gt;&lt;a name=\"messages\"&gt;&lt;/a&gt;&lt;a name=\"chapter_11\"&gt;&lt;/a&gt;&lt;h2&gt;Additions and Corrections&lt;/h2&gt;[ &lt;a href=\"/object_discussion.php?type=vote_comments&amp;amp;object_id=150291&amp;amp;is_addition=1#vote_form\"&gt;Post an Addition or Correction&lt;/a&gt; ]&lt;table cellpadding=\"0\" cellspacing=\"0\" style=\"width: 775px;\"&gt;&lt;tr&gt;&lt;td&gt;&lt;table cellpadding=\"2\" cellspacing=\"2\" width=\"100%\"&gt;&lt;tr&gt;&lt;td align=\"left\"&gt;&lt;span class=\"pagertext\"&gt;Viewing: &lt;b&gt;1&lt;/b&gt;-&lt;b&gt;5&lt;/b&gt; of &lt;b&gt;5&lt;/b&gt;&lt;/span&gt;&lt;/td&gt;&lt;td align=\"center\"&gt;&lt;/td&gt;&lt;td align=\"right\"&gt;    &lt;/td&gt;&lt;/tr&gt;&lt;/table&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/table&gt;&lt;table border=\"0\" width=\"775\" class=\"messages\"&gt;&lt;tr&gt;&lt;td width=\"110\" align=\"left\" valign=\"top\" style=\"padding-top: 6px;\"&gt;&lt;a name=\"282049\"&gt;&lt;/a&gt; &lt;a class=\"dis_user_lnk\" href=\"/users/jessicahiker/1790\"&gt;Jessicahiker&lt;/a&gt;&lt;/td&gt;&lt;td valign=\"top\" style=\"padding-top: 6px;\"&gt;&lt;b&gt;Carbon Glacier&lt;/b&gt;&lt;/td&gt;&lt;td align=\"right\" valign=\"top\" nowrap style=\"font-size: 10px;\"&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td valign=\"top\" rowspan=\"2\" align=\"left\" style=\"padding-top: 6px;\"&gt;&lt;p class=\"disc_avatar\"&gt;&lt;a href=\"/users/jessicahiker/1790\"&gt;&lt;img alt=\"Jessicahiker\" width=\"50\" height=\"50\" border=\"0\" src=\"/images/avatars/953544.jpg\"&gt;&lt;/a&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=\"font-size: 9px; color: #888;\"&gt;Hasn\'t voted&lt;/span&gt;&lt;/p&gt;&lt;/td&gt;&lt;td colspan=\"2\" style=\"padding-top: 6px;\"&gt;The road has been washed out. There is no vehicle access to the trailhead. This adds 10 miles to your trip. Currently there are no plans to rebuild the road as per the NPS. Just got back on 9/5/08.&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td valign=\"bottom\" style=\"padding-top: 6px;\" colspan=\"2\"&gt;&lt;span style=\"font-size: 9px; color: #888;\"&gt;Posted Sep 8, 2008 10:59 am&lt;/span&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/table&gt;&lt;table border=\"0\" width=\"775\" class=\"messages\"&gt;&lt;tr&gt;&lt;td width=\"110\" align=\"left\" valign=\"top\" style=\"padding-top: 6px;\"&gt;&lt;a name=\"364103\"&gt;&lt;/a&gt; &lt;a class=\"dis_user_lnk\" href=\"/users/haliku/16437\"&gt;Haliku&lt;/a&gt;&lt;/td&gt;&lt;td valign=\"top\" style=\"padding-top: 6px;\"&gt;&lt;b&gt;Liberty Ridge gone?&lt;/b&gt;&lt;/td&gt;&lt;td align=\"right\" valign=\"top\" nowrap style=\"font-size: 10px;\"&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td valign=\"top\" rowspan=\"2\" align=\"left\" style=\"padding-top: 6px;\"&gt;&lt;p class=\"disc_avatar\"&gt;&lt;a href=\"/users/haliku/16437\"&gt;&lt;img alt=\"Haliku\" width=\"50\" height=\"50\" border=\"0\" src=\"/images/avatars/576870.JPG\"&gt;&lt;/a&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=\"font-size: 9px; color: #888;\"&gt;Voted 10/10&lt;/span&gt;&lt;/p&gt;&lt;/td&gt;&lt;td colspan=\"2\" style=\"p'),(13,150302,'/handies-peak/150302','/san-juan-range/171122','Handies Peak','','','','14053','',60593,37.9131,-107.504,'Colorado, United States, North America','','',171122,'San Juan Range','','','','','','Mountain-Rock','content: &lt;article&gt;&lt;p&gt;&lt;/p&gt;&lt;a name=\"chapter_1\"&gt;&lt;/a&gt;&lt;h2 id=\"first_h2\"&gt;Overview&lt;/h2&gt;Handies Peak is the 41st highest peak in Colorado and is in the San Juan range. The climb up Handies is quite nice scenery and the summit is noted for its remotness from civilization. &lt;b&gt;From the summit you cant see a city or a road.. not a sign of civilization&lt;/b&gt;. The two most popular trailheads to climb Handies are the American Basin trailhead, and the Grizzly Gulch trailhead. Grizzly Gulch is a longer climb, but  it has the advantage that  you can use the same trailhead to also climb Redcloud and Sunshine. It is also the most scenic route up Handies and is marked as a classic route. Redcloud and Sunshine are usually done together in one day and Handies the next. &lt;br&gt;&lt;br&gt;American Basin has the advantage of being the shortest route to the summit. Sometimes people will climb Redcloud and Sunshine one day then drive to American Basin to climb Handies. Those that I know that have done this said the road was rough between the trailheads and recommend 4wd. Personally, I like the idea of setting camp at Grizzly Gulch and doing all 3 peaks from there in 2 days and not having to move- plus it is more scenic this way.&lt;br&gt;&lt;br&gt;For more information on the area see: &lt;a href=\"http://www.fs.fed.us/r2/gmug/\"&gt;Uncomahgre National Forest&lt;/a&gt;&lt;br&gt;&lt;p&gt;&lt;/p&gt;&lt;table class=\"inline_image_thumbs\"&gt;&lt;tr&gt;&lt;td&gt;&lt;a href=\"/view_object.php?object_id=1283\" rel=\"no_follow\"&gt;&lt;img src=\"/images/thumb/1283.jpg\"&gt;&lt;/a&gt;&lt;/td&gt;&lt;td&gt;&lt;a href=\"/view_object.php?object_id=1284\" rel=\"no_follow\"&gt;&lt;img src=\"/images/thumb/1284.jpg\"&gt;&lt;/a&gt;&lt;/td&gt;&lt;td&gt;&lt;a href=\"/view_object.php?object_id=8298\" rel=\"no_follow\"&gt;&lt;img src=\"/images/thumb/8298.jpg\"&gt;&lt;/a&gt;&lt;/td&gt;&lt;td&gt;&lt;a href=\"/view_object.php?object_id=20952\" rel=\"no_follow\"&gt;&lt;img src=\"/images/thumb/20952.jpg\"&gt;&lt;/a&gt;&lt;/td&gt;&lt;td&gt;&lt;a href=\"/view_object.php?object_id=61723\" rel=\"no_follow\"&gt;&lt;img src=\"/images/thumb/61723.JPG\"&gt;&lt;/a&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/table&gt;&lt;a name=\"chapter_2\"&gt;&lt;/a&gt;&lt;h2&gt;Getting There&lt;/h2&gt;In Lake City begin measuring from the bridge crossing Henson Creek, heading south. Go 2.2 miles to the Lake San Cristobal Road and turn right. Go past the beautiful lake and up the Lake Fork of the Gunnison River. Continue to mile 14.3 where the road forks and a take the right fork up Cinnamon Pass Road. At mile 18.3 you will see the large parking lot at the Silver Creek - Grizzly Gulch Trailhead. An alternate way to reach the trailheads is from Silverton. Measuring from Silverton\'s north end, follow Hwy110 for 12.3 miles to Animas Forks, climb steeply east to reach Cinnamon Pass at 15.3 miles, cross the pass and descend on its east side to reach the Silver Creek- Grizzly Gulch Trailhead after 21.1 miles. A 4 WD is required for this approach. &lt;br&gt;There is ample parking available at the Grizzly Gulch trailhead. The trailhead also has toilet facilities.&lt;br&gt;&lt;p&gt;&lt;/p&gt;&lt;table class=\"inline_image_thumbs\"&gt;&lt;tr&gt;&lt;td&gt;&lt;a href=\"/view_object.php?object_id=60929\" rel=\"no_follow\"&gt;&lt;img src=\"/images/thumb/60929.JPG\"&gt;&lt;/a&gt;&lt;/td&gt;&lt;td&gt;&lt;a href=\"/view_object.php?object_id=8395\" rel=\"no_follow\"&gt;&lt;img src=\"/images/thumb/8395.jpg\"&gt;&lt;/a&gt;&lt;/td&gt;&lt;td&gt;&lt;a href=\"/view_object.php?object_id=12206\" rel=\"no_follow\"&gt;&lt;img src=\"/images/thumb/12206.jpg\"&gt;&lt;/a&gt;&lt;/td&gt;&lt;td&gt;&lt;a href=\"/view_object.php?object_id=22959\" rel=\"no_follow\"&gt;&lt;img src=\"/images/thumb/22959.jpg\"&gt;&lt;/a&gt;&lt;/td&gt;&lt;td&gt;&lt;a href=\"/view_object.php?object_id=21579\" rel=\"no_follow\"&gt;&lt;img src=\"/images/thumb/21579.jpg\"&gt;&lt;/a&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/table&gt;&lt;a name=\"chapter_3\"&gt;&lt;/a&gt;&lt;h2&gt;Mountain Conditions&lt;/h2&gt;The trail from Grizzly Gulch starts out crossing the Lake Fork of the Gunnison River  over a  couple of logs. From there it follows the creek for about 2 miles. Handies is easily visible. We took the approach up the East slopes ( the classic approach). It takes you up to a saddle then across the ridge to the summit. You do see some cars and roads below just short of the summit, but by the time you reach the summit they are blocked from view.  Colorado mountains are famous for afternoon summer storms. Watch the skies and try to be off the summit by noon to be on the safe side.&lt;br&gt;&lt;br&gt;Conditions on this mountain, like all Colorado 14ers, are subject to change rapidly- especially during the summer when most people climb Handies. The mountains are their own weather system, and weather forecasts from nearby towns often have little to do with actual mountain conditions. You can generally count on clear to partly cloudy in the morning, and heavy clouds and thunderstorms in the afternoon during the summer. &lt;br&gt;&lt;br&gt;There is no substitute for getting an early start, and getting as much of the mountain \"behind you\" as early in the day as possible. Keep a good eye on the sky as weather conditions can deteriorate rapidly. The greatest weather danger is from lightning strikes, and climbers are killed almost every summer in Colorado by lightning strikes. July seems to be the most deadly month for lightning.&lt;br&gt;&lt;br&gt;Two  climbers were  killed by lightning in Colorado within a couple days of each other in the summer of 2003.  I think the following important information from Gerry Roach\'s book  \"Colorado\'s Fourteeners From Hikes to Climbs\" bears repeating. Added here with permission from Gerry Roach:&lt;br&gt;&lt;br&gt;Colorado is famous for apocalyptic lightning storms that threaten not just your life, but your soul as well. This section will have special meaning if you have ever been trapped by a storm that endures for more than an hour and leaves no gap between one peal of thunder and the next. The term simultaneous flash-boom has a very personal meaning for many Colorado Climbers&lt;br&gt;&lt;br&gt;&lt;b&gt;Dangers&lt;/b&gt;&lt;br&gt;&lt;br&gt;1. Lightning is dangerous!&lt;br&gt;2. Lightning is the greatest external hazard to summer mountaineering in Colorado.&lt;br&gt;3. Lightning kills people every year in Colorado\'s mountains.&lt;br&gt;4. Direct hits are usually fatal.&lt;br&gt;&lt;br&gt;&lt;b&gt;Precautions&lt;/b&gt;&lt;br&gt;&lt;br&gt;1. Start early! Be off summits by noon and back in the valley by early afternoon.&lt;br&gt;2. Observe thunderhead buildup carefully, noting speed and direction; towering thunderheads with black bottoms are bad.&lt;br&gt;3. When lightning begins nearby, count the seconds between flash and thunder, then divide by 5 to calculate the distance to the flash in miles. Repeat to determine if lightning is approaching. &lt;br&gt;4. Try to determine if the lightning activity is cloud-to-cloud or ground strikes.&lt;br&gt;5. Get off summits and ridges. &lt;br&gt;&lt;br&gt;&lt;b&gt;Protection&lt;/b&gt;&lt;br&gt;&lt;br&gt;1. You cannot outrun a storm; physics wins.&lt;br&gt;2. When caught, seek a safe zone in the 45-degree cone around an object 5 to 10 times your height.&lt;br&gt;3. Be aware of ground currents; the current from a ground strike disperses along the ground or cliff, especially in wet cracks.&lt;br&gt;4. Wet ropes are good conductors.&lt;br&gt;5. Snow is not a good conductor.&lt;br&gt;6. Separate yourself from metal objects.&lt;br&gt;7. Avoid sheltering in spark gaps under boulders and trees.&lt;br&gt;8. Disperse the group. Survivors can revive one who is hit.&lt;br&gt;9. Crouch on boot soles, ideally on dry, insulating material such as moss or grass. Dirt is better than rock. Avoid water. &lt;br&gt;&lt;br&gt;&lt;br&gt;The following link is also helpful: &lt;a href=\"http://home.fuse.net/engineering/LightningSafety.htm#Safe%20Practice%20Crouch\"&gt;Lightning Safety and Crouch&lt;/a&gt;&lt;br&gt;&lt;br&gt;&lt;br&gt;Also see this report by &lt;a href=\"http://summitpost.com/cgi-bin/user_link.pl?user_id=570\"&gt;nchenkin&lt;/a&gt; It will make you really think about lightning safety: &lt;a href=\"http://summitpost.com/mountains/trip_report.pl?trip_report_id=618&amp;amp;mountain_id=1990\"&gt;Struck By Lightning!&lt;/a&gt;&lt;br&gt;&lt;br&gt;&lt;br&gt;&lt;br&gt;&lt;br&gt;&lt;br&gt;&lt;a name=\"chapter_4\"&gt;&lt;/a&gt;&lt;h2&gt;Camping&lt;/h2&gt;Camping is readily available at the trailhead and there is adequate parking and toilet facilities. There is no charge for camping or parking. Lots of tourists will drive up to the trailhead for the day, but ample parking is available for them as well as the climbers.&lt;br&gt;&lt;br&gt;If  you are going to climb Redcloud and Sunshine one day and Handies the next I would recommend staying at the Silver Creek Grizzly Gulch trailhead. Ericdix adds: We climbed Redcloud and Sunshine, and then the next day planned to climb Handies. We chose to move our camp from the Silver Creek/Grizzley Gulch Trailhead to American Basin. This made for a much shorter route. At this trailhead however, there are not many good campsites. So for those of you looking to combine Handies with Redcloud and Sunshine, it might be easier to camp at the Grizzley Gulch Trailhead. &lt;br&gt;&lt;br&gt;&lt;p&gt;&lt;/p&gt;&lt;table class=\"inline_image_thumbs\"&gt;&lt;tr&gt;&lt;td&gt;&lt;a href=\"/view_object.php?object_id=1173\" rel=\"no_follow\"&gt;&lt;img src=\"/images/thumb/1173.jpg\"&gt;&lt;/a&gt;&lt;/td&gt;&lt;td&gt;&lt;a href=\"/view_object.php?object_id=1410\" rel=\"no_follow\"&gt;&lt;img src=\"/images/thumb/1410.jpg\"&gt;&lt;/a&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/table&gt;&lt;a name=\"chapter_5\"&gt;&lt;/a&gt;&lt;h2&gt;When To Climb&lt;/h2&gt;Summer is easiest and most popular. July and August tend to be peak season. Try to climb during the week if possible to avoid the crowds. This is a popular climb in the San Juan mountain range because you can climb 3 mountains from the same trailhead ( Redcloud Peak, Sunshine Peak, and Handies Peak). Colorado mountains are famous for afternoon storms. Try to climb early and as a rule of thumb be off the summit by noon.&lt;br&gt;&lt;p&gt;&lt;/p&gt;&lt;table class=\"inline_image_thumbs\"&gt;&lt;tr&gt;&lt;td&gt;&lt;a href=\"/view_object.php?object_id=4140\" rel=\"no_follow\"&gt;&lt;img src=\"/images/thumb/4140.jpg\"&gt;&lt;/a&gt;&lt;/td&gt;&lt;td&gt;&lt;a href=\"/view_object.php?object_id=4137\" rel=\"no_follow\"&gt;&lt;img src=\"/images/thumb/4137.jpg\"&gt;&lt;/a&gt;&lt;/td&gt;&lt;td&gt;&lt;a href=\"/view_object.php?object_id=12034\" rel=\"no_follow\"&gt;&lt;img src=\"/images/thumb/12034.jpg\"&gt;&lt;/a&gt;&lt;/td&gt;&lt;td&gt;&lt;a href=\"/view_object.php?object_id=21580\" rel=\"no_follow\"&gt;&lt;img src=\"/images/thumb/21580.jpg\"&gt;&lt;/a&gt;&lt;/td&gt;&lt;td&gt;&lt;a href=\"/view_object.php?object_id=22960\" rel=\"no_follow\"&gt;&lt;img src=\"/images/thumb/22960.jpg\"&gt;&lt;/a&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/table&gt;&lt;a name=\"chapter_6\"&gt;&lt;/a&gt;&lt;h2&gt;People Pics&lt;/h2&gt;Add your People Pics and any other miscellaneous information here.&lt;br&gt;&lt;p&gt;&lt;/p&gt;&lt;table class=\"inline_image_thumbs\"&gt;&lt;tr&gt;&lt;td&gt;&lt;a href=\"/view_object.php?object_id=3583\" rel=\"no_follow\"&gt;&lt;img src=\"/images/thumb/3583.jpg\"&gt;&lt;/a&gt;&lt;/td&gt;&lt;td&gt;&lt;a href=\"/view_object.php?object_id=61208\" rel=\"no_follow\"&gt;&lt;img src=\"/images/thumb/61208.JPG\"&gt;&lt;/a&gt;&lt;/td&gt;&lt;td&gt;&lt;a href=\"/view_object.php?object_id=40917\" rel=\"no_follow\"&gt;&lt;img src=\"/images/thumb/40917.jpg\"&gt;&lt;/a&gt;&lt;/td&gt;&lt;td&gt;&lt;a href=\"/view_object.php?object_id=82008\" rel=\"no_follow\"&gt;&lt;img src=\"/images/thumb/82008.jpg\"&gt;&lt;/a&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/table&gt;&lt;a name=\"chapter_7\"&gt;&lt;/a&gt;&lt;h2&gt;External Links&lt;/h2&gt;&lt;a href=\"http://14ers.org\"&gt;COLORADO 14ERS INITIATIVE&lt;/a&gt; LEARN MORE ABOUT COLORADO 14ERS AND VOLUNTEER OPPORTUNITIES    &lt;br&gt;&lt;br&gt;&lt;a href=\"http://www.lnt.org\"&gt;Leave No Trace&lt;/a&gt; &lt;br&gt;Check out the web site of LNT and learn some important minimum impact hiking tips.&lt;br&gt;&lt;br&gt;&lt;a href=\"http://www.cmc.org\"&gt;Colorado Mountain Club&lt;/a&gt;&lt;br&gt;CMC&rsquo;s web site will tell you all you need to know about Colorado&rsquo;s largest and oldest hiking club.&lt;br&gt;&lt;ul&gt;&lt;li&gt;&lt;a href=\"http://www.ryananderin.org/14ers/09.14.03_handies_whitecross.html\"&gt;Handies Peak &amp;amp; Whitecross Mountain&lt;/a&gt;&lt;br&gt; &lt;br&gt;&lt;/li&gt;&lt;li&gt;&lt;a href=\"http://www.justhiking.com/hikes/2005/08-10hand/hand.htm\"&gt;Handies Peak/Whitecross Mountain (08/10/2005)&lt;/a&gt;&lt;br&gt; &lt;br&gt;&lt;/li&gt;&lt;li&gt;&lt;a href=\"http://www.14ers.com\"&gt;14ers.com - The Home of Colorado\'s Highest Peaks&lt;/a&gt;&lt;br&gt; Fourteener Photos, Route Descriptions, Forum, and more...&lt;br&gt;&lt;/li&gt;&lt;li&gt;&lt;a href=\"http://distantpeak.com/web/mountains/n-america/handies_peak\"&gt;A beautiful day on Handies Peak&lt;/a&gt;&lt;br&gt; &lt;br&gt;&lt;/li&gt;&lt;/ul&gt;                                                                                   &lt;a name=\"chapter_8\"&gt;&lt;/a&gt;&lt;h2&gt;Images&lt;/h2&gt;&lt;div align=\"center\"&gt;&lt;table id=\"my_images\"&gt;&lt;tr&gt;&lt;td&gt;&lt;a href=\"/handies/226597/c-150302\"&gt;&lt;img alt=\"Handies\" src=\"/images/square/226597.JPG\"&gt;&lt;/a&gt;&lt;/td&gt;&lt;td&gt;&lt;a href=\"/colorado-columbines/272543/c-150302\"&gt;&lt;img alt=\"Colorado Columbines\" src=\"/images/square/272543.JPG\"&gt;&lt;/a&gt;&lt;/td&gt;&lt;td&gt;&lt;a href=\"/handies-peak-from-silver/103360/c-150302\"&gt;&lt;img alt=\"Handies Peak from Silver...\" src=\"/images/square/103360.JPG\"&gt;&lt;/a&gt;&lt;/td&gt;&lt;td&gt;&lt;a href=\"/sunrise/860532/c-150302\"&gt;&lt;img alt=\"Sunrise\" src=\"/images/square/860532.jpg\"&gt;&lt;/a&gt;&lt;/td&gt;&lt;td&gt;&lt;a href=\"/sloan-lake/340988/c-150302\"&gt;&lt;img alt=\"Sloan Lake\" src=\"/images/square/340988.JPG\"&gt;&lt;/a&gt;&lt;/td&gt;&lt;td&gt;&lt;a href=\"/on-handies-peak/340987/c-150302\"&gt;&lt;img alt=\"On Handies Peak\" src=\"/images/square/340987.JPG\"&gt;&lt;/a&gt;&lt;/td&gt;&lt;td&gt;&lt;a href=\"/tim-sweet-of-flagstaff/20952/c-150302\"&gt;&lt;img alt=\"Tim Sweet of Flagstaff,...\" src=\"/images/square/20952.jpg\"&gt;&lt;/a&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td&gt;&lt;a href=\"/alpine-sunflowers/351107/c-150302\"&gt;&lt;img alt=\"Alpine Sunflowers\" src=\"/images/square/351107.JPG\"&gt;&lt;/a&gt;&lt;/td&gt;&lt;td&gt;&lt;a href=\"/handies-peak/447940/c-150302\"&gt;&lt;img alt=\"Handies Peak\" src=\"/images/square/447940.JPG\"&gt;&lt;/a&gt;&lt;/td&gt;&lt;td&gt;&lt;a href=\"/looking-up-into-the-american/61723/c-150302\"&gt;&lt;img alt=\"Looking up into the American...\" src=\"/images/square/61723.JPG\"&gt;&lt;/a&gt;&lt;/td&gt;&lt;td&gt;&lt;a href=\"/handies-peak-from-the-grizzly/8396/c-150302\"&gt;&lt;img alt=\"Handies Peak from the Grizzly...\" src=\"/images/square/8396.jpg\"&gt;&lt;/a&gt;&lt;/td&gt;&lt;td&gt;&lt;a href=\"/columbines-and-handies/310787/c-150302\"&gt;&lt;img alt=\"Columbines and Handies\" src=\"/images/square/310787.JPG\"&gt;&lt;/a&gt;&lt;/td&gt;&lt;td&gt;&lt;a href=\"/my-prize-photo-a-beautiful/60929/c-150302\"&gt;&lt;img alt=\"My prize photo! A beautiful...\" src=\"/images/square/60929.JPG\"&gt;&lt;/a&gt;&lt;/td&gt;&lt;td&gt;&lt;a href=\"/beautiful-buttresses/310791/c-150302\"&gt;&lt;img alt=\"Beautiful Buttresses\" src=\"/images/square/310791.JPG\"&gt;&lt;/a&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td colspan=\"7\" align=\"center\"&gt;[ &lt;a href=\"/handies-peak/images/p-150302\"&gt;View Gallery - 193 More Images&lt;/a&gt; ]&lt;/td&gt;&lt;/tr&gt;&lt;/table&gt;&lt;/div&gt;&lt;/article&gt;date:difficulty:'),(14,150312,'/grand-teton/150312','/teton-range/171178','Grand Teton','','','','13770','',330650,43.7411,-110.802,'Wyoming, United States, North America','','',171178,'Teton Range','','','','','','Mountain-Rock','content: &lt;article&gt;&lt;p&gt;&lt;/p&gt;&lt;a name=\"chapter_1\"&gt;&lt;/a&gt;&lt;h2 id=\"first_h2\"&gt;Overview&lt;/h2&gt;&lt;center&gt;&lt;a href=\"http://www.tetoncam.com\"&gt;&lt;img src=\"http://www.tetoncam.com/tetonl.jpg\" height=\"240\" width=\"320\" style=\"\"&gt;&lt;/a&gt;&lt;br&gt;&lt;b&gt;This is a live webcam view of the Grand Teton from the west which updates every 30 minutes.&lt;br&gt;Due to night hours or weather, the mountain may not always be in view.&lt;br&gt;Also, if there is no photo, the webcam is temporarily down.&lt;/b&gt;&lt;br&gt;&lt;br&gt;Webcam direct linked with permission from &lt;a href=\"http://www.srv.net/~jpat/index.html\"&gt;Pat Neeley&lt;/a&gt;.&lt;br&gt;Click on the above webcam photo for current conditions and a larger version of the webcam view.&lt;br&gt;&lt;a href=\"http://www.srv.net/~jpat/tla.html\"&gt;Here&lt;/a&gt; are more webcams from Pat\'s website including time-lapse photos of Grand Teton and Teton Valley.&lt;br&gt;Thanks to Pat for the use of his webcam.&lt;br&gt;&lt;br&gt;&lt;a href=\"http://www.jacksonholewebcams.com/jackson-hole-webcams/\"&gt;More Webcams&lt;/a&gt;&lt;/center&gt;&lt;br&gt;This page was graciously transferred to me by fellow SummitPost member Allan Ellis. He is responsible for most of the great information on this page. I hope to maintain the same quality that he has over the years!&lt;br&gt;&lt;br&gt;The Grand Teton!   Even the name inspires one to explore and climb it\'s classic routes.    Grand Teton is a classic alpine peak in itself to the point that an entire national park was named after it.    Climbers have been drawn to \"The Grand\" for over 100 years and continue to make pilgrimages from all over the world.  With over 35 routes and 50 variations to those routes, there is something for everyone from 5.4 to 5.12.    New routes are being discovered every year including The Squeeze Box, Grade IV, M7, A0, put up by Stephen Koch and Hans Johnstone on  February 6, 2007.   This new route is on the north face and lies between Shea\'s Chute and the Alex Lowe Memorial Route, and ascends a weakness up a beautiful granite buttress (&lt;a href=\"http://www.stephenkoch.com\"&gt;www.StephenKoch.com&lt;/a&gt;).     Even the easiest route, the Owen-Spalding, is a technical endeavor and should not be taken lightly.   At Grade II, 5.4, the Owen-Spalding is the standard route and requires technical rock climbing skills and equipment.   The Exum Ridge route is the most famous, named after Grand Teton pioneer, Glenn Exum.  &lt;br&gt;&lt;br&gt;The first ascent of The Grand was on August 11, 1898 by William Owen, Franklin Spalding, Frank Peterson, and John Shive via the now-called Owen-Spalding route.    Although the Owen party is officially credited with the first ascent, there is a controversy on whether they were actually the first to climb The Grand.    Supposedly, Nathaniel Langford and James Stevenson climbed the mountain 25 years earlier.   Read an article on the controversy &lt;a href=\"http://www.summitpost.org/article/175317/the-grand-question.html\"&gt;HERE&lt;/a&gt;.The remainder of Grand Teton history is very rich and much too detailed to be described here.    The references listed below are highly recommended for a detailed history of the mountain.    One reason the early history of climbing The Grand is so well known is because the summit logs have been maintained by the park superintendent since 1927.   This rich history continues to this day.   &lt;br&gt;&lt;br&gt;&lt;a href=\"http://www.tetonclimbinghistory.com/index.html\"&gt;CLICK HERE TO VIEW THE SUMMIT REGISTER ARCHIVE&lt;/a&gt;:  This site contains a collection of digital images of the Grand Teton National Park Summit Register Archive including the register for the Grand Teton.   The summit registers were maintained from about the late-1920s into the 1980s and they form a unique repository of Teton climbing history.&lt;br&gt;&lt;br&gt;The three Tetons: Grand, Middle, and South were named in the 1800\'s by French Trappers of the Hudson Bay Company. They called them Les Trois Tetons, which meant &ldquo;the three breasts.&rdquo; Early native American Indians of the Shoshone tribe called the range &ldquo;Hoary Headed Fathers.&rdquo; &lt;br&gt;&lt;br&gt;BTW:  A common mistake is for people to call the Teton Range \"The Grand Tetons.\"   The proper term is \"the Tetons\" or \"the Teton range.\"  The Grand Teton is a single mountain and deserving of distinction.&lt;br&gt;&lt;br&gt;&lt;center&gt;&lt;a href=\"http://www.summitpost.org/mountains/photo_link.pl/p/photo_id__123180__object_id____type__new__mountain_id____route_id____user_id____order_by____limit__\"&gt;&lt;img src=\"http://www.summitpost.org/images/medium/81466.jpg\" style=\"\"&gt;&lt;/a&gt;&lt;br&gt;Photo by Biglost&lt;/center&gt;&lt;br&gt;&lt;br&gt;&lt;b&gt;Noteworthy Ascents/Events of Grand Teton:&lt;/b&gt;&lt;br&gt;Exum Ridge:  July 15, 1931; Glenn Exum&lt;br&gt;North Face:  August 25, 1936; Jack Durrance, Paul and Eldon Petzoldt  &lt;br&gt;East Ridge:  July 22, 1929; Robert Underhill&lt;br&gt;First Female Ascent:  August 27, 1923; Eleanor Davis&lt;br&gt;First Winter Ascent: December 19, 1935; Fred Brown, Paul and Eldon Petzoldt &lt;br&gt;First Ski Descent:  June 16, 1971; Bill Briggs and Robbie Garnett via the Stettner Couloir&lt;br&gt;Speed Record:  August 22, 2012; Andy Anderson; 2:53:02; Lupine Meadows to summit and back  &lt;br&gt;First Grand Traverse:  1966; Jim McCarthy, Lito Tejada-Flores &lt;br&gt;Grand Traverse Speed Record:  2000; Rolando Garibotti; 6 hours, 49 minutes  &lt;br&gt;First Winter Grand Traverse:   January 19, 2004; Stephen Koch and Mark Newcomb &lt;br&gt;&lt;br&gt;&lt;b&gt;Sources:&lt;/b&gt;&lt;br&gt;&lt;i&gt;Mountaineering In The Tetons&lt;/i&gt;, by Fritiof Fryxell&lt;br&gt;&lt;i&gt;A Climber\'s Guide To The Teton Range&lt;/i&gt;, by Leigh Ortenburger and Reynold G. Jackson&lt;br&gt;&lt;i&gt;Climbing Magazine&lt;/i&gt;, Issue 267; July 2008&lt;br&gt;&lt;br&gt;&lt;b&gt;Much of this page courtesy of &lt;a href=\"http://www.summitpost.com/mountains/user_link.pl/user_id/14\"&gt;&lt;b&gt;William Marler&lt;/b&gt;&lt;/a&gt;.&lt;/b&gt;&lt;a name=\"chapter_2\"&gt;&lt;/a&gt;&lt;h2&gt;Getting There&lt;/h2&gt; &lt;div class=\"image-right\" style=\"width: 506px;\" align=\"center\"&gt;&lt;a href=\"/a-rough-drawing-of-some-of/41731\"&gt;&lt;img alt=\"A rough drawing of some of...\" src=\"/images/medium/41731.JPG\" height=\"375\" width=\"500\" style=\"\"&gt;&lt;/a&gt;&lt;br&gt;&lt;span class=\"caption\"&gt;Several of the more popular routes on The Grand. Photo and editing by Alan Ellis&lt;/span&gt;&lt;/div&gt;&lt;br&gt;Grand Teton is located in the northwest corner of Wyoming inside &lt;a href=\"http://www.nps.gov/grte/index.htm\"&gt;Grand Teton National Park&lt;/a&gt;. From the north entrance of the park, drive south 28.5 miles to Moran Junction on highway 191. From Moran Junction, continue south on highway 191 for 18 miles to Moose Junction where you turn right (west). From Jackson, Wyoming, drive north 12 miles on highway 191 to Moose Junction and turn left (west). At Moose Junction, drive about seven miles to the sign for Lupine Meadow Trailhead.  &lt;br&gt;&lt;br&gt;Lupine Meadow trailhead is the jumping off point for most routes on the mountain.  Expect a lot of cars at this trailhead.  From Lupine Meadow Trailhead (6732 ft) follow the trail up through Garnet Canyon. At 1.7 miles the trail forks. Stay right and continue for 1.5 miles where the trail forks again. If climbing the Owen-Spalding, Exum Ridge, or Petzoldt Ridge, go left, following the signs to Garnet Canyon.  After 1.1 miles, the Platforms (8960 ft) campsites are reached. Shortly after, the trail crosses a moraine field and reaches the Meadows (9200 ft) camping area after another 1.0 miles.    At the Meadows, a small log bridge will greet you to the left.  Stay right and follow the trail as it begins to switchback up toward Spalding Falls.   Above the falls, the trail continues climbing until reaching the Lower and Upper Moraine campsites.   Above the Upper Moraine campsites the Lower Saddle headwall will appear directly to the west.  Use the fixed rope to ascend the class 3/4 headwall section.   A short hike above the headwall will end at the Lower Saddle.   &lt;br&gt;&lt;br&gt;If climbing North Face routes or the East Ridge, your trailhead will be the same, but you will go go right to Amphitheater Lake at the second fork (3.2 miles from Lupine Meadow trailhead).&lt;br&gt;&lt;br&gt;Refer to the left side-bar for a list of available routes to the summit.&lt;br&gt;&lt;br&gt;&lt;a href=\"http://www.summitpost.com/mountains/photo_link.pl?photo_id=36829&amp;amp;object_id=1547&amp;amp;type=mountain&amp;amp;mountain_id=1547&amp;amp;route_id=\"&gt;&lt;img src=\"http://www.summitpost.com/images/thumb/16850.JPG\"&gt;&lt;/a&gt;  &lt;a href=\"http://www.summitpost.com/mountains/photo_link.pl?photo_id=73210&amp;amp;object_id=140&amp;amp;type=new#51722\"&gt;&lt;img src=\"http://www.summitpost.com/images/thumb/41731.JPG\"&gt;&lt;/a&gt;  &lt;a href=\"/view_object.php?object_id=121932\" rel=\"no_follow\"&gt;&lt;img src=\"/images/thumb/121932.jpg\"&gt;&lt;/a&gt;&lt;br&gt;&lt;br&gt;&lt;a name=\"chapter_3\"&gt;&lt;/a&gt;&lt;h2&gt;Red Tape&lt;/h2&gt;Grand Teton is located inside &lt;a href=\"http://www.nps.gov/grte/index.htm\"&gt;Grand Teton National Park&lt;/a&gt;. There is a $25.00 entrance fee (per vehicle) to the park which will also get you into Yellowstone (Good for 7 days).   See more info below for other entrance fees.  No permits or fees are required to climb; however, a check-in with the Jenny Lake Ranger Station is recommended. &lt;br&gt;&lt;br&gt;&lt;b&gt;Backcountry Camping Permits:&lt;/b&gt;  Permits are needed for backcountry camping if using a high camp.  Free permits are available at the Jenny Lake Ranger Station on a first come, first served basis. A limited number of permits can be made in advance by submitting your request in writing to the address or FAX number below. Reservations may be made as early as January 1 and are accepted up to May 15. Although the permit is free, there is a $15.00 non-refundable reservation fee. For more information call (307) 739-3397 or (307) 739-3309. Even though you have a reserved permit, you must pick up your permit in person by 10:00 am the morning of your trip or they will make it available to others.   &lt;a href=\"http://www.nps.gov/grte/pubs/brochures/backcountry.pdf\"&gt;Backcountry Camping Brochure (PDF)&lt;/a&gt;&lt;br&gt;&lt;br&gt;Grand Teton National Park&lt;br&gt;Permits Office&lt;br&gt;P.O. Drawer 170&lt;br&gt;Moose, WY   83012&lt;br&gt;FAX: (307) 739-3438&lt;br&gt;&lt;br&gt;&lt;b&gt;SOME ADVICE ON PERMITS&lt;/b&gt;:  There are advantages to both getting your permits in advance and advantages to waiting and getting them the day before your climb.   If you get your permits in advance, you are guaranteed a permit, but your climb day is at the mercy of the weather.  If you wait and get your permits the day before your climb (on a first-come-first-serve basis), you can look at the forecast while you\'re there and choose which weather day offers the best chance of success for your climb.  However, you then take the chance that everyone else is doing the same thing and you won\'t get a permit.  The other disadvantage to getting your permits while there is that they only issue permits the day before, so you have to waste a entire morning \"getting a permit\" when you could be climbing instead.   Most people have no problems getting the permits they need while there but you need to the permit office VERY early (long before they open at 8:00 am). &lt;br&gt;&lt;br&gt;&lt;b&gt;ENTRANCE FEES&lt;/b&gt;&lt;br&gt;&lt;br&gt;Vehicle single entry $25.00 for 7 Days, $12.00 per person for single hiker or bicyclist, or $20.00 per motorcycle.  You may also purchase an \"America The Beautiful Federal Lands Recreational Pass\" for $80.00 which is good for entrance to all National Parks, National Forests, BLM, US Fish &amp;amp; Wildlife, and Bureau of Reclamation sites for one year from date of purchase. If you\'re disabled, you may want to get an Access Pass which are free and good for a lifetime. If you\'re 62 years of age or older, a Senior Pass can be purchased for $10 and they\'re also good for a lifetime. &lt;a href=\"http://www.nps.gov/fees_passes.htm\"&gt;Click here&lt;/a&gt; for more info on fees and passes.  Existing National Park Passes and Golden Age/Access/and Eagle Passes will be honored until they expire. (Info courtesy of SP Member Sequoia)&lt;br&gt;&lt;br&gt;&lt;b&gt;BACKCOUNTRY REGULATIONS&lt;/b&gt;&lt;br&gt;&lt;li type=\"DISC\"&gt;Respect wildlife: Do not approach or feed animals. Observe and photograph from a safe distance; stay at least 300 feet away from large animals.&lt;br&gt;&lt;/li&gt;&lt;li type=\"DISC\"&gt;All overnight camping requires a permit.&lt;br&gt;&lt;/li&gt;&lt;li type=\"DISC\"&gt;Carry out all your garbage.&lt;br&gt;&lt;/li&gt;&lt;li type=\"DISC\"&gt;Hike on established trails to prevent erosion.&lt;br&gt;&lt;/li&gt;&lt;li type=\"DISC\"&gt;Pets, weapons, bicycles or vehicles are not allowed on trails or in the backcountry.&lt;br&gt;&lt;/li&gt;&lt;li type=\"DISC\"&gt;Backcountry sanitation: To prevent contamination of waterways, bury feces in a hole 6-8 inches deep at least 200 feet from streams and lakes. Pack out used toilet paper, tampons, sanitary napkins and diapers in sealed plastic bags. Do not bury or burn them. &lt;br&gt;&lt;br&gt;&lt;div class=\"image\" style=\"width: 506px;\" align=\"center\"&gt;&lt;a href=\"/grizzly/729319\"&gt;&lt;img alt=\"Grizzly \" src=\"/images/medium/729319.JPG\" height=\"332\" width=\"500\" style=\"\"&gt;&lt;/a&gt;&lt;br&gt;&lt;span class=\"caption\"&gt;Grizzly Bear in Grand Teton National Park. All wildlife is dangerous and shoud be given a wide berth. Photo credits to fellow Summitpost member Ferdinand Verboom.&lt;/span&gt;&lt;/div&gt;&lt;a name=\"chapter_4\"&gt;&lt;/a&gt;&lt;h2&gt;When To Climb&lt;/h2&gt;June through early September is considered the summer season.   July is the driest summer month while June is the wettest.   However, that is an average over several years.  It can storm and rain for days on end during any month.   Like typical Rockies weather, the Tetons are notorious for violent afternoon thunderstorms, so get an early start.   Plan on being off the mountain by early afternoon.    However, it has also been known to storm at any time of the day including early in the morning.    Sometimes the weather clears and high pressure moves in for several days of storm-free climbing.    &lt;br&gt;&lt;div align=\"center\" class=\"image-left\" style=\"width: 406px;\"&gt;&lt;a href=\"/view_object.php?object_id=817231\"&gt;&lt;img src=\"/images/medium/817231.JPG\" alt=\"North Face/Ridge of the Grand Teton\" style=\"width: 400px; height: 300px;\"&gt;&lt;/a&gt;&lt;br&gt;&lt;span class=\"caption\"&gt;Clouds over the North Face/Ridge of the Grand Teton as seen from high on Mount Owen. It is prudent to reach the summit or bail point as early in the day as possible. Even if the forecast is clear, unexpected squalls and storms have a tendency to form in the mid to late afternoon. Downclimbing wet, technical terrain in the dark is not a fun time. Photo by Jackson Herring&lt;/span&gt;&lt;/div&gt;&lt;div class=\"image-right\" style=\"width: 406px;\" align=\"center\"&gt;&lt;a href=\"/the-grand-teton-seen-from-the-summit-of-nez-perce-with-stormy-weather-approaching/858687\"&gt;&lt;img alt=\"The Grand Teton seen from the summit of Nez Perce, with stormy weather approaching\" src=\"/images/medium/858687.JPG\" height=\"375\" width=\"500\" style=\"width: 400px; height: 300px;\"&gt;&lt;/a&gt;&lt;br&gt;&lt;span class=\"caption\"&gt;Storm approaching the Grand Teton. People have been killed by lighning strikes on the Grand, so it is best to avoid climbing when bad weather is forecast. It is better to come back and climb the peak another time than to risk your life trying to outrun a thunderstorm. Photo by Jackson Herring&lt;/span&gt;&lt;/div&gt;&lt;br&gt;&lt;br&gt;&lt;a name=\"chapter_5\"&gt;&lt;/a&gt;&lt;h2&gt;Camping and lodging&lt;/h2&gt; &lt;div class=\"image-left\" style=\"width: 506px;\" align=\"center\"&gt;&lt;a href=\"/the-view-of-the-grand-mt/41818\"&gt;&lt;img alt=\"The view of the Grand, Mt....\" src=\"/images/medium/41818.jpg\" height=\"331\" width=\"500\"&gt;&lt;/a&gt;&lt;br&gt;&lt;span class=\"caption\"&gt;American Alpine Club Climber\'s Ranch below The Grand. Photo by Alan Ellis&lt;/span&gt;&lt;/div&gt;&lt;br&gt;&lt;font color=\"red\"&gt;See Red Tape section above for backcountry camping information.&lt;/font&gt;  &lt;br&gt;&lt;br&gt;Grand Teton National Park has five &lt;a href=\"http://www.nps.gov/grte/pphtml/camping.html\"&gt;campgrounds&lt;/a&gt; all on a first come, first served basis.   Jenny Lake campground has 49 sites for tents only and fills very fast.   Other campgrounds: Gros Ventre - 360 sites, Signal Mountain -  86 sites, Colter Bay -  350 sites, Flagg Ranch - 175 sites, and Lizard Creek - 60 sites.   Colter Bay and Flagg Ranch each have over 100 full RV hookup sites.&lt;br&gt;&lt;br&gt;&lt;a href=\"http://www.nps.gov/grte/pphtml/camping.html\"&gt;&lt;b&gt;Grand Teton National Park Camping Info&lt;/b&gt;&lt;/a&gt;&lt;br&gt;&lt;br&gt;&lt;br&gt;&lt;b&gt;American Alpine Club Climbers Ranch&lt;/b&gt;&lt;br&gt;The best option to camping before your climb is to stay at the &lt;a href=\"https://americanalpineclub.org/gtcr\"&gt;American Alpine Club\'s Climber&rsquo;s Ranch&lt;/a&gt; located 7 miles past Moose Junction on Teton Park Road.   The Ranch offers very rustic accommodations for only $20/per night.    Very rustic means you bring your own bedding, mattress, etc., and you will be sharing a cabin with other climbers.  Showers and a cooking area are available.   Lodging is on a first come, first served basis but reservations are accepted. You can make an &lt;a href=\"https://americanalpineclub.org/gtcr_reservations\"&gt;online reservation here&lt;/a&gt; or call (307) 733-7271 between June 1 and September 30.    You do not have to be a member of the American Alpine Club to stay there; however, the AAC rate is only $10.00/night.&lt;br&gt;&lt;br&gt;&lt;b&gt;AAC Grand Teton Climbers Ranch:&lt;/b&gt; &lt;br&gt;ATTN: Manager &lt;br&gt;P.O. Box 57 &lt;br&gt;Moose, WY 83012 &lt;br&gt;&lt;a href=\"mailto:gtcr@americanalpineclub.org\"&gt;Email: gtcr@americanalpineclub.org&lt;/a&gt;  &lt;br&gt;&lt;br&gt;&lt;center&gt;Live webcam taken from the Climber\'s Ranch. Click to enlarge.&lt;br&gt;&lt;br&gt;&lt;a href=\"http://www.tetonrange.com/gtcr/webcam.jpg\"&gt;&lt;img src=\"http://www.tetonrange.com/gtcr/webcam.jpg\" height=\"240\" width=\"320\" style=\"\"&gt;&lt;/a&gt;&lt;/center&gt;&lt;br&gt;&lt;br&gt;&lt;br&gt;&lt;a name=\"chapter_6\"&gt;&lt;/a&gt;&lt;h2&gt;Mountain Conditions&lt;/h2&gt; &lt;div class=\"image-right\" style=\"width: 506px;\" align=\"center\"&gt;&lt;a href=\"/teton-alpenglow/249219\"&gt;&lt;img alt=\"Teton Alpenglow\" src=\"/images/medium/249219.jpg\" height=\"396\" width=\"500\" style=\"\"&gt;&lt;/a&gt;&lt;br&gt;&lt;span class=\"caption\"&gt;Brilliant sunset alpenglow light on Mount Owen, 12,928 feet, and the Grand Teton, 13,770 feet, as seen from Cascade Creek to the northwest - July. Photo by JackB&lt;/span&gt;&lt;/div&gt;&lt;br&gt;All routes are in an alpine environment.  Expect any type of conditions any time of the year, including, storms, snow, and ice.   Temperatures can vary from moderate to sub-freezing.  July is considered the best month to climb with the least average precipitation.   Rock fall is very common in the Tetons and Grand Teton is no exception.   Trailhead elevations are approximately 6800-7000 ft and tree line is at approximately 9000 ft.   The &lt;a href=\"http://www.nps.gov/grte/pphtml/facilities.html\"&gt;Jenny Lake Ranger Station&lt;/a&gt; is the headquarters for Teton climbing.   Call the ranger station at (307) 739-3343 (summer) or (307) 739-3309 (winter) for current conditions.   Also, don\'t forget to go to the webcam link at the top of the page to get current conditions and a live webcam view of the Grand.  Here is a great link for up-to-date route conditions on the Grand:  &lt;a href=\"http://www.tetonclimbing.blogspot.com/\"&gt;www.TetonClimbing.blogspot.com&lt;/a&gt;.&lt;br&gt;&lt;a name=\"chapter_7\"&gt;&lt;/a&gt;&lt;h2&gt;Maps&lt;/h2&gt;Trails Illustrated:  Grand Teton National Park&lt;br&gt;Earth Walk Press:  Grand Teton National Park&lt;br&gt;USGS 1/24 Quad:   Grand Teton&lt;br&gt;&lt;br&gt;Online Maps:&lt;br&gt;&lt;a href=\"http://data2.itc.nps.gov/parks/grte/ppMaps/ACF18EE%2Epdf\"&gt;Detailed Map Of GTTNP (PDF)&lt;/a&gt;&lt;br&gt;&lt;a href=\"http://data2.itc.nps.gov/parks/grte/ppMaps/ACF18F6%2Epdf\"&gt;Low Resolution Map Of GTTNP (PDF)&lt;/a&gt;&lt;br&gt;&lt;a href=\"http://www.grandtetonpark.org/SearchResults.asp?Cat=7\"&gt;Order Maps Online&lt;/a&gt;&lt;br&gt;&lt;a name=\"chapter_8\"&gt;&lt;/a&gt;&lt;h2&gt;Guide Services&lt;/h2&gt;Although climbing Grand Teton on your own can be a rewarding experience, one may choose to use one of the available guide services if you are a novice or if you desire to do a more challenging route above your current ability.   These guide services are considered some of the best in the country.&lt;br&gt;&lt;br&gt;&lt;a href=\"http://www.exumguides.com/\"&gt;Exum Mountain Guides&lt;/a&gt;&lt;br&gt;&lt;a href=\"http://www.jhmg.com/\"&gt;Jackson Hole Mountain Guides&lt;/a&gt;&lt;br&gt;&lt;br&gt;&lt;hr color=\"black\" size=\"1\"&gt;&lt;br&gt;&lt;br&gt;&lt;center&gt;&lt;b&gt;Photo Of The Week:   April 2005&lt;br&gt;&lt;a href=\"http://www.summitpost.org/mountains/photo_link.pl/p/photo_id__143838__object_id__140__type__mountain__mountain_id__140__route_id____user_id____order_by____limit__\"&gt;&lt;img src=\"http://www.summitpost.org/images/original/96996.jpg\" border=\"0\" style=\"\"&gt;&lt;/a&gt;&lt;br&gt;&lt;br&gt;&lt;b&gt;The Tetons in morning light from the Snake River. Taken with a large format Linhof camera, 150mm Schneider lens on Kodak E100VS film.&lt;br&gt;Photo by Arthur Yeo&lt;/b&gt;&lt;/b&gt;&lt;/center&gt;&lt;b&gt;&lt;br&gt;&lt;/b&gt;&lt;/li&gt;&lt;a name=\"chapter_9\"&gt;&lt;/a&gt;&lt;h2&gt;Images&lt;/h2&gt;&lt;div align=\"center\"&gt;&lt;table id=\"my_images\"&gt;&lt;tr&gt;&lt;td&gt;&lt;a href=\"/teton-alpenglow/249219/c-150312\"&gt;&lt;img alt=\"Teton Alpenglow\" src=\"/images/square/249219.jpg\"&gt;&lt;/a&gt;&lt;/td&gt;&lt;td&gt;&lt;a href=\"/the-tetons-in-morning-light/96996/c-150312\"&gt;&lt;img alt=\"The Tetons in morning light...\" src=\"/images/square/96996.jpg\"&gt;&lt;/a&gt;&lt;/td&gt;&lt;td&gt;&lt;a href=\"/this-must-be-paradise/106821/c-150312\"&gt;&lt;img alt=\"This must be paradise...\" src=\"/images/square/106821.jpg\"&gt;&lt;/a&gt;&lt;/td&gt;&lt;td&gt;&lt;a href=\"/a-snowy-grand-teton-from-the/109958/c-150312\"&gt;&lt;img alt=\"A snowy Grand Teton from the...\" src=\"/images/square/109958.jpg\"&gt;&lt;/a&gt;&lt;/td&gt;&lt;td&gt;&lt;a href=\"/grand-teton-rappel/29479/c-150312\"&gt;&lt;img alt=\"Grand Teton rappel\" src=\"/images/square/29479.jpg\"&gt;&lt;/a&gt;&lt;/td&gt;&lt;td&gt;&lt;a href=\"/alpenglow-on-the-tetons/204161/c-150312\"&gt;&lt;img alt=\"Alpenglow on the Tetons\" src=\"/images/square/204161.jpg\"&gt;&lt;/a&gt;&lt;/td&gt;&lt;td&gt;&lt;a href=\"/bill-krause-on-the-first-ice/16091/c-150312\"&gt;&lt;img alt=\"Bill Krause on the first ice...\" src=\"/images/square/16091.jpg\"&gt;&lt;/a&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td&gt;&lt;a href=\"/grand-teton-national-park-golden-mantled-ground-squirrel/315948/c-150312\"&gt;&lt;img alt=\"Grand Teton National Park Golden-mantled Ground Squirrel\" src=\"/images/square/315948.JPG\"&gt;&lt;/a&gt;&lt;/td&gt;&lt;td&gt;&lt;a href=\"/a-rough-drawing-of-some-of/41731/c-150312\"&gt;&lt;img alt=\"A rough drawing of some of...\" src=\"/images/square/41731.JPG\"&gt;&lt;/a&gt;&lt;/td&gt;&lt;td&gt;&lt;a href=\"/grand-teton/819090/c-150312\"&gt;&lt;img alt=\"Grand Teton\" src=\"/images/square/819090.JPG\"&gt;&lt;/a&gt;&lt;/td&gt;&lt;td&gt;&lt;a href=\"/the-tetons-after-a-storm/118411/c-150312\"&gt;&lt;img alt=\"The Tetons after a storm...\" src=\"/images/square/118411.jpg\"&gt;&lt;/a&gt;&lt;/td&gt;&lt;td&gt;&lt;a href=\"/grand-teton-sunset/841605/c-150312\"&gt;&lt;img alt=\"Grand Teton sunset...\" src=\"/images/square/841605.JPG\"&gt;&lt;/a&gt;&lt;/td&gt;&lt;td&gt;&lt;a href=\"/sunrise-on-the-grand-teton/122462/c-150312\"&gt;&lt;img alt=\"Sunrise on the Grand Teton...\" src=\"/images/square/122462.jpg\"&gt;&lt;/a&gt;&lt;/td&gt;&lt;td&gt;&lt;a href=\"/closeup-of-the-grand/308679/c-150312\"&gt;&lt;img alt=\"Closeup of the Grand\" src=\"/images/square/308679.JPG\"&gt;&lt;/a&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td colspan=\"7\" align=\"center\"&gt;[ &lt;a href=\"/grand-teton/images/p-150312\"&gt;View Gallery - 727 More Images&lt;/a&gt; ]&lt;/td&gt;&lt;/tr&gt;&lt;/table&gt;&lt;/div&gt;&lt;/article&gt;date:difficulty:'),(15,150325,'/mount-elbert/150325','/sawatch-range/171834','Mount Elbert','','','','14440','',419507,39.1178,-106.445,'Colorado, United States, North America','','',171834,'Sawatch Range','','','','','','Mountain-Rock','content: &lt;article&gt;&lt;p&gt;&lt;/p&gt;&lt;a name=\"chapter_1\"&gt;&lt;/a&gt;&lt;h2 id=\"first_h2\"&gt;Under New Ownership&lt;/h2&gt;&lt;p&gt;&lt;i&gt;This wonderful page was transferred to me by long time SP member Alan Ellis. I am honored to be able to adopt it and will make sure to keep this page as up to date and accurate as possible. I would like to send many thanks to Alan and note that a decent portion of the text was written by him. Feel free to give me input on how to maintain this page.&lt;/i&gt;&lt;br&gt;&lt;/p&gt;&lt;a name=\"chapter_2\"&gt;&lt;/a&gt;&lt;h2&gt;Overview&lt;/h2&gt;Mount Elbert has a lot to be proud of:   It is the highest mountain in Lake County, highest in Colorado, highest in the Rockies, and second highest in the contiguous United States.  But when you ask a non-climber or someone outside of Colorado, nobody has heard of it.  However, that&rsquo;s their loss, because Mt. Elbert is a spectacular peak and worthy of recognition and respect from all.   One of the great things about Mt. Elbert is the relatively moderate terrain to the summit, allowing almost anyone with reasonable fitness to enjoy the mountain and its status.   &lt;br&gt;&lt;br&gt;Located in the Sawatch Range of the Colorado Rockies, Mt. Elbert was named for Samuel Elbert who was a controversial territorial governor of Colorado in 1873.   The first recorded summit of the peak was by H.W. Stuckle of the Haydon Survey in 1874.   Since that time, it has been climbed by thousands and even been summited by jeep.  &lt;br&gt;&lt;br&gt;In the seventies, there was a movement among a group of folks that felt Mt. Elbert\'s next door neighbor, Mt. Massive, was more deserving to be Colorado\'s highest peak. They stacked rocks in an attempt to raise the height of Massive\'s summit on top. Many more detractors would tear the summit cairn down upon their visits, so the attempt at changing Massive\'s elevation was eventually given up and Mt. Elbert has retained the title as the highest.  (Courtesy of &lt;a href=\"http://www.summitpost.com/mountains/user_link.pl/user_id/2043\"&gt;Aaron Johnson&lt;/a&gt;).   Although most (or all) guidebooks list Mt. Elbert as 14,433 ft., a 2002 recalculation of old surveyed elevations, resulting in a world-wide adjustment of peak elevations, concluded that Mt. Elbert is actually 14,440 ft.   &lt;a href=\"http://www.skyrunner.com/story/coheights.htm\"&gt;Denver post article&lt;/a&gt;. Despite this re-survey, most Colorado Climbers regularly use the old survey elevations. &lt;br&gt;&lt;br&gt;There are five main routes to the summit, the two most popular and easiest are the South Mt. Elbert Trail and the North Mt. Elbert Trail, both being well trodden class one trails.  These relatively easy paths to the summit are busy in the summer, attracting all types of hikers and climbers, young and old.   Like any high peak, weather, altitude, and personal ability must be considered before attempting Mt. Elbert.   But with a little preparation and planning, Mt. Elbert offers a wonderful climbing and outdoor experience.  &lt;b&gt;The most important thing to remember about climbing this mountain (or any other in Colorado) is to start early....like sunrise....to avoid dangerous afternoon storms.   If you see dark puffy clouds building, turn around and get off the mountain.&lt;/b&gt;&lt;br&gt;&lt;br&gt;&lt;div class=\"image\" style=\"width:500px;\" align=\"center\"&gt;&lt;a href=\"/view_object.php?object_id=233116\"&gt;&lt;img src=\"/images/medium/233116.JPG\" alt=\"Fall &amp;amp; Winter\" style=\"\"&gt;&lt;/a&gt;&lt;br&gt;&lt;span class=\"caption\"&gt;Fall and winter battle it out on Mount Elbert&lt;/span&gt;&lt;/div&gt;&lt;a name=\"chapter_3\"&gt;&lt;/a&gt;&lt;h2&gt;Getting There&lt;/h2&gt;&lt;div class=\"image-right\" style=\"width: 506px;\" align=\"center\"&gt;&lt;a href=\"/the-south-approach-looking/17982\"&gt;&lt;img alt=\"The South approach looking...\" src=\"/images/medium/17982.jpg\" style=\"\" height=\"375\" width=\"500\"&gt;&lt;/a&gt;&lt;br&gt;&lt;span class=\"caption\"&gt;The South approach looking past peak 13,227. Photo by cliffhanger51&lt;/span&gt;&lt;/div&gt;Mt. Elbert is located in central Colorado near the little town of Twin Lakes about 140 miles west of Denver, 50 miles south of Vail, and 40 miles east of Aspen.   The closest large town is Leadville, approximately 16 miles northeast.    &lt;br&gt;&lt;br&gt;The trailheads for routes are: South Mt. Elbert Trail Trailhead for the east ridge route, North Mt. Elbert Trail Trailhead for the northeast ridge, South Halfmoon Creek Trailhead for the northwest ridge,  Black Cloud Trailhead for southeast ridge, and Echo Canyon Trailhead for the southwest ridge route.    Please note the &lt;a href=\"http://www.summitpost.com/mountains/photo_link.pl/p/photo_id__51659__object_id__634__type__user__mountain_id____route_id____user_id____order_by____limit__\"&gt;below photo and route graphics&lt;/a&gt; by &lt;a href=\"http://www.summitpost.com/mountains/user_link.pl?user_id=634\"&gt;Kane&lt;/a&gt; which shows several of the less common routes and variations of routes of Mt. Elbert.    The most popular and standard routes for Mt. Elbert are the South Mount Elbert Trail and the North Mount Elbert Trail as described below.&lt;br&gt;&lt;br&gt;&lt;b&gt;South Mt. Elbert Trailhead:&lt;/b&gt; (9560 ft) From Leadville, drive south approximately 10 miles to the intersection of U.S. 24 and Colorado (CO) 82. From Buena Vista drive north 20 miles to the same intersection. Once on CO 82, drive west 4 miles to Lake County Road 24 (Do not confuse with U.S. 24). Turn right (north) on Lake County 24 and drive one mile to Lakeview Campground. The trailhead is on the northwest side of the campground and is also an access point for the Colorado/Continental Divide Trail in which a short section runs from Lakeview to the North Mt. Elbert Trailhead. Shortly after Lakeview Campground, turn left into an obvious parking area, overlook, and dirt road. High clearance vehicles can drive to 10,440 ft. by driving west past the parking lot on a rough road for 2 miles until the road ends and you see other vehicles parked. The upper trailhead is on the north side of the parking area. Cross a creek via a footbridge and continue a short distance until reaching a signed intersection of the Mt. Elbert/Colorado/Continental Divide Trail where you turn left.   See the left side bar or click here for the &lt;a href=\"http://www.summitpost.org/route/322285/south-mt-elbert-trail.html\"&gt;South Mt. Elbert Trail&lt;/a&gt;. &lt;br&gt;&lt;br&gt;&lt;b&gt;North Mt. Elbert Trailhead:&lt;/b&gt; (10,050 ft.)   From Leadville, drive 3.5 miles south on U.S. 24 to Colorado (CO) 300.  Turn right (west) on CO 300 and drive .7 miles.    At .7 miles, turn left (south) on Lake County 11 which is a newly paved road.   Drive south 1.8 miles on Lake County 11 and turn right (west) at the signed intersection for Halfmoon Creek.   Continue southwest past the Halfmoon Creek Campground and the Elbert Creek Campground until reaching the signed North Mt. Elbert Trailhead at 6.9 miles.  Note:  This trailhead is also referred to as \"South Colorado/Main Range Trailhead\" in some guidebooks.  It is an access point for the Colorado/Continental Divide trail, a short section of which runs from here to the Lakeview Campground to the south.&lt;br&gt;&lt;br&gt;&lt;b&gt;South Halfmoon Creek Trailhead:&lt;/b&gt; (10,240)    Follow directions to the North Mt. Elbert Trailhead except continue another 2.1 miles to the signed South Halfmoon Creek trailhead.  &lt;br&gt;&lt;br&gt;&lt;b&gt;Black Cloud Trailhead:&lt;/b&gt; (9700 ft)     From the intersection of U.S. 24 and CO 82:   West on CO 82 for 10.5 miles to a right turn (north) to the signed trailhead.&lt;br&gt;&lt;br&gt;&lt;b&gt;Echo Canyon Trailhead:&lt;/b&gt; (10,000 ft)   From the intersection of U.S. 24 and CO 82:   West on CO 82 for 12.5 miles to a right turn (north) where there is a cabin and a sign for Echo Canyon.&lt;a name=\"chapter_4\"&gt;&lt;/a&gt;&lt;h2&gt;Red Tape&lt;/h2&gt;No permits or fees are required to park, climb, or hike.&lt;br&gt;&lt;br&gt;Mt. Elbert is located in the &lt;a href=\"http://www.fs.fed.us/r2/psicc/leadvile/\"&gt;Leadville Ranger District&lt;/a&gt; of the &lt;a href=\"http://www.fs.fed.us/r2/psicc/index.shtml\"&gt;San Isabel National Forest&lt;/a&gt;.   &lt;br&gt;&lt;br&gt;Leadville Ranger District, San Isabel National Forest, 2013 N. Poplar, Leadville, CO   80461    Phone:   (719) 486-0749   Fax:  (719) 486-0928.&lt;a name=\"chapter_5\"&gt;&lt;/a&gt;&lt;h2&gt;When To Climb&lt;/h2&gt; &lt;div class=\"image-left\" style=\"width: 506px;\" align=\"center\"&gt;&lt;a href=\"/this-is-on-the-south-mt/21939\"&gt;&lt;img alt=\"This is on the South Mt....\" src=\"/images/medium/21939.jpg\" style=\"\" height=\"375\" width=\"500\"&gt;&lt;/a&gt;&lt;br&gt;&lt;span class=\"caption\"&gt;Mt. Elbert in June. Photo by Alan Ellis&lt;/span&gt;&lt;/div&gt;&lt;br&gt;As with most Colorado high peaks, June through September is considered the summer climbing season.   However, Mt. Elbert is also climbed in the dead of winter by experienced mountaineers.   It is a popular ski mountaineering destination due to low avalanche danger on several of the routes.   Louis Dawson recommends using the South Mt. Elbert Trail and the North Mt. Elbert Trail for winter ascents.   Violent thunderstorms are a daily occurance in summer.    Plan to be off the summit or ridges by early afternoon to avoid lightning danger.   Start very early and allow 6-8 hours for climb and descent.&lt;a name=\"chapter_6\"&gt;&lt;/a&gt;&lt;h2&gt;Winter Climbing of Mt. Elbert&lt;/h2&gt;&lt;div class=\"image-left\" style=\"width: 506px;\" align=\"center\"&gt;&lt;a href=\"/view_object.php?object_id=373049\"&gt;&lt;img src=\"/images/medium/373049.jpg\" alt=\"Elbert January 2008\" style=\"width: 500px; height: 207.24346076458752px;\"&gt;&lt;/a&gt;&lt;br&gt;Winter alpenglow on Mount Elbert&lt;/div&gt;Winter is a great time to climb Mt. Elbert.  Afternoon thunderstorms are not an issue, but winter brings an entire new set of conditions that must be considered.  The standard winter route is via the east ridge (South Mt. Elbert Trail) which begins at the overlook near Lakeview Campground.  The 4WD road to the upper trailhead is closed in winter.&lt;br&gt;&lt;br&gt;This is a great winter route because there is no avalanche danger if you stick to the route.   However, do not take the mountain lightly in winter.  Since the 4WD road is closed, this equates to an 11-mile round-trip climb.  Due to the distance, some climb Mt. Elbert in winter as an overnighter, with a camp either at the 4WD upper trailhead or on the shoulder in the conifer just below treeline.  But most still climb it in a one-day push....however, expect a LONG day.   The road to the upper 4WD trailhead is easy to follow and snowshoes will sometimes be needed.  Once at the upper trailhead, the route finding can be tricky because the trail cannot be seen (due to snow).   Once you reach the upper trailhead, cross the footbridge and head northeast past the ponds (on the right) until reaching &lt;a href=\"http://www.jackieandalan.com/images/elberttrailhead.jpg\"&gt;this sign&lt;/a&gt; a short distance later.   Continue past the sign approximately 200 yards until coming to a second sign where you turn a hard left (northwest).   Climb northwest up the steep slope through the aspen forest.  With luck, you\'ll run into the switchbacks, but if not, the general rule is to head directly northwest until reaching the shoulder just below treeline.   Normally, previous hikers have created a \"trench\" in the snow to follow, but after a large fresh snowfall, the trench may not be evident.  Also, there are sometimes orange ribbons tied to trees to mark the winter route up through the apens.  Once on the shoulder, follow the standard route using the maps below.&lt;br&gt;&lt;br&gt;&lt;u&gt;Gear:&lt;/u&gt;  Ice axe and crampons are generally not needed; however, conditions may dictate otherwise.   Normally, snowshoes are a &lt;b&gt;MUST&lt;/b&gt; from the trailhead until reaching treeline, but sometimes they are needed all the way to the top depending on conditions and/or recent snow and winds.  Or....they may not be needed at all if the snow is hard packed.  The upper ridge above treeline is windswept and snowshoes are not generally needed.&lt;a name=\"chapter_7\"&gt;&lt;/a&gt;&lt;h2&gt;Camping and Lodging&lt;/h2&gt; &lt;div class=\"image-left\" style=\"width: 506px;\" align=\"center\"&gt;&lt;a href=\"/beaver-lake/204101\"&gt;&lt;img alt=\"Beaver lake\" src=\"/images/medium/204101.jpg\" style=\"\" height=\"375\" width=\"500\"&gt;&lt;/a&gt;&lt;br&gt;&lt;span class=\"caption\"&gt;Beaver Lake. Photo by Panhandler&lt;/span&gt;&lt;/div&gt;&lt;br&gt;There are several &lt;a href=\"http://www.fs.fed.us/r2/psicc/recreation/camping/camp_guide.shtml\"&gt;Forest Service campgrounds&lt;/a&gt; nearby requiring fees.    The closest to the South Mt. Elbert trail is Lakeview Campground as described in \"Getting There\" section.  Lakeview has a campground host  from May 15 until September 8.   It has 59 sites at $12.00/night and will accomodate an RV up to 32 ft.    Drinking water, pit and flushing toilets, no showers.   &lt;a href=\"http://www.reserveusa.com/jsp/commonpage.jsp?goto=/nrrs/co/lav1/newindex.html\"&gt;Reservations&lt;/a&gt; may be made at Lakeview Campground.  The closest campgrounds to Halfmoon Creek and North Mt. Elbert Trailheads are Halfmoon Creek Campground and Elbert Creek Campground located on the road to the trailheads as described in the \"Getting There\" section.   Elbert Creek has 17 sites at $13.00/night and will accomodate up to a 16 ft trailer.   Open from May 30 to September 30, it has drinking water, pit toilets, but no showers.    Halfmoon Creek has 24 sites at $13.00/night and will accomodate up to 16 ft trailer.   Open from May 20 to September 30, it has drinking water, pit toilet, but no showers.&lt;br&gt;&lt;br&gt;There are approximately nine other Forest Service campgrounds nearby.    Call the Forest Service at (719) 486-0749 or see their website.   They will send you a packet with free information.&lt;br&gt;&lt;br&gt;Primitive camping is allowed pretty much anywhere on the mountain.  Avoid camping along the 4wd road, next to a trail, or in the parking areas.&lt;br&gt;&lt;br&gt;Sugar Loafin\' RV Park and Camground:   4 miles west of Leadville on CO 300.   (719) 486-1031.   Full service RV campground.   Showers available.&lt;br&gt;&lt;br&gt;Lodging:    In Twin Lakes, &lt;a href=\"http://www.twinlakesnordicinn.com\"&gt;The Nordic Inn Bed and Breakfast&lt;/a&gt; is the best place to stay for Mt. Elbert climbers.  John Slater, owner, has being running the Nordic for 18 years and is an expert on the mountain.  The Nordic Inn is highly recommended.    Further down highway 82, the &lt;a href=\"http://www.vtinet.com/mtelbert/\"&gt;Mt. Elbert Lodge&lt;/a&gt;, B&amp;amp;B and cabins, is within walking distance to two trailheads, Black Cloud and Echo Canyon.  &lt;a href=\"http://www.leadvilleusa.com/\"&gt;Leadville&lt;/a&gt;, &lt;a href=\"http://www.aspenalive.com\"&gt;Aspen&lt;/a&gt;, and &lt;a href=\"http://www.fourteenernet.com/buenavista/\"&gt;Buena Vista&lt;/a&gt; offer the closest hotels, motels, and bed and breakfasts outside of Twin Lakes.   Aspen, to the west, also has lodging but is considerably more expensive.    There is a hostel in Leadville at 500 E. 7th which has $3.00 showers and rooms for $15.00/night.  (719) 486-9334.&lt;a name=\"chapter_8\"&gt;&lt;/a&gt;&lt;h2&gt;Mountain Conditions&lt;/h2&gt;&lt;p&gt;Most of the trails to the summit are well maintained and easy to follow.   In fact, in some sections above treeline, the trails have become multi-trails where people have started new ones to avoid the deep ruts of the old trails.   Try to avoid starting new trails and stick to the established path to minimize impact. Treeline is at approximately 12,000 ft; therefore, you have about 2400 ft of climbing in weather-exposed terrain. &lt;/p&gt;&lt;a href=\"http://www.weather.com/weather/local/USCO0385?from=search_city\"&gt;&lt;b&gt;Weather and Current Condtions&lt;/b&gt;&lt;/a&gt;&lt;br&gt;&lt;a href=\"http://www.9news.com/weather/doppler.htm\"&gt;&lt;b&gt;Doppler Radar&lt;/b&gt;&lt;/a&gt;&lt;br&gt;&lt;a href=\"http://www.weatherhub.com/radar/kftgradar.htm\"&gt;&lt;b&gt;Additional Radar&lt;/b&gt;&lt;/a&gt;&lt;br&gt;&lt;a name=\"chapter_9\"&gt;&lt;/a&gt;&lt;h2&gt;Maps&lt;/h2&gt;San Isabel National Forest (U.S. Forest Service)&lt;br&gt;Quadrangles:   Mount Elbert, Mount Massive  (USGS)&lt;br&gt;&lt;a href=\"http://shop.nationalgeographic.com/product/254.html\"&gt;Trails Illustrated #127&lt;/a&gt; - Aspen/Independence Pass&lt;br&gt;&lt;a href=\"http://www.latitude40maps.com/acbg.html\"&gt;Latitude 40&lt;/a&gt; - Aspen/Crested Butte/Gunnison&lt;br&gt;&lt;a href=\"http://www.fs.fed.us/r2/psicc/maps/index.shtml\"&gt;Online Maps&lt;/a&gt;&lt;br&gt;&lt;br&gt;Note:   The trail for the South Mt. Elbert trail is marked incorrectly on the Mt. Elbert 1/24 quad.   The correct route is displayed in the maps below.&lt;div&gt;&lt;br&gt;&lt;/div&gt;&lt;div&gt;&lt;div align=\"center\"&gt;&lt;table class=\"redactor_table\" id=\"table21165\" style=\"width: 23px; background-color: rgba(0, 0, 0, 0);\"&gt;&lt;tbody&gt;&lt;tr&gt;&lt;td&gt;&lt;div class=\"image\" style=\"width:500px;\" align=\"center\"&gt;&lt;a href=\"/view_object.php?object_id=9864\"&gt;&lt;img src=\"/images/medium/9864.jpg\" alt=\"South Mt. Elbert trail route.\" style=\"\"&gt;&lt;/a&gt;&lt;br&gt;&lt;span class=\"caption\"&gt;South Mt. Elbert trail route.&lt;/span&gt;&lt;/div&gt;&lt;/td&gt;&lt;td&gt;&lt;div class=\"image\" style=\"width: 424.0327868852459px;\" align=\"center\"&gt;&lt;a href=\"http://cunap.com/~hardingr/projects/14ers/elbert2-gps.jpg\"&gt;&lt;img src=\"http://cunap.com/~hardingr/projects/14ers/elbert2-gps.jpg\" alt=\"undefined\" style=\"width: 418.0327868852459px; height: 255px;\"&gt;&lt;/a&gt;&lt;br&gt;&lt;span class=\"caption\"&gt;undefined&lt;/span&gt;&lt;/div&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/tbody&gt;&lt;/table&gt;&lt;/div&gt;&lt;p&gt;&lt;/p&gt;&lt;/div&gt;&lt;a name=\"chapter_10\"&gt;&lt;/a&gt;&lt;h2&gt;External Links&lt;/h2&gt;&lt;p&gt;&lt;a href=\"http://forecast.weather.gov/MapClick.php?lat=39.10981695656077&amp;amp;lon=-106.44481658935547#.Umspbvk_uQ4\"&gt;Weather Forecast&lt;/a&gt;&lt;/p&gt;&lt;p&gt;&lt;a href=\"http://www.leadville.com/\"&gt;Leadville Information&lt;/a&gt;&lt;/p&gt;&lt;p&gt;&lt;a href=\"http://14ers.com/php14ers/trailheads2.php?thparm=sw03\"&gt;Current Trailhead Conditions&lt;/a&gt;&lt;/p&gt;&lt;p&gt;&lt;a href=\"http://listsofjohn.com/PeakStats/Climbers.php?Id=1\"&gt;Mount Elbert on LOJ&lt;/a&gt;&lt;/p&gt;&lt;p&gt;Also see Gerry Roach\'s \"Colorado\'s Fourteeners\" book for more great information&lt;/p&gt;&lt;div align=\"center\"&gt;&lt;a name=\"messages\"&gt;&lt;/a&gt;&lt;a name=\"chapter_11\"&gt;&lt;/a&gt;&lt;h2&gt;Additions and Corrections&lt;/h2&gt;[ &lt;a href=\"/object_discussion.php?type=vote_comments&amp;amp;object_id=150325&amp;amp;is_addition=1#vote_form\"&gt;Post an Addition or Correction&lt;/a&gt; ]&lt;table cellpadding=\"0\" cellspacing=\"0\" style=\"width: 775px;\"&gt;&lt;tr&gt;&lt;td&gt;&lt;table cellpadding=\"2\" cellspacing=\"2\" width=\"100%\"&gt;&lt;tr&gt;&lt;td align=\"left\"&gt;&lt;span class=\"pagertext\"&gt;Viewing: &lt;b&gt;1&lt;/b&gt;-&lt;b&gt;11&lt;/b&gt; of &lt;b&gt;11&lt;/b&gt;&lt;/span&gt;&lt;/td&gt;&lt;td align=\"center\"&gt;&lt;/td&gt;&lt;td align=\"right\"&gt;    &lt;/td&gt;&lt;/tr&gt;&lt;/table&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/table&gt;&lt;table border=\"0\" width=\"775\" class=\"messages\"&gt;&lt;tr&gt;&lt;td width=\"110\" align=\"left\" valign=\"top\" style=\"padding-top: 6px;\"&gt;&lt;a name=\"338086\"&gt;&lt;/a&gt; &lt;a class=\"dis_user_lnk\" href=\"/users/vanman798/23249\"&gt;vanman798&lt;/a&gt;&lt;/td&gt;&lt;td valign=\"top\" style=\"padding-top: 6px;\"&gt;&lt;b&gt;14,433 ft&lt;/b&gt;&lt;/td&gt;&lt;td align=\"right\" valign=\"top\" nowrap style=\"font-size: 10px;\"&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td valign=\"top\" rowspan=\"2\" align=\"left\" style=\"padding-top: 6px;\"&gt;&lt;p class=\"disc_avatar\"&gt;&lt;a href=\"/users/vanman798/23249\"&gt;&lt;img alt=\"vanman798\" width=\"50\" height=\"50\" border=\"0\" src=\"/images/avatars/853975.jpg\"&gt;&lt;/a&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=\"font-size: 9px; color: #888;\"&gt;Hasn\'t voted&lt;/span&gt;&lt;/p&gt;&lt;/td&gt;&lt;td colspan=\"2\" style=\"padding-top: 6px;\"&gt;Most other sites list the evelation as 14,433 ft.  See  &lt;a href=\"http://peakbagger.com/peak.aspx?pid=5736\"&gt;http://peakbagger.com/peak.aspx?pid=5736&lt;/a&gt; for example.&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td valign=\"bottom\" style=\"padding-top: 6px;\" colspan=\"2\"&gt;&lt;span style=\"font-size: 9px; color: #888;\"&gt;Posted Sep 9, 2009 12:02 pm&lt;/span&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/table&gt;&lt;table border=\"0\" width=\"745\" class=\"messages\" style=\"margin-left: 30px;\"&gt;&lt;tr&gt;&lt;td rowspan=\"4\" valign=\"top\"&gt;&lt;img src=\"/styles/layout/reply_arrow.gif\"&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td width=\"110\" align=\"left\" valign=\"top\" style=\"padding-top: 6px;\"&gt;&lt;a name=\"338139\"&gt;&lt;/a&gt; &lt;a class=\"dis_user_lnk\" href=\"/users/alan-ellis/1202\"&gt;Alan Ellis&lt;/a&gt;&lt;/td&gt;&lt;td valign=\"top\" style=\"padding-top: 6px;\"&gt;&lt;b&gt;Re: 14,433 ft&lt;/b&gt;&lt;/td&gt;&lt;td align=\"right\" valign=\"top\" nowrap style=\"font-size: 10px;\"&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td valign=\"top\" rowspan=\"2\" align=\"left\" style=\"padding-top: 6px;\"&gt;&lt;p class=\"disc_avatar\"&gt;&lt;a href=\"/users/alan-ellis/1202\"&gt;&lt;img alt=\"Alan Ellis\" width=\"50\" height=\"50\" border=\"0\" src=\"/images/avatars/794223.JPG\"&gt;&lt;/a&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=\"font-size: 9px; color: #888;\"&gt;Voted 10/10&lt;/span&gt;&lt;/p&gt;&lt;/td&gt;&lt;td colspan=\"2\" style=\"padding-top: 6px;\"&gt;Peakbagger.com is incorrect.  You need to read paragraph three of the main Mt. Elbert page.  &lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td valign=\"bottom\" style=\"padding-top: 6px;\" colspan=\"2\"&gt;&lt;span style=\"font-size: 9px; color: #888;\"&gt;Posted Sep 9, 2009 4:21 pm&lt;/span&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/table&gt;&lt;table border=\"0\" width=\"745\" class=\"messages\" style=\"margin-left: 30px;\"&gt;&lt;tr&gt;&lt;td rowspan=\"4\" valign=\"top\"&gt;&lt;img src=\"/styles/layout/reply_arrow.gif\"&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td width=\"110\" align=\"left\" valign=\"top\" style=\"padding-top: 6px;\"&gt;&lt;a name=\"338173\"&gt;&lt;/a&gt; &lt;a class=\"dis_user_lnk\" href=\"/users/vanman798/23249\"&gt;vanman798&lt;/a&gt;&lt;/td&gt;&lt;td valign=\"top\" style=\"padding-top: 6px;\"&gt;&lt;b&gt;Re: 14,433 ft&lt;/b&gt;&lt;/td&gt;&lt;td align=\"right\" valign=\"top\" nowrap style=\"font-size: 10px;\"&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td valign=\"top\" rowspan=\"2\" align=\"left\" style=\"padding-top: 6px;\"&gt;&lt;p class=\"disc_avatar\"&gt;&lt;a href=\"/users/vanman798/23249\"&gt;&lt;img alt=\"vanman798\" width=\"50\" height=\"50\" border=\"0\" src=\"/images/avatars/853975.jpg\"&gt;&lt;/a&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=\"font-size: 9px; color: #888;\"&gt;Hasn\'t voted&lt;/span&gt;&lt;/p&gt;&lt;/td&gt;&lt;td colspan=\"2\" style=\"padding-top: 6px;\"&gt;Thanks, I should have read that \"a recent survey of several Colorado peaks, including Mt. Elbert, concluded that Mt. Elbert is actually 14,440 ft.\"&lt;br&gt;&lt;br&gt;&lt;br&gt;&lt;br&gt;By the way, who does these surveys?  Is it the USGS? Must be someone else cause the USGS site (see http://egsc.usgs.gov/isb/pubs/booklets/elvadist/elvadist.html#14,000) lists it at 14,433 ft, but that is a 1967 figure (from the National Geodetic Survey).  &lt;br&gt;&lt;br&gt;&lt;br&gt;&lt;br&gt;Sorry, I\'m just very curious as to when and who did the most recent survey and came up with 14,440 ft.  &lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td valign=\"bottom\" style=\"padding-top: 6px;\" colspan=\"2\"&gt;&lt;span style=\"font-size: 9px; color: #888;\"&gt;Posted Sep 9, 2009 6:06 pm&lt;/span&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/table&gt;&lt;table border=\"0\" width=\"745\" class=\"messages\" style=\"margin-left: 30px;\"&gt;&lt;tr&gt;&lt;td rowspan=\"4\" valign=\"top\"&gt;&lt;img src=\"/styles/layout/reply_arrow.gif\"&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td width=\"110\" align=\"left\" valign=\"top\" style=\"padding-top: 6px;\"&gt;&lt;a name=\"338211\"&gt;&lt;/a&gt; &lt;a class=\"dis_user_lnk\" href=\"/users/alan-ellis/1202\"&gt;Alan Ellis&lt;/a&gt;&lt;/td&gt;&lt;td valign=\"top\" style=\"padding-top: 6px;\"&gt;&lt;b&gt;Re: 14,433 ft&lt;/b&gt;&lt;/td&gt;&lt;td align=\"right\" valign=\"top\" nowrap style=\"font-size: 10px;\"&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td valign=\"top\" rowspan=\"2\" align=\"left\" style=\"padding-top: 6px;\"&gt;&lt;p class=\"disc_avatar\"&gt;&lt;a href=\"/users/alan-ellis/1202\"&gt;&lt;img alt=\"Alan Ellis\" width=\"50\" height=\"50\" border=\"0\" src=\"/images/avatars/794223.JPG\"&gt;&lt;/a&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=\"font-size: 9px; color: #888;\"&gt;Voted 10/10&lt;/span&gt;&lt;/p&gt;&lt;/td&gt;&lt;td colspan=\"2\" style=\"padding-top: 6px;\"&gt;It is not recent, but happened in 2002.  Here is a 2002 article from the Denver Post:  &lt;a href=\"http://www.skyrunner.com/story/coheights.htm\"&gt;Denver Post&lt;/a&gt;&lt;br&gt;&lt;br&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td valign=\"bottom\" style=\"padding-top: 6px;\" colspan=\"2\"&gt;&lt;span style=\"font-size: 9px; color: #888;\"&gt;Posted Sep 9, 2009 9:27 pm&lt;/span&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/table&gt;&lt;table border=\"0\" width=\"745\" class=\"messages\" style=\"margin-left: 30px;\"&gt;&lt;tr&gt;&lt;td rowspan=\"4\" valign=\"top\"&gt;&lt;img src=\"/styles/layout/reply_arrow.gif\"&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td width=\"110\" align=\"left\" valign=\"top\" style=\"padding-top: 6px;\"&gt;&lt;a name=\"338218\"&gt;&lt;/a&gt; &lt;a class=\"dis_user_lnk\" href=\"/users/scott/16365\"&gt;Scott&lt;/a&gt;&lt;/td&gt;&lt;td valign=\"top\" style=\"padding-top: 6px;\"&gt;&lt;b&gt;Re: 14,433 ft (not a resurvey)&lt;/b&gt;&lt;/td&gt;&lt;td align=\"right\" valign=\"top\" nowrap style=\"font-size: 10px;\"&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td valign=\"top\" rowspan=\"2\" align=\"left\" style=\"padding-top: 6px;\"&gt;&lt;p class=\"disc_avatar\"&gt;&lt;a href=\"/users/scott/16365\"&gt;&lt;img alt=\"Scott\" width=\"50\" height=\"50\" border=\"0\" src=\"/images/avatars/142800.jpg\"&gt;&lt;/a&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=\"font-size: 9px; color: #888;\"&gt;Hasn\'t voted&lt;/span&gt;&lt;/p&gt;&lt;/td&gt;&lt;td colspan=\"2\" style=\"padding-top: 6px;\"&gt;If interested, it really wasn\'t a resurvey as mentioned on the mountain page.&lt;br&gt;&lt;br&gt;&lt;br&gt;&lt;br&gt;It was just a recalculation of the old surveyed elevations and Elbert was not resurveyed.&lt;br&gt;&lt;br&gt;&lt;br&gt;&lt;br&gt;It\'s actually a world wide adjustment (some areas went up and some down) and not unique to Mount Elbert or Colorado.   It\'s actually a recalculation of the geoid:&lt;br&gt;&lt;br&gt;&lt;br&gt;&lt;br&gt;&lt;a href=\"http://en.wikipedia.org/wiki/Geoid\"&gt;Geoid definition&lt;/a&gt;&lt;br&gt;&lt;br&gt;&lt;br&gt;&lt;br&gt;Most elevations in the world changed with the new calculation.&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td valign=\"bottom\" style=\"padding-top: 6px;\" colspan=\"2\"&gt;&lt;span style=\"font-size: 9px; color: #888;\"&gt;Posted Sep 9, 2009 9:50 pm&lt;/span&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/table&gt;&lt;table border=\"0\" width=\"745\" class=\"messages\" style=\"margin-left: 30px;\"&gt;&lt;tr&gt;&lt;td rowspan=\"4\" valign=\"top\"&gt;&lt;img src=\"/styles/layout/reply_arrow.gif\"&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td width=\"110\" align=\"left\" valign=\"top\" style=\"padding-top: 6px;\"&gt;&lt;a name=\"338227\"&gt;&lt;/a&gt; &lt;a class=\"dis_user_lnk\" href=\"/users/alan-ellis/1202\"&gt;Alan Ellis&lt;/a&gt;&lt;/td&gt;&lt;td valign=\"top\" style=\"padding-top: 6px;\"&gt;&lt;b&gt;Re: 14,433 ft (not a resurvey)&lt;/b&gt;&lt;/td&gt;&lt;td align=\"right\" valign=\"top\" nowrap style=\"font-size: 10px;\"&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td valign=\"top\" rowspan=\"2\" align=\"left\" style=\"padding-top: 6px;\"&gt;&lt;p class=\"disc_avatar\"&gt;&lt;a href=\"/users/alan-ellis/1202\"&gt;&lt;img alt=\"Alan Ellis\" width=\"50\" height=\"50\" border=\"0\" src=\"/images/avatars/794223.JPG\"&gt;&lt;/a&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=\"font-size: 9px; color: #888;\"&gt;Voted 10/10&lt;/span&gt;&lt;/p&gt;&lt;/td&gt;&lt;td colspan=\"2\" style=\"padding-top: 6px;\"&gt;Scott, I made changes to the text on the main page.  Let me know if it sounds right and is accurate.   Thanks.&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td valign=\"bottom\" style=\"padding-top: 6px;\" colspan=\"2\"&gt;&lt;span style=\"font-size: 9px; color: #888;\"&gt;Posted Sep 9, 2009 10:20 pm&lt;/span&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/table&gt;&lt;table border=\"0\" width=\"745\" class=\"messages\" style=\"margin-left: 30px;\"&gt;&lt;tr&gt;&lt;td rowspan=\"4\" valign=\"top\"&gt;&lt;img src=\"/styles/layout/reply_arrow.gif\"&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td width=\"110\" align=\"left\" valign=\"top\" style=\"padding-top: 6px;\"&gt;&lt;a name=\"338228\"&gt;&lt;/a&gt; &lt;a class=\"dis_user_lnk\" href=\"/users/scott/16365\"&gt;Scott&lt;/a&gt;&lt;/td&gt;&lt;td valign=\"top\" style=\"padding-top: 6px;\"&gt;&lt;b&gt;Re: 14,433 ft (not a resurvey)&lt;/b&gt;&lt;/td&gt;&lt;td align=\"right\" valign=\"top\" nowrap style=\"font-size: 10px;\"&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td valign=\"top\" rowspan=\"2\" align=\"left\" style=\"padding-top: 6px;\"&gt;&lt;p class=\"disc_avatar\"&gt;&lt;a href=\"/users/scott/16365\"&gt;&lt;img alt=\"Scott\" width=\"50\" height=\"50\" border=\"0\" src=\"/images/avatars/142800.jpg\"&gt;&lt;/a&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=\"font-size: 9px; color: #888;\"&gt;Hasn\'t voted&lt;/span&gt;&lt;/p&gt;&lt;/td&gt;&lt;td colspan=\"2\" style=\"padding-top: 6px;\"&gt;Looks good.&lt;br&gt;&lt;br&gt;&lt;br&gt;&lt;br&gt;I guess there will be lots of different elevations floating around out there for a long time.  I haven\'t bothered changing all my SP pages to the new calculated elevations!   &lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td valign=\"bottom\" style=\"padding-top: 6px;\" colspan=\"2\"&gt;&lt;span style=\"font-size: 9px; color: #888;\"&gt;Posted Sep 9, 2009 10:28 pm&lt;/span&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/table&gt;&lt;table border=\"0\" width=\"775\" class=\"messages\"&gt;&lt;tr&gt;&lt;td width=\"110\" align=\"left\" valign=\"top\" style=\"padding-top: 6px;\"&gt;&lt;a name=\"338309\"&gt;&lt;/a&gt; &lt;a class=\"dis_user_lnk\" href=\"/users/vanman798/23249\"&gt;vanman798&lt;/a&gt;&lt;/td&gt;&lt;td valign=\"top\" style=\"padding-top: 6px;\"&gt;&lt;b&gt;Thanks&lt;/b&gt;&lt;/td&gt;&lt;td align=\"right\" valign=\"top\" nowrap style=\"font-size: 10px;\"&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td valign=\"top\" rowspan=\"2\" align=\"left\" style=\"padding-top: 6px;\"&gt;&lt;p class=\"disc_avatar\"&gt;&lt;a href=\"/users/vanman798/23249\"&gt;&lt;img alt=\"vanman798\" width=\"50\" height=\"50\" border=\"0\" src=\"/images/avatars/853975.jpg\"&gt;&lt;/a&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=\"font-size: 9px; color: #888;\"&gt;Hasn\'t voted&lt;/span&gt;&lt;/p&gt;&lt;/td&gt;&lt;td colspan=\"2\" style=\"padding-top: 6px;\"&gt;I appreciate the information.  I think Scott is right when he says \"I guess there will be lots of different elevations floating around out there for a long time.\"&lt;br&gt;&lt;br&gt;&lt;br&gt;&lt;br&gt;This re-calculation makes me wonder about the Borah Peak story - that it rose 7 feet after a 1983 earthquake.  Prehaps the National Geodetic Vertical Datum of 1988 is responsible for the 7 foot rise more so than the earthquake.  Anyways&hellip;&lt;br&gt;&lt;br&gt;&lt;br&gt;&lt;br&gt;Thanks&lt;br&gt;&lt;br&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td valign=\"bottom\" style=\"padding-top: 6px;\" colspan=\"2\"&gt;&lt;span style=\"font-size: 9px; color: #888;\"&gt;Posted Sep 10, 2009 12:19 pm&lt;/span&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/table&gt;&lt;table border=\"0\" width=\"775\" class=\"messages\"&gt;&lt;tr&gt;&lt;td width=\"110\" align=\"left\" valign=\"top\" style=\"padding-top: 6px;\"&gt;&lt;a name=\"426712\"&gt;&lt;/a&gt; &lt;a class=\"dis_user_lnk\" href=\"/users/xdoogiex/56399\"&gt;xDoogiex&lt;/a&gt;&lt;/td&gt;&lt;td valign=\"top\" style=\"padding-top: 6px;\"&gt;&lt;b&gt;camp prices&lt;/b&gt;&lt;/td&gt;&lt;td align=\"right\" valign=\"top\" nowrap style=\"font-size: 10px;\"&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td valign=\"top\" rowspan=\"2\" align=\"left\" style=\"padding-top: 6px;\"&gt;&lt;p class=\"disc_avatar\"&gt;&lt;a href=\"/users/xdoogiex/56399\"&gt;&lt;img alt=\"xDoogiex\" width=\"50\" height=\"50\" border=\"0\" src=\"/images/avatars/648963.JPG\"&gt;&lt;/a&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=\"font-size: 9px; color: #888;\"&gt;Hasn\'t voted&lt;/span&gt;&lt;/p&gt;&lt;/td&gt;&lt;td colspan=\"2\" style=\"padding-top: 6px;\"&gt;Elbert Creek and halfmoon creek campgrounds are now 13$&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td valign=\"bottom\" style=\"padding-top: 6px;\" colspan=\"2\"&gt;&lt;span style=\"font-size: 9px; color: #888;\"&gt;Posted Jul 25, 2011 12:47 pm&lt;/span&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/table&gt;&lt;table border=\"0\" width=\"745\" class=\"messages\" style=\"margin-left: 30px;\"&gt;&lt;tr&gt;&lt;td rowspan=\"4\" valign=\"top\"&gt;&lt;img src=\"/styles/layout/reply_arrow.gif\"&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td width=\"110\" align=\"left\" valign=\"top\" style=\"padding-top: 6px;\"&gt;&lt;a name=\"426729\"&gt;&lt;/a&gt; &lt;a class=\"dis_user_lnk\" href=\"/users/alan-ellis/1202\"&gt;Alan Ellis&lt;/a&gt;&lt;/td&gt;&lt;td valign=\"top\" style=\"padding-top: 6px;\"&gt;&lt;b&gt;Re: camp prices&lt;/b&gt;&lt;/td&gt;&lt;td align=\"right\" valign=\"top\" nowrap style=\"font-size: 10px;\"&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td valign=\"top\" rowspan=\"2\" align=\"left\" style=\"padding-top: 6px;\"&gt;&lt;p class=\"disc_avatar\"&gt;&lt;a href=\"/users/alan-ellis/1202\"&gt;&lt;img alt=\"Alan Ellis\" width=\"50\" height=\"50\" border=\"0\" src=\"/images/avatars/794223.JPG\"&gt;&lt;/a&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=\"font-size: 9px; color: #888;\"&gt;Voted 10/10&lt;/span&gt;&lt;/p&gt;&lt;/td&gt;&lt;td colspan=\"2\" style=\"padding-top: 6px;\"&gt;Thank you.  I\'ll make the change.&lt;br&gt;&lt;br&gt;&lt;br&gt;&lt;br&gt;AE&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td valign=\"bottom\" style=\"padding-top: 6px;\" colspan=\"2\"&gt;&lt;span style=\"font-size: 9px; color: #888;\"&gt;Posted Jul 25, 2011 2:56 pm&lt;/span&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/table&gt;&lt;table border=\"0\" width=\"775\" class=\"messages\"&gt;&lt;tr&gt;&lt;td width=\"110\" align=\"left\" valign=\"top\" style=\"padding-top: 6px;\"&gt;&lt;a name=\"489681\"&gt;&lt;/a&gt; &lt;a class=\"dis_user_lnk\" href=\"/users/humnaman-gmail-com/125589\"&gt;Humnaman@gmail.com&lt;/a&gt;&lt;/td&gt;&lt;td valign=\"top\" style=\"padding-top: 6px;\"&gt;&lt;b&gt;question&lt;/b&gt;&lt;/td&gt;&lt;td align=\"right\" valign=\"top\" nowrap style=\"font-size: 10px;\"&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td valign=\"top\" rowspan=\"2\" align=\"left\" style=\"padding-top: 6px;\"&gt;&lt;p&gt;&lt;span style=\"font-size: 9px; color: #888;\"&gt;Hasn\'t voted&lt;/span&gt;&lt;/p&gt;&lt;/td&gt;&lt;td colspan=\"2\" style=\"padding-top: 6px;\"&gt;hello! sir, i was just doing a project on mount Elbert and wanted to know  What seasons do we have here ,precipitation, storms etc.  and what natural disasters can most probably occur here and some prominent physical features ,i couldn\'t find the  relevant information on the net and thought you could be a great help.&lt;br&gt;&lt;br&gt;thank you!&lt;br&gt;&lt;br&gt;and sorry for being a bother &lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td valign=\"bottom\" style=\"padding-top: 6px;\" colspan=\"2\"&gt;&lt;span style=\"font-size: 9px; color: #888;\"&gt;Posted Mar 21, 2015 3:38 pm&lt;/span&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/table&gt;&lt;br&gt;&lt;table border=\"0\" width=\"775\"&gt;&lt;tr&gt;&lt;td&gt;&lt;table cellpadding=\"2\" cellspacing=\"2\" width=\"100%\"&gt;&lt;tr&gt;&lt;td align=\"left\"&gt;&lt;span class=\"pagertext\"&gt;Viewing: &lt;b&gt;1&lt;/b&gt;-&lt;b&gt;11&lt;/b&gt; of &lt;b&gt;11&lt;/b&gt;&lt;/span&gt;&lt;/td&gt;&lt;td align=\"center\"&gt;&lt;/td&gt;&lt;td align=\"right\"&gt;    &lt;/td&gt;&lt;/tr&gt;&lt;/table&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/table&gt;&lt;br&gt;&lt;/div&gt;&lt;a name=\"chapter_12\"&gt;&lt;/a&gt;&lt;h2&gt;Images&lt;/h2&gt;&lt;div align=\"center\"&gt;&lt;table id=\"my_images\"&gt;&lt;tr&gt;&lt;td&gt;&lt;a href=\"/elbert-s-moonshadow/172790/c-150325\"&gt;&lt;img alt=\"Elbert\\\'s Moonshadow\" src=\"/images/square/172790.jpg\"&gt;&lt;/a&gt;&lt;/td&gt;&lt;td&gt;&lt;a href=\"/fall-winter/233116/c-150325\"&gt;&lt;img alt=\"Fall &amp;amp; Winter\" src=\"/images/square/233116.JPG\"&gt;&lt;/a&gt;&lt;/td&gt;&lt;td&gt;&lt;a href=\"/post-holing-on-mt-elbert/62543/c-150325\"&gt;&lt;img alt=\"Post-holing on Mt Elbert\" src=\"/images/square/62543.jpg\"&gt;&lt;/a&gt;&lt;/td&gt;&lt;td&gt;&lt;a href=\"/a-cornice-near-the-summit-of/80419/c-150325\"&gt;&lt;img alt=\"A cornice near the summit of...\" src=\"/images/square/80419.JPG\"&gt;&lt;/a&gt;&lt;/td&gt;&lt;td&gt;&lt;a href=\"/mt-elbert-joy/471068/c-150325\"&gt;&lt;img alt=\" Mt. Elbert joy\" src=\"/images/square/471068.jpg\"&gt;&lt;/a&gt;&lt;/td&gt;&lt;td&gt;&lt;a href=\"/sunrise-looking-east-from/46190/c-150325\"&gt;&lt;img alt=\"Sunrise looking east from...\" src=\"/images/square/46190.jpg\"&gt;&lt;/a&gt;&lt;/td&gt;&lt;td&gt;&lt;a href=\"/heading-into-the-wind-on-the/34348/c-150325\"&gt;&lt;img alt=\"Heading into the wind on the...\" src=\"/images/square/34348.jpg\"&gt;&lt;/a&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td&gt;&lt;a href=\"/casco-and-french-mtns-from/22825/c-150325\"&gt;&lt;img alt=\"Casco and French Mtns. from...\" src=\"/images/square/22825.jpg\"&gt;&lt;/a&gt;&lt;/td&gt;&lt;td&gt;&lt;a href=\"/sunrise-at-12-000-feet-on-mt/25343/c-150325\"&gt;&lt;img alt=\"Sunrise at 12\\\'000 feet on Mt...\" src=\"/images/square/25343.JPG\"&gt;&lt;/a&gt;&lt;/td&gt;&lt;td&gt;&lt;a href=\"/mount-elbert-from-la-plata/269340/c-150325\"&gt;&lt;img alt=\"Mount Elbert from La Plata\" src=\"/images/square/269340.jpg\"&gt;&lt;/a&gt;&lt;/td&gt;&lt;td&gt;&lt;a href=\"/mount-elbert-s-summit/316859/c-150325\"&gt;&lt;img alt=\"Mount Elbert"s summit.\" src=\"/images/square/316859.JPG\"&gt;&lt;/a&gt;&lt;/td&gt;&lt;td&gt;&lt;a href=\"/mount-elbert-summit-ridge/316849/c-150325\"&gt;&lt;img alt=\"Mount Elbert, Summit Ridge.\" src=\"/images/square/316849.JPG\"&gt;&lt;/a&gt;&lt;/td&gt;&lt;td&gt;&lt;a href=\"/mt-elbert-from-just-west-of/29305/c-150325\"&gt;&lt;img alt=\"Mt. Elbert from just west of...\" src=\"/images/square/29305.jpg\"&gt;&lt;/a&gt;&lt;/td&gt;&lt;td&gt;&lt;a href=\"/making-our-way-to-the-summit/85308/c-150325\"&gt;&lt;img alt=\"Making our way to the summit...\" src=\"/images/square/85308.JPG\"&gt;&lt;/a&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td colspan=\"7\" align=\"center\"&gt;[ &lt;a href=\"/mount-elbert/images/p-150325\"&gt;View Gallery - 465 More Images&lt;/a&gt; ]&lt;/td&gt;&lt;/tr&gt;&lt;/table&gt;&lt;/div&gt;&lt;/article&gt;date:difficulty:'),(16,150362,'/gannett-peak/150362','/wind-river-range/171223','Gannett Peak','Hiking, Mountaineering, Sport Climbing, Scrambling','','','13804','',173679,43.1844,-109.654,'Wyoming, United States, North America','','',171223,'Wind River Range','','','','Summer, Fall','','Mountain-Rock','content: &lt;article&gt;&lt;p&gt;&lt;/p&gt;&lt;a name=\"chapter_1\"&gt;&lt;/a&gt;&lt;h2 id=\"first_h2\"&gt;Overview&lt;/h2&gt;If a poll is given to the general public asking what the highest peak in Wyoming is, chances are that if any guess is taken the most common answer would be the widely famous and picturesque Grand Teton. Although much more famous than the true Wyoming state highpoint, Grand Teton is actually shorter by a mere 34\' elevation. In reality, the highest point in Wyoming is actually Gannett Peak (13804\'), a massive and remote summit located in the Wind River Range nearly 70 miles southeast of Grand Teton.&lt;br&gt;&lt;br&gt;&lt;div align=\"center\" class=\"image\" style=\"width: 506px;\"&gt;&lt;a href=\"/gannett-peak-in-the-morning/115295\"&gt;&lt;img alt=\"Gannett Peak in the morning...\" height=\"348\" width=\"500\" src=\"/images/medium/115295.jpg\"&gt;&lt;/a&gt;&lt;br&gt;&lt;span class=\"caption\"&gt;Gannett Peak (Photo credit: David Kiene)&lt;/span&gt;&lt;/div&gt;&lt;br&gt;Although most famous for being the Wyoming state highpoint, Gannett Peak is also notable for multiple other reasons. In example, the mountain is the highest point of two counties (Fremont, Sublette). The peak is also the highest point of two National Forests (Bridger-Teton, Shoshone) as well as two Wilderness Areas (Bridger, Fitzpatrick). The mountain is also the highest point of three drainage basins (Yellowstone, Bighorn, Green).&lt;br&gt;&lt;br&gt;Gannett Peak is the fifth-highest state highpoint. In addition, with 290 miles of isolation from a higher peak, the mountain is also the most isolated peak in Wyoming and the ninth-most isolated peak in the contiguous USA. With 7076\' of clean prominence, the mountain is the most prominent peak in Wyoming and the 15th-most prominent peak in the contiguous USA.&lt;br&gt;&lt;br&gt;From a mountaineering perspective, Gannett Peak has an assortment of challenges associated with it. The mountain is the 3rd-highest \"Triple Crown\" county highpoint, which is a peak that has a minimum of 5000\' of clean prominence, requires a minimum of 5000\' elevation gain from a passenger vehicle road, and is on the \"Apex\" list of most difficult county highpoints in the contiguous USA. Gannett Peak is the fourth-highest peak on the \"Apex\" list and easily one of the most remote.&lt;br&gt;&lt;br&gt;&lt;div align=\"center\" class=\"image\" style=\"width: 381px;\"&gt;&lt;a href=\"/gannett-peak/355489\"&gt;&lt;img alt=\"Gannett Peak\" height=\"500\" width=\"375\" src=\"/images/medium/355489.JPG\"&gt;&lt;/a&gt;&lt;br&gt;&lt;span class=\"caption\"&gt;Mixture Of Snow, Ice, and Rock&lt;/span&gt;&lt;/div&gt;&lt;br&gt;Any summit attempt of Gannett Peak requires a wide variety of mountaineering skills. Considered as one of the most difficult state highpoints, the only other state highpoints that either rival or exceed its technical difficulty are Denali (Alaska), Mount Rainier (Washington), and Granite Peak (Montana). It is debatable whether or not the latter two are actually more difficult than Gannett Peak, depending on experience, skills, weather, and conditions.&lt;br&gt;&lt;br&gt;&lt;div align=\"center\" class=\"image\" style=\"width: 381px;\"&gt;&lt;a href=\"/climbers-on-gannett-peak/646643\"&gt;&lt;img alt=\"Climbers on Gannett Peak\" height=\"500\" width=\"375\" src=\"/images/medium/646643.jpg\"&gt;&lt;/a&gt;&lt;br&gt;&lt;span class=\"caption\"&gt;Mountaineers on Gannett Peak (Photo credit: \"Holsti97\")&lt;/span&gt;&lt;/div&gt;&lt;a name=\"chapter_2\"&gt;&lt;/a&gt;&lt;h2&gt;Standard Approaches&lt;/h2&gt;Any summit attempt of Gannett Peak should be considered a backcountry trip. Although a few mountaineers have been able to summit the peak within 24 hours, most people require 3-5 days. There are two standard approaches for the peak, each with its advantages and disadvantages. There are also some route variations, too.&lt;br&gt;&lt;br&gt;&lt;a name=\"chapter_3\"&gt;&lt;/a&gt;&lt;h3&gt;STANDARD APPROACH FROM EAST (Dubois, WY):&lt;/h3&gt;&lt;br&gt;&lt;b&gt;&lt;a href=\"http://www.summitpost.org/glacier-trail/530102\"&gt;Glacier Trail&lt;/a&gt;&lt;/b&gt;:&lt;br&gt;-&amp;gt; 50 miles roundtrip with approximately 10750\' cumulative elevation gain.&lt;br&gt;This route is the longest standard approach for any state highpoint in the contiguous USA. As a result, sometimes this route gets overlooked. However, it can be considered short-sighted to only look at its mileage and elevation gain.&lt;br&gt;&lt;br&gt;In reality, this route only has 10 more miles and 1700\' cumulative elevation gain than the other standard approach (from the west), which converts to only five extra miles and 850\' extra gain each way. The biggest advantage to this route is on summit day, due to a closer high camp (i.e. shorter summit day provides higher potential of success) and less strenuous terrain than the western standard approach.&lt;br&gt;&lt;br&gt;&lt;div align=\"center\" class=\"image\" style=\"width: 506px;\"&gt;&lt;a href=\"/gannett-peak-from-glacier-trail/878127\"&gt;&lt;img alt=\"Gannett Peak From Glacier Trail\" height=\"375\" width=\"500\" src=\"/images/medium/878127.jpg\"&gt;&lt;/a&gt;&lt;br&gt;&lt;span class=\"caption\"&gt;Glacier Peak, as seen from Glacier Trail&lt;/span&gt;&lt;/div&gt;&lt;br&gt;------------&lt;br&gt;&lt;br&gt;&lt;a name=\"chapter_4\"&gt;&lt;/a&gt;&lt;h3&gt;STANDARD APPROACH FROM WEST (Pinedale, WY):&lt;/h3&gt;&lt;br&gt;&lt;b&gt;&lt;a href=\"http://www.summitpost.org/pole-creek-trail-seneca-lake-trail-india/155536\"&gt;Pole Creek Trail to Titcomb Basin&lt;/a&gt;&lt;/b&gt;:&lt;br&gt;-&amp;gt; 40 miles roundtrip with approximately 9050\' cumulative elevation gain.&lt;br&gt;This route has long been considered the main standard approach for Gannett Peak, although for the reasons mentioned above the Glacier Trail approach from the east has been gaining popularity during recent years. The western approach is a well-travelled route full of spectacular scenery and views.&lt;br&gt;&lt;br&gt;However, despite an overall shorter route and less cumulative elevation gain, summit day is where it loses its most advantages over the eastern standard approach. The necessity to climb over Bonney Pass (also known as Dinwoody Pass) requires an elevation loss of approximately 1200\' during the approach to Gannett Peak, which of course becomes an extra elevation gain of 1200\' when returning back to camp from the peak. The extra mileage, gains, and losses on summit day can sometimes double-up the time required on that day, when compared with the eastern standard approach.&lt;br&gt;&lt;br&gt;&lt;div align=\"center\" class=\"image\" style=\"width: 506px;\"&gt;&lt;a href=\"/titcomb-basin/207928\"&gt;&lt;img alt=\"Titcomb Basin\" height=\"375\" width=\"500\" src=\"/images/medium/207928.JPG\"&gt;&lt;/a&gt;&lt;br&gt;&lt;span class=\"caption\"&gt;Titcomb Basin, via Western Approach&lt;/span&gt;&lt;/div&gt;&lt;br&gt;------------&lt;br&gt;&lt;br&gt;&lt;a name=\"chapter_5\"&gt;&lt;/a&gt;&lt;h3&gt;ROUTE VARIATIONS:&lt;/h3&gt;&lt;br&gt;&lt;b&gt;&lt;a href=\"http://www.summitpost.org/ink-wells-gooseneck-glacier/159310\"&gt;Ink Wells Trail&lt;/a&gt;&lt;/b&gt;:&lt;br&gt;The advantage of this route is that a large amount of distance and effort can be saved by the time the route intersects with the Glacier Trail. However, the disadvantage of this route is that access is both limited and expensive. This route crosses the Wind River Indian Reservation, which requires special permission, guidance, and transportation to travel through. Reservations are required in advance and sometimes difficult to achieve, and even if a reservation can be made the cost can be substantial (hundreds of dollars, total).&lt;br&gt;&lt;br&gt;&lt;div align=\"center\" class=\"image\" style=\"width: 506px;\"&gt;&lt;a href=\"/on-the-ink-wells-trail/11224\"&gt;&lt;img alt=\"On the Ink Wells trail...\" height=\"375\" width=\"500\" src=\"/images/medium/11224.JPG\"&gt;&lt;/a&gt;&lt;br&gt;&lt;span class=\"caption\"&gt;Gannett Peak, as seen from Ink Wells Trail&lt;/span&gt;&lt;/div&gt;&lt;br&gt;&lt;br&gt;&lt;b&gt;&lt;a href=\"&amp;lt;b\"&gt;&lt;/a&gt;&lt;a href=\"http://www.summitpost.org/tourist-creek/536366\"&gt;Tourist Creek Route&lt;/a&gt;&lt;/b&gt;:&lt;br&gt;The advantage of this route is that it is shorter than the standard approaches and ascends the steep west face of Gannett Peak. The disadvantage of this route is that there is a large amount of routefinding, bushwhacking, and technical rock climbing involved.&lt;br&gt;&lt;br&gt;&lt;b&gt;&lt;a href=\"http://www.summitpost.org/wells-creek/536454\"&gt;Wells Creek Route&lt;/a&gt;&lt;/b&gt;:&lt;br&gt;The advantage of this route is that it is shorter than the standard approaches and ascends the steep west face of Gannett Peak. The disadvantage of this route is that there is a large amount of routefinding, bushwhacking, and technical rock climbing involved.&lt;br&gt;&lt;br&gt;&lt;div align=\"center\" class=\"image\" style=\"width: 506px;\"&gt;&lt;a href=\"/the-seldom-visited-west-faces/78116\"&gt;&lt;img alt=\"The seldom visited west faces...\" height=\"361\" width=\"500\" src=\"/images/original/78116.jpg\"&gt;&lt;/a&gt;&lt;br&gt;&lt;span class=\"caption\"&gt;Gannett Peak, as seen from Wells Creek&lt;/span&gt;&lt;/div&gt;&lt;br&gt;------------&lt;br&gt;&lt;br&gt;Other routes and variations also exist for Gannett Peak, although less popular and possibly with higher technical difficulty. Some people might also opt to have local outfitters help porter mountaineering parties and gear along the standard approaches, although for a substantial fee.&lt;br&gt;&lt;br&gt;Regardless of route, any approach of Gannett Peak must be taken seriously as a true mountain climb. Potential summiters needs to be prepared for long hikes, possible routefinding, inclement weather and terrain conditions, steep snow and ice, glacier travel, and scrambling. Mountaineering skills are required; this peak is not recommended for amateur hikers.&lt;br&gt;&lt;br&gt;&lt;div align=\"center\" class=\"image\" style=\"width: 506px;\"&gt;&lt;a href=\"/climbing-above-the-bergschrund/651501\"&gt;&lt;img alt=\"Climbing Above the Bergschrund\" height=\"375\" width=\"500\" src=\"/images/medium/651501.jpg\"&gt;&lt;/a&gt;&lt;br&gt;&lt;span class=\"caption\"&gt;Climbing the upper Gooseneck Glacier&lt;/span&gt;&lt;/div&gt;&lt;a name=\"chapter_6\"&gt;&lt;/a&gt;&lt;h2&gt;Driving Directions&lt;/h2&gt;&lt;a name=\"chapter_7\"&gt;&lt;/a&gt;&lt;h3&gt;FOR STANDARD APPROACH FROM EAST (Dubois, WY):&lt;/h3&gt;&lt;br&gt;&lt;b&gt;STARTING AT DUBOIS, WY:&lt;/b&gt;&lt;br&gt;1) Drive approximately 4.0 miles south along Highway 26/287 to Whiskey Basin/Trail Lake Road, within a block of crossing a bridge over Jakeys Fork.&lt;br&gt;&lt;br&gt;2) Turn right onto Whiskey Basin/Trail Lake Road, and then take an immediate left. This road turns into Forest Road #411.&lt;br&gt;&lt;br&gt;3) Continue following the road (Forest Road #411) for 9.2 miles until its end at the Trail Lake Ranch parking area. The trailhead for Glacier Trail, Bomber Basin, and Whiskey Mountain originates at this location.&lt;br&gt;&lt;br&gt;&lt;div align=\"center\" class=\"image\" style=\"width: 506px;\"&gt;&lt;a href=\"/trailhead-for-glacier-trail/530147\"&gt;&lt;img alt=\"Trailhead For Glacier Trail\" height=\"375\" width=\"500\" src=\"/images/medium/530147.JPG\"&gt;&lt;/a&gt;&lt;br&gt;&lt;span class=\"caption\"&gt;Trailhead for Glacier Trail&lt;/span&gt;&lt;/div&gt;&lt;br&gt;------------&lt;br&gt;&lt;br&gt;&lt;a name=\"chapter_8\"&gt;&lt;/a&gt;&lt;h3&gt;STANDARD APPROACH FROM WEST (Pinedale, WY):&lt;/h3&gt;&lt;br&gt;&lt;b&gt;STARTING AT PINEDALE, WY:&lt;/b&gt;&lt;br&gt;1) At the east end of town, where Highway 191 begins to bend south, turn left onto Fremont Lake Road.&lt;br&gt;&lt;br&gt;2) After approximately 3.0 miles along Fremont Lake Road, the roadway splits. Take the right fork, onto Forest Road 134 (also known as Skyline Drive).&lt;br&gt;&lt;br&gt;3) Follow Skyline Drive for approximately 11.2 miles until its end at the Trails End Campground and Elkhart Park parking area. The trailhead for Pole Creek Trail originates at this location.&lt;br&gt;-&amp;gt; For reference purposes, the parking area is located only a short distance beyond the Elkhart Park Ranger Station.&lt;br&gt;&lt;br&gt;&lt;div align=\"center\" class=\"image\" style=\"width: 506px;\"&gt;&lt;a href=\"/the-wind-river-range-as-seen/595\"&gt;&lt;img alt=\"The Wind River range as seen...\" height=\"338\" width=\"500\" src=\"/images/medium/595.JPG\"&gt;&lt;/a&gt;&lt;br&gt;&lt;span class=\"caption\"&gt;Trailhead for Pole Creek Trail&lt;/span&gt;&lt;/div&gt;&lt;a name=\"chapter_9\"&gt;&lt;/a&gt;&lt;h2&gt;Red Tape&lt;/h2&gt;No special permits are required for the standard approaches of Gannett Peak. However, standard wilderness rules and regulations apply.&lt;br&gt;&lt;br&gt;If a trail register is present at a trailhead, sign-in at the beginning of the trek and sign-out upon return.&lt;br&gt;&lt;br&gt;Large groups (8+ people) visiting the region might require a special group permit prior to any trip.&lt;br&gt;&lt;br&gt;&lt;b&gt;Contact a local ranger district for current rules, regulations, and route conditions prior to any trip to this area.&lt;/b&gt;&lt;a name=\"chapter_10\"&gt;&lt;/a&gt;&lt;h2&gt;When To Climb&lt;/h2&gt;The optimum climbing season for Gannett Peak is June through September. Most climbers visit the peak during July and August. Far less snow is encountered along the standard approaches during August, but many climbing parties prefer visiting during July when the Gooseneck Glacier bergschrund (and other crevasses) are not yet fully opened (if at all). Snowpack can actually make a summit attempt less strenuous than travelling on ice and/or rock for long distances.&lt;br&gt;&lt;br&gt;&lt;div align=\"center\" class=\"image\" style=\"width: 381px;\"&gt;&lt;a href=\"/gannett-peak-trip-bergschrund/530000\"&gt;&lt;img alt=\"Gannett Peak Trip - Bergschrund\" height=\"500\" width=\"375\" src=\"/images/medium/530000.JPG\"&gt;&lt;/a&gt;&lt;br&gt;&lt;span class=\"caption\"&gt;Gooseneck Glacier bergschrund starting to open...&lt;/span&gt;&lt;/div&gt;&lt;br&gt;A few people attempt and climb Gannett Peak off-season, but it is not recommended due to inclement off-season weather, snow blizzards, avalanches, and possibly snowed-in roads leading to trailheads.&lt;a name=\"chapter_11\"&gt;&lt;/a&gt;&lt;h2&gt;Camping&lt;/h2&gt;Backcountry camping is permitted within both Bridger Wilderness and Fitzpatrick Wilderness.&lt;br&gt;&lt;br&gt;&lt;div align=\"center\" class=\"image\" style=\"width: 506px;\"&gt;&lt;a href=\"/gannett-peak-trip-high-camp/530020\"&gt;&lt;img alt=\"Gannett Peak Trip - High Camp\" height=\"375\" width=\"500\" src=\"/images/medium/530020.JPG\"&gt;&lt;/a&gt;&lt;br&gt;&lt;span class=\"caption\"&gt;High Camp Along Eastern Approach&lt;/span&gt;&lt;/div&gt;&lt;a name=\"chapter_12\"&gt;&lt;/a&gt;&lt;h2&gt;Mountain Conditions&lt;/h2&gt;&lt;b&gt;For current conditions for the eastern standard approach to Gannett Peak:&lt;/b&gt;&lt;br&gt;Wind River Ranger District, Shoshone National Forest&lt;br&gt;ADDRESS: 1403 West Ramshorn, Dubois, WY 82513&lt;br&gt;TELEPHONE: 307-455-2466&lt;br&gt;&lt;br&gt;&lt;br&gt;&lt;b&gt;For current conditions for the western standard approach to Gannett Peak:&lt;/b&gt;&lt;br&gt;Pinedale Ranger District, Bridger-Teton National Forest&lt;br&gt;ADDRESS: 29 East Fremont Lake Road, Pinedale, WY 92941&lt;br&gt;TELEPHONE: 307-367-4326&lt;a name=\"chapter_13\"&gt;&lt;/a&gt;&lt;h2&gt;Historical Facts&lt;/h2&gt;Gannett Peak was officially named during 1906, in honor of &lt;a href=\"http://www.amerisurv.com/content/view/4519/153/\"&gt;Henry Gannett&lt;/a&gt;. Gannett had been a Chief Geographer for the United States Geological Society as well as one of the founding members of the National Geographic Society. During his lifetime, Gannett was well-known for his topographical work and in doing so became regarded as the \"Father of the Quadrangle.\" During 1890, he was responsible (in partnership with Thomas Corwin Mendenhall) for helping establish the United States Board on Geographic Names, which started creating official names for locations within the USA.&lt;br&gt;&lt;br&gt;The first successful summit of Gannett Peak was by Arthur Tate and Floyd Stahlnaker during 1922.&lt;br&gt;&lt;br&gt;The Gannett Glacier, located on the north side of Gannett Peak, is the largest glacier in the Rocky Mountains south of the Canadian border.&lt;br&gt;&lt;br&gt;&lt;div align=\"center\" class=\"image\" style=\"width: 506px;\"&gt;&lt;a href=\"/gannett-glacier/269432\"&gt;&lt;img alt=\"Gannett Glacier\" height=\"203\" width=\"500\" src=\"/images/medium/269432.jpg\"&gt;&lt;/a&gt;&lt;br&gt;&lt;span class=\"caption\"&gt;Gannett Glacier&lt;/span&gt;&lt;/div&gt;&lt;a name=\"chapter_14\"&gt;&lt;/a&gt;&lt;h2&gt;External Links&lt;/h2&gt;An excellent resource for attempting Gannett Peak, as well as any other state highpoints, is \"&lt;a href=\"http://www.amazon.com/dp/1463692013/ref=asc_df_14636920132806093?smid=ATVPDKIKX0DER&amp;amp;tag=pg-1583-86-20&amp;amp;linkCode=asn&amp;amp;creative=395097&amp;amp;creativeASIN=1463692013\"&gt;Highpoint Adventures: The Complete Guide to the 50 State Highpoints&lt;/a&gt;\" by Charlie and Diane Winger. In the book, each standard approach for Gannett Peak has detailed descriptions, general route maps, and diagrams.                                         &lt;a name=\"chapter_15\"&gt;&lt;/a&gt;&lt;h2&gt;Images&lt;/h2&gt;&lt;div align=\"center\"&gt;&lt;table id=\"my_images\"&gt;&lt;tr&gt;&lt;td&gt;&lt;a href=\"/gannett-peak-in-the-morning/115295/c-150362\"&gt;&lt;img alt=\"Gannett Peak in the morning...\" src=\"/images/square/115295.jpg\"&gt;&lt;/a&gt;&lt;/td&gt;&lt;td&gt;&lt;a href=\"/mt-woodrow-wilson-from/115245/c-150362\"&gt;&lt;img alt=\"Mt. Woodrow Wilson from...\" src=\"/images/square/115245.JPG\"&gt;&lt;/a&gt;&lt;/td&gt;&lt;td&gt;&lt;a href=\"/island-lake-and-titcomb-basin/600/c-150362\"&gt;&lt;img alt=\"Island Lake and Titcomb Basin\" src=\"/images/square/600.JPG\"&gt;&lt;/a&gt;&lt;/td&gt;&lt;td&gt;&lt;a href=\"/wind-river-mountains/356594/c-150362\"&gt;&lt;img alt=\"Wind River Mountains\" src=\"/images/square/356594.JPG\"&gt;&lt;/a&gt;&lt;/td&gt;&lt;td&gt;&lt;a href=\"/sunrise-on-island-lake/647908/c-150362\"&gt;&lt;img alt=\"Sunrise on island lake\" src=\"/images/square/647908.jpg\"&gt;&lt;/a&gt;&lt;/td&gt;&lt;td&gt;&lt;a href=\"/gannett-peak/355489/c-150362\"&gt;&lt;img alt=\"Gannett Peak\" src=\"/images/square/355489.JPG\"&gt;&lt;/a&gt;&lt;/td&gt;&lt;td&gt;&lt;a href=\"/fremont-peak-and-elephant-head/272040/c-150362\"&gt;&lt;img alt=\"Fremont peak and Elephant head\" src=\"/images/square/272040.jpg\"&gt;&lt;/a&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td&gt;&lt;a href=\"/looking-se-from-the-summit-of/592/c-150362\"&gt;&lt;img alt=\"Looking SE from the summit of...\" src=\"/images/square/592.jpg\"&gt;&lt;/a&gt;&lt;/td&gt;&lt;td&gt;&lt;a href=\"/gannett-peak-wy/211911/c-150362\"&gt;&lt;img alt=\"Gannett Peak - WY\" src=\"/images/square/211911.jpg\"&gt;&lt;/a&gt;&lt;/td&gt;&lt;td&gt;&lt;a href=\"/a-pond-in-the-titcomb-basin/599/c-150362\"&gt;&lt;img alt=\"A pond in the Titcomb Basin.\" src=\"/images/square/599.JPG\"&gt;&lt;/a&gt;&lt;/td&gt;&lt;td&gt;&lt;a href=\"/gannett-peak/205165/c-150362\"&gt;&lt;img alt=\"Gannett Peak\" src=\"/images/square/205165.jpg\"&gt;&lt;/a&gt;&lt;/td&gt;&lt;td&gt;&lt;a href=\"/looking-toward-titcomb-basin/6502/c-150362\"&gt;&lt;img alt=\"Looking toward Titcomb Basin...\" src=\"/images/square/6502.jpg\"&gt;&lt;/a&gt;&lt;/td&gt;&lt;td&gt;&lt;a href=\"/titcomb-basin/121162/c-150362\"&gt;&lt;img alt=\"Titcomb Basin\" src=\"/images/square/121162.jpg\"&gt;&lt;/a&gt;&lt;/td&gt;&lt;td&gt;&lt;a href=\"/smoky-evening-reflection/435403/c-150362\"&gt;&lt;img alt=\"Smoky evening reflection\" src=\"/images/square/435403.jpg\"&gt;&lt;/a&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td colspan=\"7\" align=\"center\"&gt;[ &lt;a href=\"/gannett-peak/images/p-150362\"&gt;View Gallery - 304 More Images&lt;/a&gt; ]&lt;/td&gt;&lt;/tr&gt;&lt;/table&gt;&lt;/div&gt;&lt;/article&gt;date:difficulty:'),(17,150376,'/kings-peak/150376','/high-uintas-wilderness/170976','Kings Peak','Hiking, Mountaineering, Skiing','','','13528','',196115,40.7765,-110.372,'Utah, United States, North America','','',170976,'High Uintas Wilderness','','','','Spring, Summer, Fall, Winter','','Mountain-Rock','content: &lt;article&gt;&lt;p&gt;&lt;/p&gt;&lt;a name=\"chapter_1\"&gt;&lt;/a&gt;&lt;h2 id=\"first_h2\"&gt;Under New Ownership&lt;/h2&gt;&lt;p&gt;This page was transferred to me by Bob Bolton. I am very thankful to be able to adopt and continue to provide lots of information about this wonderful peak here on SummitPost. Some of the text below was written by Bob.&lt;/p&gt;&lt;div class=\"image\" style=\"width: 406px;\" align=\"center\"&gt;&lt;a href=\"/view_object.php?object_id=303023\"&gt;&lt;img style=\"width: 400px; height: 289.59px;\" alt=\"Kings Peak and South Kings Peak\" src=\"/images/medium/303023.jpg\" unselectable=\"on\"&gt;&lt;/a&gt;&lt;br&gt;&lt;span class=\"caption\"&gt;Kings Peak and South Kings Peak&lt;/span&gt;&lt;/div&gt;&lt;a name=\"chapter_2\"&gt;&lt;/a&gt;&lt;h2&gt;Overview&lt;/h2&gt;&lt;p&gt;Kings Peak is the highpoint of Utah, and of &lt;a href=\"http://cohp.org/ut/utah.html\" target=\"_blank\"&gt;Duchesne County&lt;/a&gt;. It is the seventh highest of the U.S. State Highpoints and located approximately 42 miles north of Duchesne in the High Uintas Wilderness Area of the Ashley and Wasatch National Forests.  The mountain was named after Clarence King, who was the first director of the US Geological Survey.  The standard hiking route is a 28.8 mile round trip trek, so the peak is usually climbed as part of a backpacking trip.&lt;/p&gt;&lt;p&gt;The present-day Kings Peak wasn\'t known to be the highest point in Utah before the mid 1960s. The USGS hadn\'t determined whether the current Kings Peak or South Kings Peak was the highest of the two summits prior to satellite measurements taken in 1966. Prior to this discovery, the USGS had marked South Kings Peak as the highest peak in Utah. Any hikers going to Utah\'s highest peak prior to 1966 would have scaled South Kings Peak instead of the now official Kings Peak. &lt;/p&gt;&lt;p&gt;Kings Peak\'s &lt;a href=\"http://www.cohp.org/prominence/index.htm\" target=\"_blank\"&gt;topographic prominence&lt;/a&gt; is 6,348 feet and ranks 19th in the contiguous 48 states. It is one of 57 so-called \"Ultra\" prominence peaks, those with more than 5,000 feet of prominence. For the complete list of Ultras, see &lt;a href=\"http://www.peaklist.org/USlists/USP5000.html\" target=\"_blank\"&gt;this page&lt;/a&gt;.&lt;/p&gt;&lt;p&gt;&lt;/p&gt;&lt;div align=\"center\"&gt;&lt;table class=\"redactor_table\" id=\"table67162\"&gt;&lt;tbody&gt;&lt;tr&gt;&lt;td&gt;&lt;div class=\"image\" style=\"width: 356px;\" align=\"center\"&gt;&lt;a href=\"/view_object.php?object_id=417319\"&gt;&lt;img style=\"width: 350px; height: 233.8px;\" alt=\"Kings Peak Tele\" src=\"/images/medium/417319.JPG\" unselectable=\"on\"&gt;&lt;/a&gt;&lt;br&gt;&lt;span class=\"caption\"&gt;Kings Peak&lt;/span&gt;&lt;/div&gt;&lt;/td&gt;&lt;td&gt;&lt;div class=\"image\" style=\"width: 316.32px;\" align=\"center\"&gt;&lt;a href=\"/view_object.php?object_id=318620\"&gt;&lt;img style=\"width: 310.32px; height: 234px;\" alt=\"Kings at sunset\" src=\"/images/medium/318620.JPG\" unselectable=\"on\"&gt;&lt;/a&gt;&lt;br&gt;&lt;span class=\"caption\"&gt;Kings Peak at sunset&lt;/span&gt;&lt;/div&gt;&lt;/td&gt;&lt;td&gt;&lt;div class=\"image\" style=\"width: 318.73px;\" align=\"center\"&gt;&lt;a href=\"/view_object.php?object_id=530086\"&gt;&lt;img style=\"width: 312.73px; height: 235px;\" alt=\"South Kings and Kings\" src=\"/images/medium/530086.JPG\" unselectable=\"on\"&gt;&lt;/a&gt;&lt;br&gt;&lt;span class=\"caption\"&gt;Kings Peak&lt;/span&gt;&lt;/div&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/tbody&gt;&lt;/table&gt;&lt;/div&gt;&lt;p&gt;&lt;/p&gt;&lt;a name=\"chapter_3\"&gt;&lt;/a&gt;&lt;h2&gt;Trailheads&lt;/h2&gt;&lt;b&gt;Henry\'s Fork Trailhead&lt;/b&gt; &lt;div&gt;&lt;p&gt;This trailhead is on the north side of the range. Take exit 39 off Interstate 80 on Wyoming and turn south on State Route 414. Go 6 miles to Mountain View, WY where route 414 turns west through Mountain View and becomes Route 410 as the road turns south at the west end of town.  Drive Route 410 for 6.7 miles toward Robertson, WY to the point where it turns west.  From this point, drive Uinta County Road 283 (also named Forest Road 072) south toward Grahams Reservoir and Bridger Lake for 12.1 miles to Forest Road 017.  Turn left on Forest Road 017 and continue south 6.8 miles to Forest Road 077.  Drive south on Forest Road 077 for 2.8 miles, and turn right on the road to Henry\'s Fork Campground and the Henry\'s Fork Trailhead. This is the trailhead most people use for a climb of Kings Peak. The hike is beautiful but expect crowds in July and August.&lt;/p&gt;&lt;p&gt;&lt;b&gt;Yellowstone Creek (Swift Creek) Trailhead&lt;/b&gt;&lt;/p&gt;&lt;p&gt;This trailhead is on the south side of the range and although not quite as scenic as the hike from Henry\'s Fork, it offers a very mellow hike up the long Yellowstone Creek Trail. If you\'re coming from the east in Roosevelt, Utah, continue south through town on US Highway 40 / 191. Don\'t head onto UT 121. Follow Highway 40 as it turns back to the southwest and after 5.6 miles, turn right on UT 87 (AKA Ioka Lane) and follow this paved highway for 16.5 miles to the town of Altamont. A quarter-mile west of this town, turn right (north) onto 16,000 West and continue another 4 miles. Turn left onto 8,000 North, then right again on 17,000 West which then curves left and turns into 9,000 North and then into Yellow Stone Road. Cross the Yellow Stone River and continue 5.5 miles on the now gravel road to the forest boundary where the road turns into FR 119. Continue down the hill and pass the Yellowstone Group Campground and Bridge Campground, crossing the river again back to its east side. 4 miles past the Bridge Campground, reach the Swift Creek Campground at the end of the road and park at the trailhead marked by a large sign.&lt;/p&gt;&lt;p&gt;Or, if you are coming from the west in Duchene, turn north onto State Highway 87. Follow State Highway 87 until it joins with State Highway 134 (this is where state Highway 87 heads due east instead of continuing north) between milepost 15 and 16. Follow State Highway 134 (21,000 West) north to the little town of Mountain Home. Where State Highway 134 begins to head due east at Mountain Home, Continue north on 21,000 West and turn right on 20,780 West at the T junction 0.7 miles north of town. Continue on 20,780 West for 4 miles, heading down the hill and turning right onto a short road which crosses the Lake Fork River. After crossing the bridge, turn left (north) on Yellowstone River Road and follow the road to the trailhead at the Swift Creek Campground as described above.&lt;/p&gt;&lt;p&gt;&lt;b&gt;Uinta River Trailhead&lt;/b&gt;&lt;/p&gt;&lt;p&gt;This trailhead is also on the south side of the range and provides a longer, more scenic approach than the Yellowstone Creek route. From the intersection of Highway 121 and Highway 40 in the town of Roosevelt in the Uinta Basin, drive west on 200 North (Highway 121). Stay on Highway 121 as it quickly bends to the north. Most junctions are marked with signs, but they are very hard to read in the dark. At mile 9.9, just after the actual mile marker 10 at a stop sign, at the hamlet of Neola go straight towards Uintah Canyon. At mile 16.7 continue straight. At mile 18, turn right to Uintah Canyon. At mile 21.9 turn right. At mile 22.2 turn left just after the bridge. Park at the trailhead at mile 25.6.&lt;/p&gt;&lt;a name=\"chapter_4\"&gt;&lt;/a&gt;&lt;h2&gt;Approaches&lt;/h2&gt;&lt;p&gt;&lt;b&gt;&lt;a href=\"http://www.summitpost.org/swift-creek-yellowstone-creek/160822\"&gt;Swift/Yellowstone Creek Trail&lt;/a&gt;&lt;/b&gt;&lt;br&gt;&lt;/p&gt;&lt;p&gt;This approach avoids the crowds of the Henry\'s Fork approach. From the trailhead (see above) hike about a half mile up the Yellowstone Creek Trail to a junction where you have two choices. You can either continue straight up the Yellowstone Creek Trail for 17 uneventful miles to Anderson Pass. This avoids the ups and downs and makes for easy route-finding but the hike through the Yellowstone Creek valley is in the forest nearly the whole way. Only the last couple miles are in the nice alpine meadows above treeline. Yellowstone Creek does form a narrow, deep gorge that you\'ll pass by through the trees which may make the boring hike a little more interesting. &lt;/p&gt;&lt;p&gt;The other option is to turn right shortly after the trailhead and hike the Swift Creek trail to Milk Lake. This trail is much more scenic as it hikes on a ridge crest for a while and passes by a few large beaver ponds before reaching Deer Lake, which is at 10,200 feet and 6 miles from the trailhead. After Deer Lake, continue on the trail and after 0.75 miles, you will come to a junction. Turn left and follow the trail past Farmers Lake and over Bluebell Pass, and then descend to Milk Lake. From Milk Lake, follow the base of the ridge to the east all the way to Anderson Pass. The route passes through many spectacular and beautiful alpine meadows full of wildflowers. There is no trail after Milk Lake, but the cross-country route is easy to find since there are open views and travel is not difficult.&lt;/p&gt;&lt;p&gt;&lt;/p&gt;&lt;div class=\"image\" style=\"width: 406px;\" align=\"center\"&gt;&lt;a href=\"/view_object.php?object_id=541327\"&gt;&lt;img src=\"/images/medium/541327.JPG\" alt=\"Deer Lake\" style=\"width: 400px; height: 300px;\"&gt;&lt;/a&gt;&lt;br&gt;&lt;span class=\"caption\"&gt;Deer Lake&lt;/span&gt;&lt;/div&gt;&lt;p&gt;&lt;/p&gt;&lt;p&gt;&lt;b&gt;&lt;a href=\"http://www.summitpost.org/henry-s-fork-via-anderson-pass/159182\"&gt;Henry\'s Fork&lt;/a&gt;&lt;/b&gt;&lt;/p&gt;&lt;p&gt;This is the most popular approach for Kings Peak coming from the north side of the range. The trail heads in a Southerly direction on a gradual rise. After about 3 miles and 430 feet of elevation gain, you will intersect with the Alligator Lake trail that heads off to the right. Continue straight for another 2.4 miles where you will come across a sign for the Elkhorn stream crossing to the left. At this point turn left (east) and cross via a log bridge. After crossing continue heading in a southerly direction for another couple miles where you reach Dollar Lake. This is a good spot to camp or you can continue on and setup camp closer to Gunsight Pass. Continue on the main trail to Gunsight Pass and hike the ridge over Gunsight Pass Peak and continue down to Anderson Pass where the North Ridge Route of Kings Peak takes off.&lt;/p&gt;&lt;p&gt;&lt;/p&gt;&lt;div class=\"image\" style=\"width: 406px;\" align=\"center\"&gt;&lt;a href=\"/view_object.php?object_id=541326\"&gt;&lt;img src=\"/images/medium/541326.JPG\" alt=\"Henry\'s Fork Lake\" style=\"width: 400px; height: 300px;\"&gt;&lt;/a&gt;&lt;br&gt;&lt;span class=\"caption\"&gt;Henry\'s Fork Lake&lt;/span&gt;&lt;/div&gt;&lt;b&gt;&lt;a href=\"http://www.summitpost.org/uinta-river/160820\"&gt;Uinta River Trail&lt;/a&gt;&lt;/b&gt;&lt;p&gt;&lt;/p&gt;&lt;p&gt;This approach starts on the south side of the range but approaches Kings Peak from the east. This route is very long tough and rarely done. Allow 2-4 days for this 50 mile round-trip hike. From the trailhead at 7800 feet elevation, follow the trail north along the Uinta River. After about 3.6 miles, you will reach a junction where you turn left and cross Sheep Bridge and onto the Chain Lakes Trail. Follow this steep trail to the Chain Lakes Basin. Lower Chain Lake is about 9.2 miles from the trailhead and at 10,580 feet elevation. The 4th Chain Lake is 1.8 miles beyond at 10,900 feet elevation. Any of these lakes make a fine campsite. From the upper Chain Lake, take the trail over Roberts Pass to the Lake Atwood Basin, and then over Trail Rider Pass to Painter Basin. You could follow the trail all the way to Anderson Pass, but the shortest way is to climb the east face of Kings directly and moot the north ridge. There is a lot of boulder hopping but doesn\'t exceed Class 2.&lt;/p&gt;&lt;p&gt;&lt;/p&gt;&lt;div class=\"image\" style=\"width: 406px;\" align=\"center\"&gt;&lt;a href=\"/view_object.php?object_id=59933\"&gt;&lt;img src=\"/images/medium/59933.jpg\" alt=\"Emmons (right center), July...\" style=\"width: 400px; height: 264px;\"&gt;&lt;/a&gt;&lt;br&gt;&lt;span class=\"caption\"&gt;Painter Basin&lt;/span&gt;&lt;/div&gt;&lt;br&gt;&lt;p&gt;&lt;/p&gt;&lt;a name=\"chapter_5\"&gt;&lt;/a&gt;&lt;h2&gt;North Ridge Standard Route - Class 1+&lt;/h2&gt;&lt;p&gt;This is the standard route on Kings Peak and regardless on which approach you take (with the possible exception of the Uinta River Trail), the route begins at Anderson Pass. From the pass, hike south on the ridge to the summit. It\'s actually pretty simple and only takes about an hour from Anderson Pass and there is a worn boot path most of the way.&lt;/p&gt;&lt;p&gt;&lt;/p&gt;&lt;div class=\"image\" style=\"width:500px;\" align=\"center\"&gt;&lt;a href=\"/view_object.php?object_id=634152\"&gt;&lt;img src=\"/images/medium/634152.JPG\" alt=\"Kings Peak\"&gt;&lt;/a&gt;&lt;br&gt;&lt;span class=\"caption\"&gt;Kings Peak\'s North Ridge on the right skyline&lt;/span&gt;&lt;/div&gt;&lt;br&gt;&lt;p&gt;&lt;/p&gt;&lt;a name=\"chapter_6\"&gt;&lt;/a&gt;&lt;h2&gt;Kings-Emmons Ridge Run&lt;/h2&gt;&lt;p&gt;The &lt;a href=\"http://www.summitpost.org/kings-emmons-ridge/160824\"&gt;Kings to Emmons&lt;/a&gt; ridge-run is arguably the highest sustained ridge-crest in the entire Rocky Mountain chain outside of Colorado. It provides a fantastic peak-bagging adventure and endurance test as you will pass over seven 13ers if you complete the ridge. For climbers wanting to try this long hike, the best place to start is the Yellowstone/Swift Creek trailhead and make a huge loop out of the ridge. When I did it, I hiked all the way up to Anderson Pass on the Yellowstone Creek Trail all night long, then headed south on the ridge getting the first five of the peaks before tiring out and bailing down into the upper Timothy Lakes basin and hiking out the Swift Creek Trail. You can get all 7 peaks before descending Swift Creek but make sure there is no chance for thunderstorms to develop as you will be way above treeline for many hours. Other nearby 13ers like \"Henry\'s Fork Peak\" and Gunsight Peak can also be tagged. The loop can also be done in the opposite direction. The peaks on the ridge from north to south are as follows:&lt;br&gt;&lt;/p&gt;&lt;p&gt;&lt;/p&gt;&lt;div align=\"center\"&gt;&lt;table class=\"redactor_table\" id=\"table57200\" style=\"width: 600px; background-color: rgb(198, 217, 240);\"&gt;&lt;tbody&gt;&lt;tr&gt;&lt;td&gt;Name&lt;/td&gt;&lt;td&gt;Elevation (ft)&lt;/td&gt;&lt;td&gt;Prominence (ft)&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td&gt;Kings Peak&lt;/td&gt;&lt;td&gt;13,528&lt;/td&gt;&lt;td&gt;6,358&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td&gt;South Kings Peak&lt;/td&gt;&lt;td&gt;13,512&lt;/td&gt;&lt;td&gt;372&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td&gt;\"Trail Rider Peak\"&lt;/td&gt;&lt;td&gt;13,247&lt;/td&gt;&lt;td&gt;362&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td&gt;\"Painter Peak\"&lt;/td&gt;&lt;td&gt;13,387&lt;/td&gt;&lt;td&gt;329&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td&gt;\"Roberts Peak\"&lt;/td&gt;&lt;td&gt;13,287&lt;/td&gt;&lt;td&gt;543&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td&gt;\"North Emmons\"&lt;/td&gt;&lt;td&gt;13,068&lt;/td&gt;&lt;td&gt;481&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td&gt;Mount Emmons&lt;/td&gt;&lt;td&gt;13,440&lt;/td&gt;&lt;td&gt;930&lt;/td&gt;&lt;/tr&gt;&lt;/tbody&gt;&lt;/table&gt;&lt;/div&gt;&lt;p&gt;&lt;/p&gt;&lt;div align=\"center\"&gt;&lt;table class=\"redactor_table\" id=\"table1636\"&gt;&lt;tbody&gt;&lt;tr&gt;&lt;td&gt;&lt;div class=\"image\" style=\"width: 456px;\" align=\"center\"&gt;&lt;a href=\"/view_object.php?object_id=129727\"&gt;&lt;img src=\"/images/medium/129727.jpg\" alt=\"Kings-Emmons Ridge as viewed...\" style=\"width: 450px; height: 212.4px;\"&gt;&lt;/a&gt;&lt;br&gt;&lt;span class=\"caption\"&gt;Kings-Emmons Ridge...North (Mount Emmons) is to the left&lt;/span&gt;&lt;/div&gt;&lt;br&gt;&lt;div class=\"image\" style=\"width: 456px;\" align=\"center\"&gt;&lt;a href=\"/view_object.php?object_id=810029\"&gt;&lt;img src=\"/images/medium/810029.JPG\" alt=\"South Kings Peak\" style=\"width: 450px; height: 337.5px;\"&gt;&lt;/a&gt;&lt;br&gt;&lt;span class=\"caption\"&gt;Looking south to South Kings Peak&lt;/span&gt;&lt;/div&gt;&lt;/td&gt;&lt;td&gt;&lt;div class=\"image\" style=\"width: 497.848px;\" align=\"center\"&gt;&lt;a href=\"/view_object.php?object_id=884717\"&gt;&lt;img style=\"width: 491.848px; height: 580px;\" src=\"/images/medium/884717.jpg\" alt=\"Kings-Emmons Ridge Map\"&gt;&lt;/a&gt;&lt;br&gt;&lt;span class=\"caption\"&gt;Map showing the Kings-Emmons ridge-run loop (first 5 peaks). North Emmons and Emmons are just off to the east&lt;/span&gt;&lt;/div&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/tbody&gt;&lt;/table&gt;&lt;/div&gt;&lt;p&gt;&lt;/p&gt;&lt;a name=\"chapter_7\"&gt;&lt;/a&gt;&lt;h2&gt;Map&lt;/h2&gt;&lt;p&gt;&lt;/p&gt;&lt;div class=\"image\" style=\"width: 906px;\" align=\"center\"&gt;&lt;a href=\"/view_object.php?object_id=884720\"&gt;&lt;img style=\"width: 900px; height: 600.293px;\" src=\"/images/large/884720.jpg\" alt=\"Kings Peak North Map\"&gt;&lt;/a&gt;&lt;br&gt;&lt;/div&gt;&lt;p&gt;&lt;/p&gt;&lt;p style=\"text-align: center;\"&gt;ORANGE: Yellowstone Creek Approach - Class 1&lt;/p&gt;&lt;p style=\"text-align: center;\"&gt;BLUE: Henry\'s Fork Approach via Gunsight Pass - Class 1&lt;/p&gt;&lt;p style=\"text-align: center;\"&gt;GREEN: Uinta River Approach and East Slopes Route - Class 2&lt;/p&gt;&lt;p style=\"text-align: center;\"&gt;RED: North Ridge Standard Route - Class 1+&lt;br&gt;&lt;/p&gt;&lt;a name=\"chapter_8\"&gt;&lt;/a&gt;&lt;h2&gt;When To Climb&lt;/h2&gt;&lt;p&gt;Generally the best conditions for climbing Kings Peak occur from June through September, depending on snow and weather conditions. It is certainly climbable any time of year however mid-winter ascents will require skis, lots of warm clothing, a liberal dose of spare time and a love for sub-zero temperatures and hurricane force winds!&lt;/p&gt;&lt;p&gt;&lt;/p&gt;&lt;div align=\"center\"&gt;&lt;table class=\"redactor_table\" id=\"table54858\"&gt;&lt;tbody&gt;&lt;tr&gt;&lt;td&gt;&lt;div class=\"image\" style=\"width: 306px;\" align=\"center\"&gt;&lt;a href=\"/view_object.php?object_id=183715\"&gt;&lt;img src=\"/images/medium/183715.jpg\" alt=\"Strong Winds\" style=\"width: 300px; height: 225px;\"&gt;&lt;/a&gt;&lt;br&gt;&lt;span class=\"caption\"&gt;Kings in Winter&lt;/span&gt;&lt;/div&gt;&lt;/td&gt;&lt;td&gt;&lt;div class=\"image\" style=\"width: 306px;\" align=\"center\"&gt;&lt;a href=\"/view_object.php?object_id=252143\"&gt;&lt;img src=\"/images/medium/252143.JPG\" alt=\"Kings Peak...\" style=\"width: 300px; height: 225px;\"&gt;&lt;/a&gt;&lt;br&gt;&lt;span class=\"caption\"&gt;Kings in Summer&lt;/span&gt;&lt;/div&gt;&lt;/td&gt;&lt;td&gt;&lt;div class=\"image\" style=\"width: 306px;\" align=\"center\"&gt;&lt;a href=\"/view_object.php?object_id=76408\"&gt;&lt;img src=\"/images/medium/76408.JPG\" alt=\"Trail from Dollar Lake...\" style=\"width: 300px; height: 225px;\"&gt;&lt;/a&gt;&lt;br&gt;&lt;span class=\"caption\"&gt;Kings in Fall&lt;/span&gt;&lt;/div&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/tbody&gt;&lt;/table&gt;&lt;/div&gt;&lt;br&gt;&lt;p&gt;&lt;/p&gt;&lt;a name=\"chapter_9\"&gt;&lt;/a&gt;&lt;h2&gt;Camping/Red Tape&lt;/h2&gt;&lt;p&gt;Camping can be found at any of the trailheads without charge and there is no red tape. Camping is also available at any of the lakes on the flanks of the peak. Water can be found in abundance in all the basins on all sides of the mountain. Many people camp at one of the many ponds and tarns north of Gunsight Pass on the Henry\'s Fork approach or around treeline on the Yellowstone Creek approach. Practice Leave No Trace policies! &lt;/p&gt;&lt;a name=\"chapter_10\"&gt;&lt;/a&gt;&lt;h2&gt;Mountain Conditions&lt;/h2&gt;&lt;p&gt;Information regarding the conditions for climbing Kings Peak can be obtained  from:&lt;/p&gt;&lt;p&gt;Wasatch-Cache National Forest&lt;br&gt;8236 Federal Bldg.&lt;br&gt;125 S. State Street&lt;br&gt;Salt Lake City, UT 84128&lt;br&gt;(801)524-3900&lt;/p&gt;&lt;p&gt;&lt;a href=\"http://www.wrh.noaa.gov/total_forecast/?dgtl=1&amp;amp;wfo=slc&amp;amp;zone=utz009&amp;amp;county=UTC013&amp;amp;mapzone=utz009&amp;amp;map.x=158&amp;amp;map.y=108\" target=\"_blank\"&gt;&lt;b&gt;Weather Forecast for Central Uinta Mountains&lt;/b&gt;&lt;/a&gt;.&lt;/p&gt;&lt;div&gt;&lt;a name=\"chapter_11\"&gt;&lt;/a&gt;&lt;h2&gt;External Links&lt;/h2&gt;&lt;p&gt;&lt;a href=\"http://forecast.weather.gov/MapClick.php?lat=40.77638178482896&amp;amp;lon=-110.37290096282959#.UtiX1_RDuUo\"&gt;Weather Forecast&lt;/a&gt;&lt;/p&gt;&lt;p&gt;&lt;a href=\"http://hiking.hyrumwright.org/peak/Kings+Peak\"&gt;Kings Peak Trip Report&lt;/a&gt;&lt;/p&gt;&lt;p&gt;&lt;a href=\"http://www.summitpost.org/mountains/trip_report.pl?trip_report_id=1860&amp;amp;mountain_id=3009\"&gt;Utah\'s 12000+ Foot Peaks with 300+ Feet Prominence&lt;/a&gt;&lt;/p&gt;&lt;p&gt;&lt;a href=\"http://www.greatoutdoors.com/published/camp/southwest/kingspeaklivesuptoitsname\"&gt;Kings Peak Lives Up to its Name&lt;/a&gt;&lt;/p&gt;&lt;p&gt;&lt;a href=\"http://www.greatoutdoors.com\" target=\"_blank\"&gt;Kings Peak on the Great Outdoors Website&lt;/a&gt;&lt;/p&gt;&lt;/div&gt;&lt;/div&gt;&lt;div align=\"center\"&gt;&lt;a name=\"messages\"&gt;&lt;/a&gt;&lt;a name=\"chapter_12\"&gt;&lt;/a&gt;&lt;h2&gt;Additions and Corrections&lt;/h2&gt;[ &lt;a href=\"/object_discussion.php?type=vote_comments&amp;amp;object_id=150376&amp;amp;is_addition=1#vote_form\"&gt;Post an Addition or Correction&lt;/a&gt; ]&lt;table cellpadding=\"0\" cellspacing=\"0\" style=\"width: 775px;\"&gt;&lt;tr&gt;&lt;td&gt;&lt;table cellpadding=\"2\" cellspacing=\"2\" width=\"100%\"&gt;&lt;tr&gt;&lt;td align=\"left\"&gt;&lt;span class=\"pagertext\"&gt;Viewing: &lt;b&gt;1&lt;/b&gt;-&lt;b&gt;14&lt;/b&gt; of &lt;b&gt;14&lt;/b&gt;&lt;/span&gt;&lt;/td&gt;&lt;td align=\"center\"&gt;&lt;/td&gt;&lt;td align=\"right\"&gt;    &lt;/td&gt;&lt;/tr&gt;&lt;/table&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/table&gt;&lt;table border=\"0\" width=\"775\" class=\"messages\"&gt;&lt;tr&gt;&lt;td width=\"110\" align=\"left\" valign=\"top\" style=\"padding-top: 6px;\"&gt;&lt;a name=\"110775\"&gt;&lt;/a&gt; &lt;a class=\"dis_user_lnk\" href=\"/users/scott/16365\"&gt;Scott&lt;/a&gt;&lt;/td&gt;&lt;td valign=\"top\" style=\"padding-top: 6px;\"&gt;&lt;b&gt;Untitled Comment&lt;/b&gt;&lt;/td&gt;&lt;td align=\"right\" valign=\"top\" nowrap style=\"font-size: 10px;\"&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td valign=\"top\" rowspan=\"2\" align=\"left\" style=\"padding-top: 6px;\"&gt;&lt;p class=\"disc_avatar\"&gt;&lt;a href=\"/users/scott/16365\"&gt;&lt;img alt=\"Scott\" width=\"50\" height=\"50\" border=\"0\" src=\"/images/avatars/142800.jpg\"&gt;&lt;/a&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=\"font-size: 9px; color: #888;\"&gt;Voted 10/10&lt;/span&gt;&lt;/p&gt;&lt;/td&gt;&lt;td colspan=\"2\" style=\"padding-top: 6px;\"&gt;The register comes and goes.     It wasn\'t there on my visits, but has been on other\'s visits.&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td valign=\"bottom\" style=\"padding-top: 6px;\" colspan=\"2\"&gt;&lt;span style=\"font-size: 9px; color: #888;\"&gt;Posted Jan 5, 2006 2:04 pm&lt;/span&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/table&gt;&lt;table border=\"0\" width=\"775\" class=\"messages\"&gt;&lt;tr&gt;&lt;td width=\"110\" align=\"left\" valign=\"top\" style=\"padding-top: 6px;\"&gt;&lt;a name=\"113338\"&gt;&lt;/a&gt; &lt;a class=\"dis_user_lnk\" href=\"/users/woodstrider/17180\"&gt;woodstrider&lt;/a&gt;&lt;/td&gt;&lt;td valign=\"top\" style=\"padding-top: 6px;\"&gt;&lt;b&gt;Untitled Comment&lt;/b&gt;&lt;/td&gt;&lt;td align=\"right\" valign=\"top\" nowrap style=\"font-size: 10px;\"&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td valign=\"top\" rowspan=\"2\" align=\"left\" style=\"padding-top: 6px;\"&gt;&lt;p class=\"disc_avatar\"&gt;&lt;a href=\"/users/woodstrider/17180\"&gt;&lt;img alt=\"woodstrider\" width=\"50\" height=\"50\" border=\"0\" src=\"/images/avatars/218787.gif\"&gt;&lt;/a&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=\"font-size: 9px; color: #888;\"&gt;Hasn\'t voted&lt;/span&gt;&lt;/p&gt;&lt;/td&gt;&lt;td colspan=\"2\" style=\"padding-top: 6px;\"&gt;Thanks Scott for the information. I don\'t feel so dumb now- it may  not have been there in that time frame. I had a great visit regardless.  &lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td valign=\"bottom\" style=\"padding-top: 6px;\" colspan=\"2\"&gt;&lt;span style=\"font-size: 9px; color: #888;\"&gt;Posted Jan 19, 2006 3:31 pm&lt;/span&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/table&gt;&lt;table border=\"0\" width=\"775\" class=\"messages\"&gt;&lt;tr&gt;&lt;td width=\"110\" align=\"left\" valign=\"top\" style=\"padding-top: 6px;\"&gt;&lt;a name=\"115151\"&gt;&lt;/a&gt; &lt;a class=\"dis_user_lnk\" href=\"/users/dmitry-pruss/22982\"&gt;Dmitry Pruss&lt;/a&gt;&lt;/td&gt;&lt;td valign=\"top\" style=\"padding-top: 6px;\"&gt;&lt;b&gt;Untitled Comment&lt;/b&gt;&lt;/td&gt;&lt;td align=\"right\" valign=\"top\" nowrap style=\"font-size: 10px;\"&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td valign=\"top\" rowspan=\"2\" align=\"left\" style=\"padding-top: 6px;\"&gt;&lt;p class=\"disc_avatar\"&gt;&lt;a href=\"/users/dmitry-pruss/22982\"&gt;&lt;img alt=\"Dmitry Pruss\" width=\"50\" height=\"50\" border=\"0\" src=\"/images/avatars/719338.JPG\"&gt;&lt;/a&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=\"font-size: 9px; color: #888;\"&gt;Voted 10/10&lt;/span&gt;&lt;/p&gt;&lt;/td&gt;&lt;td colspan=\"2\" style=\"padding-top: 6px;\"&gt;It\' s the forest service which is charged with removing registers from up there. Ever year there is something different, sometimes a few registers, sometimes none. There used to be a Highpoint plaque in late 90s, and a geocache around 2002-2004, and a fallen officer plaque in 2005, but all of them end up being removed from the top as unapproved in the designated wilderness. I guess the latest plaque actually has been approved, but by a wrong office of the FS, so it had to go in the end.&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td valign=\"bottom\" style=\"padding-top: 6px;\" colspan=\"2\"&gt;&lt;span style=\"font-size: 9px; color: #888;\"&gt;Posted Jan 28, 2006 2:24 pm&lt;/span&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/table&gt;&lt;table border=\"0\" width=\"775\" class=\"messages\"&gt;&lt;tr&gt;&lt;td width=\"110\" align=\"left\" valign=\"top\" style=\"padding-top: 6px;\"&gt;&lt;a name=\"121366\"&gt;&lt;/a&gt; &lt;a class=\"dis_user_lnk\" href=\"/users/powerjl/25722\"&gt;POWERJL&lt;/a&gt;&lt;/td&gt;&lt;td valign=\"top\" style=\"padding-top: 6px;\"&gt;&lt;b&gt;Untitled Comment&lt;/b&gt;&lt;/td&gt;&lt;td align=\"right\" valign=\"top\" nowrap style=\"font-size: 10px;\"&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td valign=\"top\" rowspan=\"2\" align=\"left\" style=\"padding-top: 6px;\"&gt;&lt;p&gt;&lt;span style=\"font-size: 9px; color: #888;\"&gt;Hasn\'t voted&lt;/span&gt;&lt;/p&gt;&lt;/td&gt;&lt;td colspan=\"2\" style=\"padding-top: 6px;\"&gt;The majority of folks heading up TR 117 (Henry\'s Fork Trail) are camping at Dollar Lake, and when we were there recently (7/29), there were over 200 people at Dollar lake alone. Due to heavy use the USFS has placed a no camping restriction within 1000\' of both Dollar and Henry\'s Fork lakes.  &lt;br&gt;&lt;br&gt;\r&lt;br&gt;&lt;br&gt;&lt;br&gt;\r&lt;br&gt;FOR SMALL PARTIES ONLY! (1, 2 or 3 people) I would recomend continuing past Dollar lake and camping closer to Gunsight Pass. There is a small pond approx. 100m to the sw of the trail at around  11,000\'  which have a couple good spots to pitch a tent, and a larger pond / small lake at  about 11,630\' , approx. 5km ssw from the trail. These are small areas and will NOT support the large groups the BSA likes to bring in.&lt;br&gt;&lt;br&gt;\r&lt;br&gt;&lt;br&gt;&lt;br&gt;\r&lt;br&gt;Be aware that the USFS (as they should) are enforcing the no camping closer than 200\' from trails or water sources.&lt;br&gt;&lt;br&gt;\r&lt;br&gt;&lt;br&gt;&lt;br&gt;\r&lt;br&gt;If your really up to it, go on over Gunsight pass into the basin on the other side. Although its above tree-line there are a lot of places to camp, running water, and a lot of solitude. &lt;br&gt;&lt;br&gt;\r&lt;br&gt;&lt;br&gt;&lt;br&gt;\r&lt;br&gt;One final note. In addition to speaking with a very frustrated USFS Ranger who was cleaing campsites,  there is a lot of trash in the area.  &lt;br&gt;&lt;br&gt;\r&lt;br&gt;Please, if you pack in, pack it out!&lt;br&gt;&lt;br&gt;\r&lt;br&gt;&lt;br&gt;&lt;br&gt;\r&lt;br&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td valign=\"bottom\" style=\"padding-top: 6px;\" colspan=\"2\"&gt;&lt;span style=\"font-size: 9px; color: #888;\"&gt;Posted Aug 4, 2005 2:52 pm&lt;/span&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/table&gt;&lt;table border=\"0\" width=\"775\" class=\"messages\"&gt;&lt;tr&gt;&lt;td width=\"110\" align=\"left\" valign=\"top\" style=\"padding-top: 6px;\"&gt;&lt;a name=\"121977\"&gt;&lt;/a&gt; &lt;a class=\"dis_user_lnk\" href=\"/users/woodstrider/17180\"&gt;woodstrider&lt;/a&gt;&lt;/td&gt;&lt;td valign=\"top\" style=\"padding-top: 6px;\"&gt;&lt;b&gt;Untitled Comment&lt;/b&gt;&lt;/td&gt;&lt;td align=\"right\" valign=\"top\" nowrap style=\"font-size: 10px;\"&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td valign=\"top\" rowspan=\"2\" align=\"left\" style=\"padding-top: 6px;\"&gt;&lt;p class=\"disc_avatar\"&gt;&lt;a href=\"/users/woodstrider/17180\"&gt;&lt;img alt=\"woodstrider\" width=\"50\" height=\"50\" border=\"0\" src=\"/images/avatars/218787.gif\"&gt;&lt;/a&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=\"font-size: 9px; color: #888;\"&gt;Hasn\'t voted&lt;/span&gt;&lt;/p&gt;&lt;/td&gt;&lt;td colspan=\"2\" style=\"padding-top: 6px;\"&gt;There seems to be a summit register on Kings Peak. I discovered this, after I had been there, when I saw a photo of it in the Highpointers magazine. It is bright red. I wish I had known about it so that I could have signed it. Well, I guess another trip is warranted.&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td valign=\"bottom\" style=\"padding-top: 6px;\" colspan=\"2\"&gt;&lt;span style=\"font-size: 9px; color: #888;\"&gt;Posted Jan 5, 2006 1:39 pm&lt;/span&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/table&gt;&lt;table border=\"0\" width=\"775\" class=\"messages\"&gt;&lt;tr&gt;&lt;td width=\"110\" align=\"left\" valign=\"top\" style=\"padding-top: 6px;\"&gt;&lt;a name=\"471253\"&gt;&lt;/a&gt; &lt;a class=\"dis_user_lnk\" href=\"/users/climbingrandy/58232\"&gt;ClimbingRandy&lt;/a&gt;&lt;/td&gt;&lt;td valign=\"top\" style=\"padding-top: 6px;\"&gt;&lt;b&gt;Mountain Bikes?&lt;/b&gt;&lt;/td&gt;&lt;td align=\"right\" valign=\"top\" nowrap style=\"font-size: 10px;\"&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td valign=\"top\" rowspan=\"2\" align=\"left\" style=\"padding-top: 6px;\"&gt;&lt;p class=\"disc_avatar\"&gt;&lt;a href=\"/users/climbingrandy/58232\"&gt;&lt;img alt=\"ClimbingRandy\" width=\"50\" height=\"50\" border=\"0\" src=\"/images/avatars/908446.jpg\"&gt;&lt;/a&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=\"font-size: 9px; color: #888;\"&gt;Hasn\'t voted&lt;/span&gt;&lt;/p&gt;&lt;/td&gt;&lt;td colspan=\"2\" style=\"padding-top: 6px;\"&gt;The approach from Henry\'s Fork doesn\'t appear to increase in elevation too quickly and also covers what appears to be open plains.  Has anyone ridden a mountain bike the majority of the way to Gunsight pass?  Is it permitted by USFS?&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td valign=\"bottom\" style=\"padding-top: 6px;\" colspan=\"2\"&gt;&lt;span style=\"font-size: 9px; color: #888;\"&gt;Posted May 25, 2013 3:05 pm&lt;/span&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/table&gt;&lt;table border=\"0\" width=\"745\" class=\"messages\" style=\"margin-left: 30px;\"&gt;&lt;tr&gt;&lt;td rowspan=\"4\" valign=\"top\"&gt;&lt;img src=\"/styles/layout/reply_arrow.gif\"&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td width=\"110\" align=\"left\" valign=\"top\" style=\"padding-top: 6px;\"&gt;&lt;a name=\"471954\"&gt;&lt;/a&gt; &lt;a class=\"dis_user_lnk\" href=\"/users/mccannster/23212\"&gt;McCannster&lt;/a&gt;&lt;/td&gt;&lt;td valign=\"top\" style=\"padding-top: 6px;\"&gt;&lt;b&gt;Re: Mountain Bikes?&lt;/b&gt;&lt;/td&gt;&lt;td align=\"right\" valign=\"top\" nowrap style=\"font-size: 10px;\"&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td valign=\"top\" rowspan=\"2\" align=\"left\" style=\"padding-top: 6px;\"&gt;&lt;p class=\"disc_avatar\"&gt;&lt;a href=\"/users/mccannster/23212\"&gt;&lt;img alt=\"McCannster\" width=\"50\" height=\"50\" border=\"0\" src=\"/images/avatars/959073.JPG\"&gt;&lt;/a&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=\"font-size: 9px; color: #888;\"&gt;Voted 10/10&lt;/span&gt;&lt;/p&gt;&lt;/td&gt;&lt;td colspan=\"2\" style=\"padding-top: 6px;\"&gt;Bikes aren\'t allowed on that trail, as it enters a wilderness area very quickly and wheeled vehicles aren\'t permitted in wilderness areas.&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td valign=\"bottom\" style=\"padding-top: 6px;\" colspan=\"2\"&gt;&lt;span style=\"font-size: 9px; color: #888;\"&gt;Posted Jun 14, 2013 11:40 am&lt;/span&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/table&gt;&lt;table border=\"0\" width=\"775\" class=\"messages\"&gt;&lt;tr&gt;&lt;td width=\"110\" align=\"left\" valign=\"top\" style=\"padding-top: 6px;\"&gt;&lt;a name=\"483505\"&gt;&lt;/a&gt; &lt;a class=\"dis_user_lnk\" href=\"/users/dmitry-pruss/22982\"&gt;Dmitry Pruss&lt;/a&gt;&lt;/td&gt;&lt;td valign=\"top\" style=\"padding-top: 6px;\"&gt;&lt;b&gt;Seasons&lt;/b&gt;&lt;/td&gt;&lt;td align=\"right\" valign=\"top\" nowrap style=\"font-size: 10px;\"&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td valign=\"top\" rowspan=\"2\" align=\"left\" style=\"padding-top: 6px;\"&gt;&lt;p class=\"disc_avatar\"&gt;&lt;a href=\"/users/dmitry-pruss/22982\"&gt;&lt;img alt=\"Dmitry Pruss\" width=\"50\" height=\"50\" border=\"0\" src=\"/images/avatars/719338.JPG\"&gt;&lt;/a&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=\"font-size: 9px; color: #888;\"&gt;Voted 10/10&lt;/span&gt;&lt;/p&gt;&lt;/td&gt;&lt;td colspan=\"2\" style=\"padding-top: 6px;\"&gt;Matt, may I add a correction re: \"Generally the best conditions for climbing Kings Peak occur from June through September, depending on snow and weather conditions. It is certainly climbable any time of year however winter ascents will require skis, lots of warm clothing, a liberal dose of spare time and a love for self inflicted torture!\"&lt;br&gt;&lt;br&gt;June climbs may be plagued by softened snowfields. And it is certainly quite unwise to call skiing a \"love of torture\". Springtime skiing typically has mild temperatures, and in good conditions, glide back from the upper basins to the trailhead may take mere couple hours and little effort, making daytrips to the peak a cherished tradition. No rain or mud, no insects or unstable boulders, no horsesh*t or noisy gangs of scouts, just what kind of a \"turture\" is it?&lt;br&gt;&lt;br&gt;And of course snowshoeing is fairly popular too.&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td valign=\"bottom\" style=\"padding-top: 6px;\" colspan=\"2\"&gt;&lt;span style=\"font-size: 9px; color: #888;\"&gt;Posted Jun 4, 2014 1:45 pm&lt;/span&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/table&gt;&lt;table border=\"0\" width=\"745\" class=\"messages\" style=\"margin-left: 30px;\"&gt;&lt;tr&gt;&lt;td rowspan=\"4\" valign=\"top\"&gt;&lt;img src=\"/styles/layout/reply_arrow.gif\"&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td width=\"110\" align=\"left\" valign=\"top\" style=\"padding-top: 6px;\"&gt;&lt;a name=\"483506\"&gt;&lt;/a&gt; &lt;a class=\"dis_user_lnk\" href=\"/users/matt-lemke/61266\"&gt;Matt Lemke&lt;/a&gt;&lt;/td&gt;&lt;td valign=\"top\" style=\"padding-top: 6px;\"&gt;&lt;b&gt;Re: Seasons&lt;/b&gt;&lt;/td&gt;&lt;td align=\"right\" valign=\"top\" nowrap style=\"font-size: 10px;\"&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td valign=\"top\" rowspan=\"2\" align=\"left\" style=\"padding-top: 6px;\"&gt;&lt;p class=\"disc_avatar\"&gt;&lt;a href=\"/users/matt-lemke/61266\"&gt;&lt;img alt=\"Matt Lemke\" width=\"50\" height=\"50\" border=\"0\" src=\"/images/avatars/888707.JPG\"&gt;&lt;/a&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=\"font-size: 9px; color: #888;\"&gt;Hasn\'t voted&lt;/span&gt;&lt;/p&gt;&lt;/td&gt;&lt;td colspan=\"2\" style=\"padding-top: 6px;\"&gt;I am referring to midwinter ascents with that comment...not springtime :)&lt;br&gt;&lt;br&gt;&lt;br&gt;&lt;br&gt;It was meant as humerous and to be taken  as as joke lol. &lt;br&gt;&lt;br&gt;&lt;br&gt;&lt;br&gt;However I will also say that skiing is impossible ;)&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td valign=\"bottom\" style=\"padding-top: 6px;\" colspan=\"2\"&gt;&lt;span style=\"font-size: 9px; color: #888;\"&gt;Posted Jun 4, 2014 1:48 pm&lt;/span&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/table&gt;&lt;table border=\"0\" width=\"745\" class=\"messages\" style=\"margin-left: 30px;\"&gt;&lt;tr&gt;&lt;td rowspan=\"4\" valign=\"top\"&gt;&lt;img src=\"/styles/layout/reply_arrow.gif\"&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td width=\"110\" align=\"left\" valign=\"top\" style=\"padding-top: 6px;\"&gt;&lt;a name=\"483645\"&gt;&lt;/a&gt; &lt;a class=\"dis_user_lnk\" href=\"/users/dmitry-pruss/22982\"&gt;Dmitry Pruss&lt;/a&gt;&lt;/td&gt;&lt;td valign=\"top\" style=\"padding-top: 6px;\"&gt;&lt;b&gt;Re: Seasons&lt;/b&gt;&lt;/td&gt;&lt;td align=\"right\" valign=\"top\" nowrap style=\"font-size: 10px;\"&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td valign=\"top\" rowspan=\"2\" align=\"left\" style=\"padding-top: 6px;\"&gt;&lt;p class=\"disc_avatar\"&gt;&lt;a href=\"/users/dmitry-pruss/22982\"&gt;&lt;img alt=\"Dmitry Pruss\" width=\"50\" height=\"50\" border=\"0\" src=\"/images/avatars/719338.JPG\"&gt;&lt;/a&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=\"font-size: 9px; color: #888;\"&gt;Voted 10/10&lt;/span&gt;&lt;/p&gt;&lt;/td&gt;&lt;td colspan=\"2\" style=\"padding-top: 6px;\"&gt;Oh, I understand that you were trying to be funny. Humor is appreciated. Of course trip reports and picture captions, being more personal accounts, may be better places for jokes than mountain pages (which are generally written to provide useful information). At the very least, if you are compelled to be funny, please don\'t do it \"at the expense of factual truth\"&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td valign=\"bottom\" style=\"padding-top: 6px;\" colspan=\"2\"&gt;&lt;span style=\"font-size: 9px; color: #888;\"&gt;Posted Jun 10, 2014 1:31 pm&lt;/span&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/table&gt;&lt;table border=\"0\" width=\"775\" class=\"messages\"&gt;&lt;tr&gt;&lt;td width=\"110\" align=\"left\" valign=\"top\" style=\"padding-top: 6px;\"&gt;&lt;a name=\"484377\"&gt;&lt;/a&gt; &lt;a class=\"dis_user_lnk\" href=\"/users/bark-eater/38982\"&gt;Bark Eater&lt;/a&gt;&lt;/td&gt;&lt;td valign=\"top\" style=\"padding-top: 6px;\"&gt;&lt;b&gt;West Gunsight Peak Variation&lt;/b&gt;&lt;/td&gt;&lt;td align=\"right\" valign=\"top\" nowrap style=\"font-size: 10px;\"&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td valign=\"top\" rowspan=\"2\" align=\"left\" style=\"padding-top: 6px;\"&gt;&lt;p class=\"disc_avatar\"&gt;&lt;a href=\"/users/bark-eater/38982\"&gt;&lt;img alt=\"Bark Eater\" width=\"50\" height=\"50\" border=\"0\" src=\"/images/avatars/941847.jpg\"&gt;&lt;/a&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=\"font-size: 9px; color: #888;\"&gt;Voted 10/10&lt;/span&gt;&lt;/p&gt;&lt;/td&gt;&lt;td colspan=\"2\" style=\"padding-top: 6px;\"&gt;We didn\'t find the herd path from Gunsight Pass around West Gunsight Peak, instead dropping down into Painter Basin.  A lower herd path allowed us to cut off a bit from the \"official\" trail junction, but down on the flats.  Apparently one has to climb directly up from Gunsight Pass in order to stay above the cliff band and large talus slope.  Fun mountain all the same!&lt;br&gt;&lt;br&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td valign=\"bottom\" style=\"padding-top: 6px;\" colspan=\"2\"&gt;&lt;span style=\"font-size: 9px; color: #888;\"&gt;Posted Jul 7, 2014 12:36 pm&lt;/span&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/table&gt;&lt;table border=\"0\" width=\"775\" class=\"messages\"&gt;&lt;tr&gt;&lt;td width=\"110\" align=\"left\" valign=\"top\" style=\"padding-top: 6px;\"&gt;&lt;a name=\"485940\"&gt;&lt;/a&gt; &lt;a class=\"dis_user_lnk\" href=\"/users/hansendp/122577\"&gt;hansendp&lt;/a&gt;&lt;/td&gt;&lt;td valign=\"top\" style=\"padding-top: 6px;\"&gt;&lt;b&gt;Saturday before Labor Day 2014&lt;/b&gt;&lt;/td&gt;&lt;td align=\"right\" valign=\"top\" nowrap style=\"font-size: 10px;\"&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td valign=\"top\" rowspan=\"2\" align=\"left\" style=\"padding-top: 6px;\"&gt;&lt;p&gt;&lt;span style=\"font-size: 9px; color: #888;\"&gt;Hasn\'t voted&lt;/span&gt;&lt;/p&gt;&lt;/td&gt;&lt;td colspan=\"2\" style=\"padding-top: 6px;\"&gt;Having climbed Kings 10 years ago I had forgotton how long this climb really is. When we finally reached Anderson Pass there was snow and icy wind blowing as well as black clouds everywhere. Terrible conditions. Most people were turning back and so did we. We made the mistake of hiking back to Dollar Lake to our camp and then hiking back to the trail head in the dark. What a mistake! Stumbling along the trail from Elk Horn crossing to the parking lot was brutal especially with how totally spent we were and then having to use head lamps. Anwyay we made it to the truck around 11:30 pm. &lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td valign=\"bottom\" style=\"padding-top: 6px;\" colspan=\"2\"&gt;&lt;span style=\"font-size: 9px; color: #888;\"&gt;Posted Sep 17, 2014 3:30 pm&lt;/span&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/table&gt;&lt;table border=\"0\" width=\"775\" class=\"messages\"&gt;&lt;tr&gt;&lt;td width=\"110\" align=\"left\" valign=\"top\" style=\"padding-top: 6px;\"&gt;&lt;a name=\"490666\"&gt;&lt;/a&gt; &lt;a class=\"dis_user_lnk\" href=\"/users/greg-bkn/126782\"&gt;Greg_BKN&lt;/a&gt;&lt;/td&gt;&lt;td valign=\"top\" style=\"padding-top: 6px;\"&gt;&lt;b&gt;Current conditions&lt;/b&gt;&lt;/td&gt;&lt;td align=\"right\" valign=\"top\" nowrap style=\"font-size: 10px;\"&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td valign=\"top\" rowspan=\"2\" align=\"left\" style=\"padding-top: 6px;\"&gt;&lt;p class=\"disc_avatar\"&gt;&lt;a href=\"/users/greg-bkn/126782\"&gt;&lt;img alt=\"Greg_BKN\" width=\"50\" height=\"50\" border=\"0\" src=\"/images/avatars/940904.jpg\"&gt;&lt;/a&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=\"font-size: 9px; color: #888;\"&gt;Hasn\'t voted&lt;/span&gt;&lt;/p&gt;&lt;/td&gt;&lt;td colspan=\"2\" style=\"padding-top: 6px;\"&gt;Has anyone been on the normal route to Kings Peak recently?&lt;br&gt;&lt;br&gt;What are conditions like?  Are crampons/ or snow shoes needed?&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td valign=\"bottom\" style=\"padding-top: 6px;\" colspan=\"2\"&gt;&lt;span style=\"font-size: 9px; color: #888;\"&gt;Posted May 25, 2015 6:40 pm&lt;/span&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/table&gt;&lt;table border=\"0\" width=\"775\" class=\"messages\"&gt;&lt;tr&gt;&lt;td width=\"110\" align=\"left\" valign=\"top\" style=\"padding-top: 6px;\"&gt;&lt;a name=\"490931\"&gt;&lt;/a&gt; &lt;a class=\"dis_user_lnk\" href=\"/users/jstodda/127155\"&gt;jstodda&lt;/a&gt;&lt;/td&gt;&lt;td valign=\"top\" style=\"padding-top: 6px;\"&gt;&lt;b&gt;Awesome Hike&lt;/b&gt;&lt;/td&gt;&lt;td align=\"right\" valign=\"top\" nowrap style=\"font-size: 10px;\"&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td valign=\"top\" rowspan=\"2\" align=\"left\" style=\"padding-top: 6px;\"&gt;&lt;p&gt;&lt;span style=\"font-size: 9px; color: #888;\"&gt;Voted 10/10&lt;/span&gt;&lt;/p&gt;&lt;/td&gt;&lt;td colspan=\"2\" style=\"padding-top: 6px;\"&gt;Planning my second trip to Kings for Fathers day. It was a great hike, I chose to camp at the base of the chute and take that first thing in the morning. Its like climbing an escalator the wrong way, but it wasn\'t impossible (I was alone so I didn\'t worry about falling rocks). From there it was up to meet the trail and onto glory. Was a beautiful day at the top and a great climb. I hike purely in Vibram Five Fingers so it was a bit rocky but not really tough. One of my all time favorites.&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td valign=\"bottom\" style=\"padding-top: 6px;\" colspan=\"2\"&gt;&lt;span style=\"font-size: 9px; color: #888;\"&gt;Posted Jun 15, 2015 10:25 pm&lt;/span&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/table&gt;&lt;br&gt;&lt;table border=\"0\" width=\"775\"&gt;&lt;tr&gt;&lt;td&gt;&lt;table cellpadding=\"2\" cellspacing=\"2\" width=\"100%\"&gt;&lt;tr&gt;&lt;td align=\"left\"&gt;&lt;span class=\"pagertext\"&gt;Viewing: &lt;b&gt;1&lt;/b&gt;-&lt;b&gt;14&lt;/b&gt; of &lt;b&gt;14&lt;/b&gt;&lt;/span&gt;&lt;/td&gt;&lt;td align=\"center\"&gt;&lt;/td&gt;&lt;td align=\"right\"&gt;    &lt;/td&gt;&lt;/tr&gt;&lt;/table&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/table&gt;&lt;br&gt;&lt;/div&gt;&lt;a name=\"chapter_13\"&gt;&lt;/a&gt;&lt;h2&gt;Images&lt;/h2&gt;&lt;div align=\"center\"&gt;&lt;table id=\"my_images\"&gt;&lt;tr&gt;&lt;td&gt;&lt;a href=\"/kings-peak/434069/c-150376\"&gt;&lt;img alt=\"Kings Peak\" src=\"/images/square/434069.jpg\"&gt;&lt;/a&gt;&lt;/td&gt;&lt;td&gt;&lt;a href=\"/kings-peak-tele/417319/c-150376\"&gt;&lt;img alt=\"Kings Peak Tele\" src=\"/images/square/417319.JPG\"&gt;&lt;/a&gt;&lt;/td&gt;&lt;td&gt;&lt;a href=\"/reflection-in-tarn/416895/c-150376\"&gt;&lt;img alt=\"Reflection in Tarn\" src=\"/images/square/416895.JPG\"&gt;&lt;/a&gt;&lt;/td&gt;&lt;td&gt;&lt;a href=\"/west-gunsight-pk-5-22-05/106713/c-150376\"&gt;&lt;img alt=\"West Gunsight Pk, 5/22/05\" src=\"/images/square/106713.JPG\"&gt;&lt;/a&gt;&lt;/td&gt;&lt;td&gt;&lt;a href=\"/looking-at-kings-peak-through/20402/c-150376\"&gt;&lt;img alt=\"Looking at Kings Peak through...\" src=\"/images/square/20402.jpg\"&gt;&lt;/a&gt;&lt;/td&gt;&lt;td&gt;&lt;a href=\"/henrys-fork-basin/506613/c-150376\"&gt;&lt;img alt=\"Henrys Fork Basin\" src=\"/images/square/506613.jpg\"&gt;&lt;/a&gt;&lt;/td&gt;&lt;td&gt;&lt;a href=\"/kpt07-jan-on-the-summit-block/282010/c-150376\"&gt;&lt;img alt=\"KPT07: Jan on the summit block\" src=\"/images/square/282010.JPG\"&gt;&lt;/a&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td&gt;&lt;a href=\"/king-s-peak-and-rock-slide/121876/c-150376\"&gt;&lt;img alt=\"King\\\'s Peak and rock slide\" src=\"/images/square/121876.JPG\"&gt;&lt;/a&gt;&lt;/td&gt;&lt;td&gt;&lt;a href=\"/kings-peak-and-west-gunsight-peak/842412/c-150376\"&gt;&lt;img alt=\"Kings Peak and West Gunsight Peak\" src=\"/images/square/842412.JPG\"&gt;&lt;/a&gt;&lt;/td&gt;&lt;td&gt;&lt;a href=\"/west-face-of-kings-south-kings/441665/c-150376\"&gt;&lt;img alt=\"West Face of Kings / South Kings\" src=\"/images/square/441665.JPG\"&gt;&lt;/a&gt;&lt;/td&gt;&lt;td&gt;&lt;a href=\"/utah-s-highest-peaks/572513/c-150376\"&gt;&lt;img alt=\"Utah\\\'s highest peaks\" src=\"/images/square/572513.jpg\"&gt;&lt;/a&gt;&lt;/td&gt;&lt;td&gt;&lt;a href=\"/kings-at-sunset/318620/c-150376\"&gt;&lt;img alt=\"Kings at sunset\" src=\"/images/square/318620.JPG\"&gt;&lt;/a&gt;&lt;/td&gt;&lt;td&gt;&lt;a href=\"/west-gunsight-and-kings-peaks/392977/c-150376\"&gt;&lt;img alt=\"West Gunsight and Kings Peaks\" src=\"/images/square/392977.JPG\"&gt;&lt;/a&gt;&lt;/td&gt;&lt;td&gt;&lt;a href=\"/kings-peak-and-south-kings-peak/303023/c-150376\"&gt;&lt;img alt=\"Kings Peak and South Kings Peak\" src=\"/images/square/303023.jpg\"&gt;&lt;/a&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td colspan=\"7\" align=\"center\"&gt;[ &lt;a href=\"/kings-peak/images/p-150376\"&gt;View Gallery - 354 More Images&lt;/a&gt; ]&lt;/td&gt;&lt;/tr&gt;&lt;/table&gt;&lt;/div&gt;&lt;/article&gt;date:difficulty:'),(18,150378,'/half-dome/150378','/yosemite-national-park/171226','Half Dome','','','','8842','',120448,37.7461,-119.532,'California, United States, North America','','',171226,'Yosemite National Park','','','','','','Mountain-Rock','content: &lt;article&gt;&lt;p&gt;&lt;/p&gt;&lt;a name=\"chapter_1\"&gt;&lt;/a&gt;&lt;h2 id=\"first_h2\"&gt;**NEWS: Regular Northwest Face affected by huge rockfall**&lt;/h2&gt;On or around July 3 (a party attempting the route on the 5th made the discovery), huge rockrall, including the ledge comprising the Robbins Traverse, occurred on the northwest face of Half Dome. Portions of the Regular NW face have been affected. At this time, it is unknown how this will be addressed to allow future climbers to climb this route, one of the most significant historically in North America. In addition to this, sections above the affected areas are currently considered unstable and at risk of future rockfall. Climbers are advised to stay away from the route until stability is better ascertained. &lt;br&gt;&lt;br&gt;(&lt;a href=\"http://www.climbing.com/news/big-section-of-half-dome-route-falls-off/\"&gt;link&lt;/a&gt;)&lt;br&gt;&lt;a name=\"chapter_2\"&gt;&lt;/a&gt;&lt;h2&gt;Overview&lt;/h2&gt;When most people think of Yosemite, an image of Half Dome\'s &lt;a href=\"/mountains/photo_link.pl?photo_id=2011&amp;amp;object_id=305&amp;amp;type=mountain&amp;amp;mountain_id=305&amp;amp;route_id=\"&gt;sheer northwest face&lt;/a&gt; towering over the valley immediately comes to mind. The inspiring view and hiker accessible \"Cables Route\" to the summit has made Half Dome one of the must do hikes for visitors coming to Yosemite National Park (YNP) from California, other parts of the United States, and indeed from around the world. Half Dome has inspired many people to take up hiking, climbing, and has even seen BASE jumpers (see Red Tape section below) and snowboarders take off from its summit. The end of 2000 saw the first two snowboard descents of Half Dome\'s east side by Jim Zellers. Unfortunately, all this attention has also made this summit extremely crowded, especially between Memorial Day and Labor Day. If you plan to hike the &lt;a href=\"/mountains/route_link.pl/route_id/1534/object_id/221\"&gt;Cables Route&lt;/a&gt; (see below) during the high season, be sure to get an early start (as well as a permit; see Red Tape below) as you can often get stuck in gridlock on the narrow cables section during the middle of the day (Ref: &lt;a href=\"/mountains/user_link.pl?user_id=1237\"&gt;BranchWhitney&lt;/a&gt;). The park doesn\'t keep official numbers of visitors, but Jim Snyder, the park\'s historian, says \"\"On Memorial Day in 1972, more than 700 people went up. It was horrendous.\" While people have been known to spend an hour on the Cables each way during the high season, climbing &lt;a href=\"/mountains/route_link.pl/route_id/79/object_id/221\"&gt;Snake Dike&lt;/a&gt; on a popular summer weekend may be even worse with reports of parties waiting 5 hours at the base of the route before getting on the rock. To get away from the crowds and enjoy fine views of the valley, try &lt;a href=\"/mountains/mountain_link.pl/mountain_id/279\"&gt;Clouds Rest&lt;/a&gt; or &lt;a href=\"/mountains/mountain_link.pl/mountain_id/305\"&gt;North Dome&lt;/a&gt;. These peaks also have excellent views of Half Dome itself.  &lt;p&gt;  The majority of Half Dome ascents are made via the \"Cables Route\" via the Yosemite Valley or Happy Isles Approach. This 8.2 mile route follows the John Muir Trail (JMT) from the valley\'s Happy Isles Trailhead (4,105 feet) for 6.2 miles before branching off to the Half Dome summit trail. This route is often good at intimidating for the first time hiker due to the last 840 feet of the ascent which are assisted by &lt;a href=\"/mountains/photo_link.pl/p/photo_id__1119__object_id__2__type__user__mountain_id____route_id____user_id__\"&gt;steel cables&lt;/a&gt; attached to the dome\'s east side. Many people choose to use work gloves on the cables portion of the route to get a better grip. During the high season there is often a pile of work gloves at the base of the cables for your use, however, it may be a good idea to bring your own gloves just in case. Hikers using the JMT from the valley also have an option to take the more strenuous Mists Trail variation which gets its name from the fine spray flowing over the trail from Nevada Falls. The Mists Trail connects to the JMT on both ends so you won\'t have to worry about getting lost.  &lt;/p&gt;&lt;p&gt;  Warning from &lt;a href=\"/mountains/user_link.pl/user_id/305\"&gt;mconnell&lt;/a&gt; (edited): Many inexperienced hikers attempt this summit unprepared and wind up running out of water or coming down in the dark without. Basic essentials that should be considered include: enough water (2+ liters / person recommended), water filters or purification tablets, flashlights and jackets for late night descents, etc. Don\'t depend on rangers to escort you down, since they won\'t do this unless you\'re seriously injured.  &lt;/p&gt;&lt;p&gt;  Half Dome\'s steep NW face is home to dozens of classic rock climbs, including the Regular and Direct NW Face, all being fairly serious. There are also a number of rock climbs on its lower angle SW and South faces including the ultra popular Snake Dike route. The Regular NW Face is probably the third most popular route on the dome after the Cables Route and Snake Dike.  &lt;/p&gt;&lt;ul&gt;  &lt;table border=\"1\" cellspacing=\"0\"&gt;&lt;tbody&gt;&lt;tr&gt;&lt;td&gt;&lt;table border=\"0\" cellspacing=\"2\" cellpading=\"1\"&gt;&lt;tbody&gt;&lt;tr bgcolor=\"#c0c0c0\"&gt;&lt;td&gt;&lt;font style=\"font-weight: bold;\"&gt;Map Name&lt;/font&gt;&lt;/td&gt;  &lt;td&gt;&lt;font style=\"font-weight: bold;\"&gt;Publisher&lt;/font&gt;&lt;/td&gt;  &lt;td&gt;&lt;font style=\"font-weight: bold;\"&gt;Scale&lt;/font&gt;&lt;/td&gt;  &lt;td&gt;&lt;font style=\"font-weight: bold;\"&gt;Interval&lt;/font&gt;&lt;/td&gt;  &lt;td&gt;&lt;font style=\"font-weight: bold;\"&gt;Plastic&lt;/font&gt;&lt;/td&gt;  &lt;td&gt;&lt;font style=\"font-weight: bold;\"&gt;Lat/Long&lt;/font&gt;&lt;/td&gt;  &lt;td&gt;&lt;font style=\"font-weight: bold;\"&gt;UTM&lt;/font&gt;&lt;/td&gt;&lt;/tr&gt;  &lt;tr&gt;  &lt;td&gt;Half Dome&lt;/td&gt;  &lt;td&gt;&lt;a href=\"http://mcmcweb.er.usgs.gov/topomaps/\"&gt;USGS&lt;/a&gt;&lt;/td&gt;  &lt;td&gt;1:24,000&lt;/td&gt;  &lt;td align=\"center\"&gt;?&lt;/td&gt;  &lt;td align=\"center\"&gt;N&lt;/td&gt;  &lt;td align=\"center\"&gt;?&lt;/td&gt;  &lt;td align=\"center\"&gt;?&lt;/td&gt;  &lt;/tr&gt;  &lt;tr&gt;  &lt;td&gt;Yosemite High Country Trail Map&lt;/td&gt;  &lt;td&gt;&lt;a href=\"http://www.tomharrisonmaps.com\"&gt;Tom Harrison Maps&lt;/a&gt;&lt;/td&gt;  &lt;td&gt;1:63,360&lt;/td&gt;  &lt;td align=\"center\"&gt;200 ft&lt;/td&gt;  &lt;td align=\"center\"&gt;Y&lt;/td&gt;  &lt;td align=\"center\"&gt;Y&lt;/td&gt;  &lt;td align=\"center\"&gt;Y&lt;/td&gt;  &lt;/tr&gt;  &lt;tr&gt;  &lt;td&gt;&lt;a href=\"http://www.trailsillustrated.com/acb/showdetl.cfm?&amp;amp;DID=15&amp;amp;Product_ID=26&amp;amp;CATID=1\"&gt;Yosemite National Park&lt;/a&gt;&lt;/td&gt;  &lt;td&gt;Trails Illustrated&lt;/td&gt;  &lt;td&gt;1:100,000&lt;/td&gt;  &lt;td align=\"center\"&gt;200 ft&lt;/td&gt;  &lt;td align=\"center\"&gt;Y&lt;/td&gt;  &lt;td align=\"center\"&gt;Y&lt;/td&gt;  &lt;td align=\"center\"&gt;?&lt;/td&gt;  &lt;/tr&gt;  &lt;/tbody&gt;&lt;/table&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/tbody&gt;&lt;/table&gt;&lt;/ul&gt;&lt;br&gt;&lt;p&gt;&lt;/p&gt;&lt;table class=\"inline_image_thumbs\"&gt;&lt;tbody&gt;&lt;tr&gt;&lt;td&gt;&lt;a href=\"/view_object.php?object_id=40707\" rel=\"no_follow\"&gt;&lt;img src=\"/images/thumb/40707.jpg\" unselectable=\"on\"&gt;&lt;/a&gt;&lt;/td&gt;&lt;td&gt;&lt;a href=\"/view_object.php?object_id=5934\" rel=\"no_follow\"&gt;&lt;img src=\"/images/thumb/5934.jpg\" unselectable=\"on\"&gt;&lt;/a&gt;&lt;/td&gt;&lt;td&gt;&lt;a href=\"/view_object.php?object_id=41718\" rel=\"no_follow\"&gt;&lt;img src=\"/images/thumb/41718.jpg\" unselectable=\"on\"&gt;&lt;/a&gt;&lt;/td&gt;&lt;td&gt;&lt;a href=\"/view_object.php?object_id=31927\" rel=\"no_follow\"&gt;&lt;img src=\"/images/thumb/31927.jpg\" unselectable=\"on\"&gt;&lt;/a&gt;&lt;/td&gt;&lt;td&gt;&lt;a href=\"/view_object.php?object_id=142859\" rel=\"no_follow\"&gt;&lt;img src=\"/images/thumb/142859.jpg\" unselectable=\"on\"&gt;&lt;/a&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/tbody&gt;&lt;/table&gt;&lt;p&gt;&lt;/p&gt;&lt;a name=\"chapter_3\"&gt;&lt;/a&gt;&lt;h2&gt;Routes Overview&lt;/h2&gt;Here is a working list of routes on Half Dome.&lt;ul&gt;  &lt;table border=\"1\" cellspacing=\"0\"&gt;&lt;tbody&gt;&lt;tr&gt;&lt;td&gt;&lt;table border=\"0\" cellspacing=\"2\" cellpadding=\"1\"&gt;    &lt;tbody&gt;&lt;tr bgcolor=\"#c0c0c0\"&gt;&lt;td colspan=\"6\"&gt;&lt;font style=\"font-weight: bold;\"&gt;ROUTES OVERVIEW&lt;/font&gt;&lt;/td&gt;&lt;/tr&gt;  &lt;tr&gt;&lt;td align=\"center\"&gt;&lt;font style=\"font-weight: bold;\"&gt;#&lt;/font&gt;&lt;/td&gt;  &lt;td align=\"center\"&gt;&lt;font style=\"font-weight: bold;\"&gt;Route Name&lt;/font&gt;&lt;/td&gt;  &lt;td align=\"center\"&gt;&lt;font style=\"font-weight: bold;\"&gt;Difficulty&lt;/font&gt;&lt;/td&gt;  &lt;td align=\"center\"&gt;&lt;font style=\"font-weight: bold;\"&gt;FA&lt;/font&gt;&lt;/td&gt;  &lt;td align=\"center\"&gt;&lt;font style=\"font-weight: bold;\"&gt;FA Party&lt;/font&gt;&lt;/td&gt;  &lt;td align=\"center\"&gt;&lt;font style=\"font-weight: bold;\"&gt;Notes&lt;/font&gt;&lt;/td&gt;&lt;/tr&gt;    &lt;tr&gt;&lt;td align=\"center\" bgcolor=\"#e0e0e0\"&gt;1&lt;/td&gt;  &lt;td bgcolor=\"#e0e0e0\"&gt;Arcturas&lt;/td&gt;  &lt;td align=\"center\" bgcolor=\"#e0e0e0\"&gt;VI 5.7 A4&lt;/td&gt;  &